import React, { useEffect, useState } from 'react';
import { Calendar as BigCalendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import "./styles/calendar.css";

const localizer = momentLocalizer(moment);

const Calendar = ({ works }) => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const mappedEvents = works.flatMap(workType => 
      workType.map(work => ({
        title: work.name,
        start: new Date(work.startDate),
        end: new Date(work.endDate),
        description: work.describe,
        color: getColor(work.type) // Hàm getColor để xác định màu sắc
      }))
    );
    setEvents(mappedEvents);
  }, [works]);

  function getColor(type) {
    const colors = {
      "doNow": "red",
      "schedule": "blue",
      "delegate": "green",
      "drop": "orange",
      // Thêm các màu khác tương ứng với các loại khác
    };
    return colors[type] || "gray"; // Màu mặc định là xám nếu không tìm thấy
  }

  return (
    <div className="main-form" data-aos="zoom-in">
      <header className="search-bar">
        <h1>Calendar</h1>
      </header>
      <BigCalendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        style={{ height: '600px', width: '100%' }}
        eventPropGetter={event => ({
          style: { backgroundColor: event.color }
        })}
      />
    </div>
  );
};

export default Calendar;
