// File: dashboard.js

import React, { useState, useEffect } from "react";
import Aos from "aos";
import "./styles/dashboard.css";
import { AiFillDelete, AiFillEdit, AiOutlinePlus } from "react-icons/ai";

const Dashboard = ({ works, setWorks, completedWorks, setCompletedWorks }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isTaskModalOpen, setIsTaskModalOpen] = useState(false);

  const [formData, setFormData] = useState({
    id: null,
    name: "",
    type: "",
    startDate: "",
    endDate: "",
    describe: "",
    tag: "",
  });

  const [taskFormData, setTaskFormData] = useState({
    workId: null,
    taskName: "",
    taskDescription: "",
    startTime: "",
    endTime: "",
    repeatOption: "Không lặp lại",
    customRepeat: {
      repeatEvery: 1,
      repeatUnit: "ngày",
      repeatDays: [],
    },
  });

  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    Aos.init({ duration: 1000 });
  }, []);

  useEffect(() => {
    if (isModalOpen || isTaskModalOpen) {
      document.body.classList.add("disable-hover");
    } else {
      document.body.classList.remove("disable-hover");
    }
  }, [isModalOpen, isTaskModalOpen]);

  const handleAddNewWork = () => {
    setIsModalOpen(true);
    setEditMode(false);
    setFormData({
      id: null,
      name: "",
      type: "",
      startDate: "",
      endDate: "",
      describe: "",
      tag: "",
    });
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleTaskFormChange = (e) => {
    const { name, value } = e.target;

    setTaskFormData((prev) => {
      const updated = { ...prev, [name]: value };

      if (name === "customRepeat.repeatUnit" && value === "ngày") {
        updated.customRepeat.repeatDays = [];
      }

      return updated;
    });
  };

  const handleAddTask = (workId) => {
    const work = Object.values(works).flat().find((work) => work.id === workId);
    setIsTaskModalOpen(true);
    setTaskFormData({ ...taskFormData, workId, workName: work ? work.name : "" });
  };

  const handleDone = () => {
    const { name, type, startDate, endDate } = formData;

    if (!name || !type || !startDate || !endDate || !formData.tag) {
      alert("Please fill in all fields.");
      return;
    }

    setWorks((prevWorks) => {
      const updatedWorks = { ...prevWorks };

      if (editMode) {
        Object.keys(updatedWorks).forEach((type) => {
          updatedWorks[type] = updatedWorks[type].filter(
            (work) => work.id !== formData.id
          );
        });
      }

      updatedWorks[type] = [
        ...updatedWorks[type],
        {
          ...formData,
          id: formData.id || Date.now(),
          subWorks: formData.subWorks || [],
        },
      ];

      return updatedWorks;
    });

    setIsModalOpen(false);
  };

  const handleWorkCheckbox = (type, workId, subWorkIndex) => {
    setWorks((prevWorks) => {
      const updatedWorks = { ...prevWorks };

      if (subWorkIndex === -1) {
        const completedWork = updatedWorks[type].find((work) => work.id === workId);

        if (completedWork) {
          // Thêm work vào danh sách completedWorks
          setCompletedWorks((prevCompleted) => {
            const updatedCompleted = [...prevCompleted, {
              ...completedWork,
              isCompleted: true, // Đánh dấu là hoàn thành
              subWorks: completedWork.subWorks.map((task) => ({
                ...task,
                isCompleted: true, // Đánh dấu các task là hoàn thành
              })),
            }];
            return updatedCompleted;
          });
        }

        // Xóa work ở danh sách work chưa hoàn thành
        updatedWorks[type] = updatedWorks[type].filter((work) => work.id !== workId);
      }

      return updatedWorks;
    });
  };

  const handleDragStart = (e, work, type) => {
    e.dataTransfer.setData("work", JSON.stringify({ ...work, type }));
  };

  const handleDrop = (e, newType) => {
    const data = JSON.parse(e.dataTransfer.getData("work"));

    if (data.type === newType) return;

    setWorks((prevWorks) => {
      const updatedSourceWorks = prevWorks[data.type].filter(
        (work) => work.id !== data.id
      );
      const updatedTargetWorks = [
        ...prevWorks[newType],
        { ...data, type: newType },
      ];
      return {
        ...prevWorks,
        [data.type]: updatedSourceWorks,
        [newType]: updatedTargetWorks,
      };
    });
  };

  const allowDrop = (e) => e.preventDefault();

  const formatDate = (dateString) => {
    if (!dateString) return "";
    const [year, month, day] = dateString.split("-");
    return `${day}/${month}/${year}`;
  };

  return (
    <div className="home-body-conatiner" data-aos="zoom-in">
      <header id="dash-header" className="search-bar">
        <h1>Dashboard</h1>
      </header>
      <button className="add-work-btn" onClick={handleAddNewWork}>
        Add new work
      </button>

      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h2>{editMode ? "Edit Work" : "Add New Work"}</h2>
            <input
              type="text"
              placeholder="Enter work name"
              name="name"
              value={formData.name}
              onChange={handleFormChange}
            />
            <div className="form-row">
              <select
                name="type"
                value={formData.type}
                onChange={handleFormChange}
              >
                <option value="">Select type</option>
                <option value="doNow">Do now</option>
                <option value="schedule">Schedule</option>
                <option value="delegate">Delegate</option>
                <option value="drop">Drop</option>
              </select>
              <select
                name="tag"
                value={formData.tag}
                onChange={(e) => {
                  handleFormChange(e);
                  if (e.target.value === "Other") {
                    setFormData((prev) => ({ ...prev, customTag: "" }));
                  } else {
                    setFormData((prev) => ({ ...prev, customTag: null }));
                  }
                }}
              >
                <option value="">Select tag</option>
                <option value="Học tập">Học tập</option>
                <option value="Làm việc">Làm việc</option>
                <option value="Nghỉ ngơi">Nghỉ ngơi</option>
                <option value="Giải trí">Giải trí</option>
                <option value="Other">Other</option>
              </select>
              {formData.tag === "Other" && (
                <input
                  type="text"
                  placeholder="Enter here"
                  name="customTag"
                  value={formData.customTag || ""}
                  onChange={handleFormChange}
                />
              )}
            </div>
            <div className="form-row">
              <input
                type="date"
                name="startDate"
                value={formData.startDate}
                onChange={handleFormChange}
              />
              <input
                type="date"
                name="endDate"
                value={formData.endDate}
                onChange={handleFormChange}
              />
            </div>
            <textarea
              placeholder="Describe work (use # for hashtags)"
              name="describe"
              value={formData.describe}
              onChange={handleFormChange}
              rows="4"
            ></textarea>
            <div className="modal-buttons">
              <button onClick={handleDone}>Done</button>
              <button onClick={handleCancel}>Cancel</button>
            </div>
          </div>
        </div>
      )}

      <main className="body-content">
        {["doNow", "schedule", "delegate", "drop"].map((type) => (
          <div
            className="work-box"
            key={type}
            onDrop={(e) => handleDrop(e, type)}
            onDragOver={allowDrop}
          >
            <h2>{type.charAt(0).toUpperCase() + type.slice(1)}</h2>
            <div className="work-list">
              {works[type].map((work) => (
                <div
                  key={work.id}
                  className="work"
                  draggable
                  onDragStart={(e) => handleDragStart(e, work, type)}
                >
                  <div className="work-info">
                    <input
                      type="checkbox"
                      onChange={() => handleWorkCheckbox(type, work.id, -1)}
                    />
                    <span>{work.name}</span>
                    <p>
                      {formatDate(work.startDate)} - {formatDate(work.endDate)}
                    </p>
                    <p>
                      {work.describe
                        ? work.describe.split(" ").map((word, i) =>
                            word.startsWith("#") ? (
                              <span key={i} style={{ color: "blue" }}>
                                {word} {" "}
                              </span>
                            ) : (
                              `${word} `
                            )
                          )
                        : "No description"}
                    </p>
                    <p>Tasks: {work.subWorks ? work.subWorks.length : 0}</p>
                  </div>
                  <div className="work-buttons">
                    <AiOutlinePlus
                      className="add-work-icon"
                      onClick={() => handleAddTask(work.id)}
                    />
                    <AiFillEdit
                      className="edit-icon"
                      onClick={() => handleEditTask(work)}
                    />
                    <AiFillDelete
                      className="delete-icon"
                      onClick={() => handleDeleteTask(type, work.id)}
                    />
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </main>
    </div>
  );
};

export default Dashboard;
