import React, { useEffect, useState } from "react";
import "./styles/focus.css";
import moment from "moment";

const Focus = ({ works = {}, toast }) => {
  const [mode, setMode] = useState("auto");
  const [tasksForToday, setTasksForToday] = useState([]);

  useEffect(() => {
    if (mode === "auto") {
      calculateTasksForToday();
    }
  }, [mode, works]);

  const calculateTasksForToday = () => {
    const today = moment().startOf("day");
    const tasks = [];

    Object.values(works).flat().forEach((work) => {
      const workStartDate = moment(work.startDate, "DD/MM/YYYY");
      const workEndDate = moment(work.endDate, "DD/MM/YYYY");

      work.subWorks.forEach((task) => {
        const {
          startTime,
          endTime,
          repeatOption,
          customRepeat = {},
        } = task;
        const taskStartTime = moment(startTime, "hh:mm A");
        const taskEndTime = moment(endTime, "hh:mm A");

        let validDates = [];

        if (repeatOption === "Không lặp lại") {
          validDates = [workStartDate];
        } else if (repeatOption === "Hàng ngày") {
          let currentDate = workStartDate.clone();
          while (currentDate.isSameOrBefore(workEndDate)) {
            validDates.push(currentDate.clone());
            currentDate.add(1, "day");
          }
        } else if (repeatOption === "Tùy chỉnh") {
          const {
            repeatEvery = 1,
            repeatUnit = "ngày",
            startDate,
            repeatDays = [],
          } = customRepeat;

          const taskStartDate = moment(startDate, "DD/MM/YYYY");
          let currentDate = taskStartDate.clone();

          if (repeatUnit === "ngày") {
            while (currentDate.isSameOrBefore(workEndDate)) {
              validDates.push(currentDate.clone());
              currentDate.add(repeatEvery, "days");
            }
          } else if (repeatUnit === "tuần") {
            while (currentDate.isSameOrBefore(workEndDate)) {
              if (repeatDays.includes(currentDate.format("ddd"))) {
                validDates.push(currentDate.clone());
              }
              currentDate.add(1, "day");
            }
          }
        }

        validDates.forEach((date) => {
          if (date.isSame(today, "day")) {
            tasks.push({
              ...task,
              workName: work.name,
              startTime: taskStartTime,
              endTime: taskEndTime,
            });
          }
        });
      });
    });

    const sortedTasks = tasks.sort((a, b) => a.startTime - b.startTime);
    setTasksForToday(sortedTasks);
  };

  const formatTime = (momentObj) => momentObj.format("hh:mm A");

  return (
    <div className="main-form" data-aos="zoom-in">
      <header className="search-bar">
        <h1>Focus</h1>
      </header>

      <div className="focus-modes">
        <button
          className={`focus-mode ${mode === "auto" ? "active" : ""}`}
          onClick={() => setMode("auto")}
        >
          Tự động
        </button>
        <button
          className={`focus-mode ${mode === "manual" ? "active" : ""}`}
          onClick={() => setMode("manual")}
        >
          Tự chọn
        </button>
        <button
          className={`focus-mode ${mode === "free" ? "active" : ""}`}
          onClick={() => setMode("free")}
        >
          Tự do
        </button>
      </div>

      <div className="focus-content">
        {mode === "auto" && tasksForToday.length > 0 ? (
          <div className="task-list">
            <h2>Task cho hôm nay</h2>
            {tasksForToday.map((task, index) => (
              <div
                key={index}
                className={`task-item ${index === 0 ? "representative" : ""}`}
              >
                <h4>{task.name}</h4>
                <p>
                  {formatTime(task.startTime)} - {formatTime(task.endTime)}
                </p>
                <p>
                  <strong>Work:</strong> {task.workName}
                </p>
              </div>
            ))}
          </div>
        ) : (
          <p>No tasks available for today.</p>
        )}
      </div>
    </div>
  );
};

export default Focus;
