import React, { useState } from "react";
import "./styles/timetable.css";

const TimeTable = () => {
  const [tasks, setTasks] = useState([]);
  const [showPopup, setShowPopup] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    startTime: "",
    endTime: "",
    weekday: "2",
  });
  const [errorMessage, setErrorMessage] = useState("");

  const weekdays = ["Thứ 2", "Thứ 3", "Thứ 4", "Thứ 5", "Thứ 6", "Thứ 7", "Chủ nhật"];

  const openPopup = () => setShowPopup(true);
  const closePopup = () => {
    setShowPopup(false);
    setErrorMessage("");
    setFormData({ name: "", startTime: "", endTime: "", weekday: "2" });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const addTask = () => {
    const { name, startTime, endTime, weekday } = formData;

    if (!name || !startTime || !endTime) {
      setErrorMessage("Vui lòng nhập đầy đủ thông tin nhiệm vụ.");
      return;
    }

    const [startH, startM] = startTime.split(":").map(Number);
    const [endH, endM] = endTime.split(":").map(Number);
    const startMinutes = startH * 60 + startM;
    const endMinutes = endH * 60 + endM;

    if (startMinutes >= endMinutes) {
      setErrorMessage("Thời gian kết thúc phải sau thời gian bắt đầu.");
      return;
    }

    setTasks((prev) => [
      ...prev,
      {
        id: Date.now(),
        name,
        startTime,
        endTime,
        weekday,
        top: (startMinutes / (24 * 60)) * 100,
        height: ((endMinutes - startMinutes) / (24 * 60)) * 100,
      },
    ]);
    closePopup();
  };

  return (
    <div className="timetable-container">
      <h1>Thời Gian Biểu</h1>

      <button className="add-task-button" onClick={openPopup}>
        +
      </button>

      {showPopup && (
        <div className="task-popup">
          <div className="popup-content">
            <label>
              Tên nhiệm vụ:
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                placeholder="Tên nhiệm vụ"
              />
            </label>

            <label>
              Bắt đầu:
              <input
                type="time"
                name="startTime"
                value={formData.startTime}
                onChange={handleChange}
              />
            </label>

            <label>
              Kết thúc:
              <input
                type="time"
                name="endTime"
                value={formData.endTime}
                onChange={handleChange}
              />
            </label>

            <label>
              Thứ:
              <select name="weekday" value={formData.weekday} onChange={handleChange}>
                {weekdays.map((day, index) => (
                  <option key={index} value={index + 2}>
                    {day}
                  </option>
                ))}
              </select>
            </label>

            <div className="button-container">
              <button className="action-button" onClick={addTask}>
                Lưu
              </button>
              <button className="action-button" onClick={closePopup}>
                Hủy
              </button>
            </div>

            {errorMessage && <p className="error">{errorMessage}</p>}
          </div>
        </div>
      )}

      <div className="timetable">
        {weekdays.map((day, index) => (
          <div key={index} className="weekday-column">
            <div className="column-header">{day}</div>
            <div className="tasks">
              {tasks
                .filter((task) => parseInt(task.weekday) === index + 2)
                .map((task) => (
                  <div
                    key={task.id}
                    className="task"
                    style={{ top: `${task.top}%`, height: `${task.height}%` }}
                  >
                    {task.name}
                  </div>
                ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TimeTable;
