import React, { useState, useEffect } from "react";
import { AiFillDelete } from "react-icons/ai";
import { BiSearchAlt2 } from "react-icons/bi";
import Aos from "aos";
import "aos/dist/aos.css";
import "./styles/timetable.css";

const TimeTable = () => {
  const [newItem, setNewItem] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [todo, setTodo] = useState([
    { todoId: 1, title: "Morning Meeting", status: false },
    { todoId: 2, title: "Lunch Break", status: true },
  ]);

  useEffect(() => {
    Aos.init({ duration: 1000 });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newItem === "") {
      alert("Please type something");
      return;
    }
    const newTodoItem = {
      todoId: Date.now(),
      title: newItem,
      status: false,
    };
    setTodo((current) => [...current, newTodoItem]);
    setNewItem("");
  };

  const tickTodo = (todoId, status) => {
    setTodo((current) =>
      current.map((todo) =>
        todo.todoId === todoId ? { ...todo, status } : todo
      )
    );
  };

  const deleteTodo = (todoId) => {
    setTodo((current) => current.filter((todo) => todo.todoId !== todoId));
  };

  const filteredItems = todo.filter((eachItem) =>
    eachItem.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="main-form" data-aos="zoom-in">
      <div className="search-bar">
        <h1>TimeTable</h1>
        <input
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          type="search"
          placeholder="Search"
        />
        <button id="search-bt">
          <BiSearchAlt2 size={22} />
        </button>
      </div>
      <form onSubmit={handleSubmit} className="form-row">
        <input
          value={newItem}
          onChange={(e) => setNewItem(e.target.value)}
          type="text"
          className="todo-ip"
          placeholder="New Task"
        />
        <button id="add-todo">ADD </button>
      </form>
      <div className="item-list" data-aos="zoom-out">
        <ul>
          {todo.length === 0 && <h3 id="no-todo">No Tasks</h3>}
          {filteredItems.map((todo) => (
            <li key={todo.todoId}>
              <label htmlFor="" className="item-name">
                <input
                  type="checkbox"
                  checked={todo.status}
                  onChange={(e) => tickTodo(todo.todoId, e.target.checked)}
                />
                {todo.title}
              </label>
              <button id="del-bt" onClick={() => deleteTodo(todo.todoId)}>
                <AiFillDelete size={20} color="#FF6969" />
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default TimeTable;
