import Toggler from "./components/Toggler";
import Calendar from "./components/Calendar";
import Home from "./components/Home";
import "react-toastify/dist/ReactToastify.css";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import Project from "./components/Project";
import Focus from "./components/Focus";
import Dashboard from "./components/Dashboard";
import Report from "./components/Report";
import ForgotPass from "./components/ForgotPass";
import TimeTable from "./components/TimeTable";
import ResetPass from "./components/ResetPass";
// import { useState } from "react";
import { useState, useEffect } from "react";

function App() {
  const [works, setWorks] = useState(() => {
    const savedWorks = localStorage.getItem("works");
    return savedWorks ? JSON.parse(savedWorks) : {
      doNow: [],
      schedule: [],
      delegate: [],
      drop: [],
    };
  });

  const [completedWorks, setCompletedWorks] = useState(() => {
    const savedCompletedWorks = localStorage.getItem("worksdone");
    return savedCompletedWorks ? JSON.parse(savedCompletedWorks) : [];
  });

  useEffect(() => {
    // Lưu `works` và `completedWorks` vào `localStorage` mỗi khi chúng thay đổi
    localStorage.setItem("works", JSON.stringify(works));
    localStorage.setItem("worksdone", JSON.stringify(completedWorks));
  }, [works, completedWorks]);

  return (
    <BrowserRouter>
      <ToastContainer
        position="top-center"
        autoClose={800}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />
      <Routes>
        <Route path="/" element={<Toggler toast={toast} />} />
        <Route path="/ForgotPass" element={<ForgotPass toast={toast} />} />
        <Route path="/ResetPass/:id/:token" element={<ResetPass toast={toast} />} />
        <Route path="/Home" element={<Home tasks={tasks} />}>
          <Route path="/Home" element={
            <Dashboard
              works={works}
              setWorks={setWorks}
              completedWorks={completedWorks}
              setCompletedWorks={setCompletedWorks}
            />
          } />
          <Route path="/Home/projects" element={
            <Project
              works={works}
              setWorks={setWorks}
              completedWorks={completedWorks}
              setCompletedWorks={setCompletedWorks}
            />
          } />
          <Route path="/Home/focus" element={<Focus toast={toast} tasks={tasks} />} />
          <Route path="/Home/timetable" element={<TimeTable toast={toast} tasks={tasks} />} />
          <Route path="/Home/calendar" element={<Calendar toast={toast} tasks={tasks} />} />
          <Route path="/Home/report" element={<Report toast={toast} tasks={tasks} />} />
        </Route>
      </Routes>
    </BrowserRouter>
  );
}

export default App;
