import React, { useEffect, useState } from "react";
import { BiLogOut, BiBarChart as BiDashboard } from "react-icons/bi";
import { MdOutlineWatchLater } from "react-icons/md";
import { AiOutlineSchedule, AiOutlineBarChart } from "react-icons/ai";
import { IoMdNotifications, IoMdSunny, IoIosMoon } from "react-icons/io";
import { useNavigate, useLocation } from "react-router-dom";
import icon from "../utils/icon.PNG";
import Aos from "aos";
import "aos/dist/aos.css";
import { toast } from "react-toastify";
import Notification from "./Notification/Notification";
import axios from 'axios';

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [user, setUser] = useState({});
  const [upcomingTasks, setUpcomingTasks] = useState([]);
  const [dialog, setDialog] = useState(false);

  useEffect(() => {
    Aos.init({ duration: 800 });
    axios.get(`${process.env.REACT_APP_API_URL}/getUser`)
      .then(res => setUser(res.data))
      .catch(err => console.error(err));

    axios.get(`${process.env.REACT_APP_API_URL}/task/getTask`)
      .then(res => setUpcomingTasks(res.data.filter(task => !task.done)))
      .catch(err => console.error(err));
  }, []);

  const toggleDialog = () => setDialog(!dialog);
  const toggleDarkMode = e => {
    document.body.setAttribute('data-theme', e.target.checked ? 'dark' : 'light');
  };

  function logOut() {
    toast.success("Logged out successfully");
    navigate("/");
  }

  return (
    <nav className="nav-left" data-aos="fade-right">
      <button className="awaretime-theme">
        <img src={icon} alt="" />
      </button>
      <button onClick={toggleDialog}>
        <IoMdNotifications size={25} color="#3081D0" />
        {upcomingTasks.length > 0 && <span className="notification-count">{upcomingTasks.length}</span>}
      </button>
      <button>
        <input type="checkbox" onChange={toggleDarkMode} />
        <IoMdSunny size={16} />
        <IoIosMoon size={16} />
      </button>
      <div className="user-icon">
        <img src={user.picUrl || '/default-user.png'} alt="User" style={{ width: 30, height: 30, borderRadius: '50%' }} />
      </div>
      {location.pathname === "/Home" && <BiDashboard size={22} color="white" />}
      <button onClick={logOut}>
        <BiLogOut size={22} color="white" />
      </button>
      {dialog && <Notification closeNotifi={toggleDialog} upcomingTasks={upcomingTasks} />}
    </nav>
  );
};

export default Navbar;
