import React, { useEffect } from "react";
import { LuListTodo } from "react-icons/lu";
import { IoCalendarNumber } from "react-icons/io5";
import { FaRegNoteSticky } from "react-icons/fa6";
import axios from "axios";
import { BiLogOut, BiSolidDashboard } from "react-icons/bi";
import { useNavigate } from "react-router-dom";
import icon from "../utils/icon.PNG";
import Aos from "aos";
import "aos/dist/aos.css";
import { toast } from "react-toastify";

const Navbar = () => {
  const navigate = useNavigate();
  axios.defaults.withCredentials = true;

  useEffect(() => {
    Aos.init({ duration: 800 });
  }, []);

  function openTodo() {
    navigate("/Home/todos");
  }
  function openTask() {
    navigate("/Home/task");
  }
  function openNotes() {
    navigate("/Home/notes");
  }
  function gotoDashboard() {
    navigate("/Home");
  }

  function logOut() {
    // Sử dụng mock response để kiểm tra
    const mockResponse = {
      data: {
        success: true, // Phản hồi giả định thành công
      },
    };

    // Đảm bảo mockResponse.data tồn tại trước khi kiểm tra success
    if (mockResponse?.data?.success) {
      toast.success("Logged out successfully (mock)");
      navigate("/");
    } else {
      toast.error("Logout failed (mock)");
    }

    // Khi sử dụng API thật
    /*
    axios
      .get(`${process.env.REACT_APP_API_URL}/logout`)
      .then((res) => {
        if (res?.data?.success) {
          toast.success("Logged out successfully");
          navigate("/");
        } else {
          toast.error("Logout failed");
        }
      })
      .catch((err) => {
        console.error(err);
        toast.error("An error occurred during logout");
      });
    */
  }

  return (
    <nav className="nav-left" data-aos="fade-right">
      <button className="todo-theme">
        <img src={icon} alt="" />
      </button>
      <button onClick={gotoDashboard} className="nav-icon skull">
        <BiSolidDashboard size={22} color="white" />
      </button>
      <button onClick={openTodo} className="nav-icon skull">
        <LuListTodo size={20} color="white" />
      </button>
      <button onClick={openTask} className="nav-icon skull">
        <IoCalendarNumber size={20} color="white" />
      </button>
      <button onClick={openNotes} className="nav-icon skull">
        <FaRegNoteSticky size={20} color="white" />
      </button>
      <button className="nav-icon skull" onClick={logOut}>
        <BiLogOut size={22} color="white" />
      </button>
    </nav>
  );
};

export default Navbar;
