import React, { useEffect, useMemo, useState } from "react";
import "aos/dist/aos.css";
import "./styles/timetable.css";

const getWeekTasks = (works, currentDate) => {
  // Loại bỏ khai báo trùng lặp của currentDate
const truncateTime = (date) => new Date(date.getFullYear(), date.getMonth(), date.getDate());

// Chỉ sử dụng một khai báo duy nhất cho currentDate
const todayDate = truncateTime(new Date()); // Đổi tên biến cho rõ ràng
const currentDay = todayDate.getDay(); // 0 - Chủ Nhật, 1 - Thứ 2, ..., 6 - Thứ 7

// Tính toán startOfWeek và endOfWeek
const startOfWeek = new Date(todayDate);
startOfWeek.setDate(todayDate.getDate() - (currentDay === 0 ? 6 : currentDay - 1)); // Thứ 2 của tuần

const endOfWeek = new Date(startOfWeek);
endOfWeek.setDate(startOfWeek.getDate() + 6); // Chủ Nhật của tuần



  const tasksByDay = Array(7).fill(null).map((_, i) => ({
    date: new Date(startOfWeek.getTime() + i * 24 * 60 * 60 * 1000),
    tasks: [],
  }));

  Object.values(works).flat().forEach((work) => {
    const workStartDate = new Date(work.startDate);
    const workEndDate = new Date(work.endDate);

    work.subWorks.forEach((task) => {
      const { repeatOption, customRepeat = {}, startTime, endTime } = task;

      const calculateDates = () => {
        let dates = [];
        const truncateTime = (date) =>
          new Date(date.getFullYear(), date.getMonth(), date.getDate());
      
        console.log("Processing task:", task.name); // Xác định task hiện tại
      
        // Kiểm tra repeatDays
        const dayMapping = { CN: 0, T2: 1, T3: 2, T4: 3, T5: 4, T6: 5, T7: 6 };
        const convertedRepeatDays = (customRepeat.repeatDays || []).map(
          (day) => dayMapping[day] || day
        );
        console.log("Original repeatDays:", customRepeat.repeatDays);
        console.log("Converted repeatDays:", convertedRepeatDays);
      
        // Kiểm tra taskStartDate và endDate
        const taskStartDate = truncateTime(
          customRepeat.startDate ? new Date(customRepeat.startDate) : new Date(workStartDate)
        );
        const endDate = truncateTime(new Date(workEndDate));
      
        console.log("Task Start Date:", taskStartDate);
        console.log("Task End Date:", endDate);
      
        if (!taskStartDate || !endDate) {
          console.error("Invalid taskStartDate or endDate.");
          return dates; // Trả về danh sách trống
        }
      
        if (taskStartDate > endDate) {
          console.error("Start date is after end date!");
          return dates; // Trả về danh sách trống
        }
      
        if (repeatOption === "Không lặp lại") {
          const taskDate = truncateTime(new Date(workStartDate));
          if (taskDate >= startOfWeek && taskDate <= endOfWeek) dates.push(taskDate);
        } else if (repeatOption === "Hàng ngày") {
          let currentDate = truncateTime(new Date(workStartDate));
          while (currentDate <= endDate) {
            if (currentDate >= startOfWeek && currentDate <= endOfWeek) {
              dates.push(new Date(currentDate));
            }
            currentDate.setDate(currentDate.getDate() + 1);
          }
        } else if (repeatOption === "Tùy chỉnh") {
          if (customRepeat.repeatUnit === "ngày") {
            let currentDate = new Date(taskStartDate);
            while (currentDate <= endDate) {
              if (currentDate >= startOfWeek && currentDate <= endOfWeek) {
                dates.push(new Date(currentDate));
              }
              currentDate.setDate(currentDate.getDate() + customRepeat.repeatEvery);
            }
          } else if (customRepeat.repeatUnit === "tuần") {
            let currentDate = new Date(taskStartDate);
            while (currentDate <= endDate) {
              const startOfCurrentWeek = new Date(currentDate);
              startOfCurrentWeek.setDate(currentDate.getDate() - currentDate.getDay());
              const endOfCurrentWeek = new Date(startOfCurrentWeek);
              endOfCurrentWeek.setDate(startOfCurrentWeek.getDate() + 6);
      
              convertedRepeatDays.forEach((dayOfWeek) => {
                const targetDate = new Date(startOfCurrentWeek);
                targetDate.setDate(startOfCurrentWeek.getDate() + dayOfWeek);
                if (
                  targetDate >= startOfWeek &&
                  targetDate <= endOfWeek &&
                  targetDate >= taskStartDate &&
                  targetDate <= endDate
                ) {
                  dates.push(new Date(targetDate));
                }
              });
      
              currentDate.setDate(currentDate.getDate() + customRepeat.repeatEvery * 7);
            }
          }
        }
      
        return dates;
      };
      


      calculateDates().forEach((date) => {
        const taskDay = tasksByDay.find(
          (day) => day.date.toDateString() === date.toDateString()
        );
        if (taskDay) {
          taskDay.tasks.push({
            name: task.name,
            startTime,
            endTime,
            workName: work.name,
          });
        }
      });
    });
  });

  return tasksByDay;
};

const TimeTable = ({ works }) => {
  const days = ["Thứ 2", "Thứ 3", "Thứ 4", "Thứ 5", "Thứ 6", "Thứ 7", "Chủ Nhật"];
const hours = Array.from({ length: 24 }, (_, i) => `${i}:00 - ${i + 1}:00`);

  const [isTaskListVisible, setIsTaskListVisible] = useState(false);
  const [weekTasks, setWeekTasks] = useState([]);

  useEffect(() => {
    const currentDate = new Date();
    setWeekTasks(getWeekTasks(works, currentDate));
  }, [works]);

  return (
    <div className="main-form" data-aos="zoom-in">
      <header className="search-bar">
        <h1>TimeTable</h1>
        <button
          className="view-tasklist-btn"
          onClick={() => setIsTaskListVisible(true)}
        >
          View Task List
        </button>
      </header>

      {isTaskListVisible && (
        <div className="tasklist-overlay">
          <div className="tasklist-container">
            <button
              className="close-btn"
              onClick={() => setIsTaskListVisible(false)}
            >
              Close
            </button>
            <div className="tasklist">
              {weekTasks.map((day, index) => (
                <div key={index} className="day-tasks">
                  <h3>
                    {["Thứ 2", "Thứ 3", "Thứ 4", "Thứ 5", "Thứ 6", "Thứ 7", "Chủ Nhật"][index]}{" "}
                    ({day.date.toLocaleDateString("vi-VN")})
                  </h3>
                  {day.tasks.length > 0 ? (
                    day.tasks.map((task, i) => (
                      <div key={i} className="task-item">
                        <p>
                          <strong>{task.name}</strong> - {task.workName} ({task.startTime} - {task.endTime})
                        </p>
                      </div>
                    ))
                  ) : (
                    <p>No tasks</p>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

<div className="timetable-container">
        <div className="header-row">
          <div className="header-cell time-column">Thời gian</div>
          {days.map((day, index) => (
            <div key={index} className="header-cell">
              {day}
            </div>
          ))}
        </div>

        <div className="body-container">
          {[...Array(8)].map((_, dayIndex) => (
            <div key={dayIndex} className={`column ${dayIndex === 0 ? "time-column" : ""}`}>
              {hours.map((hour, hourIndex) => (
                <div key={hourIndex} className="time-slot">
                  {dayIndex === 0 ? hour : null}
                  <div className="atomic-slots">
                    {[...Array(4)].map((_, atomicIndex) => (
                      <div key={atomicIndex} className="atomic-slot"></div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TimeTable;
