import Toggler from "./components/Toggler";
import Calendar from "./components/Calendar";
import Home from "./components/Home";
import "react-toastify/dist/ReactToastify.css";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import Project from "./components/Project";
import Focus from "./components/Focus";
import Dashboard from "./components/Dashboard";
import Report from "./components/Report";
import ForgotPass from "./components/ForgotPass";
import TimeTable from "./components/TimeTable";
import ResetPass from "./components/ResetPass";
import { useState, useEffect } from "react";

function App() {
  // Quản lý works và completedWorks từ cấp App
  const [works, setWorks] = useState(() => {
    // Khởi tạo works từ localStorage hoặc giá trị mặc định
    const savedWorks = localStorage.getItem("works");
    return savedWorks
      ? JSON.parse(savedWorks)
      : {
          doNow: [],
          schedule: [],
          delegate: [],
          drop: [],
        };
  });

  const [completedWorks, setCompletedWorks] = useState(() => {
    // Khởi tạo completedWorks từ localStorage hoặc giá trị mặc định
    const savedCompletedWorks = localStorage.getItem("worksdone");
    return savedCompletedWorks ? JSON.parse(savedCompletedWorks) : [];
  });

  const [recycledWorks, setRecycledWorks] = useState(() => {
    // Khởi tạo recycledWorks từ localStorage hoặc giá trị mặc định
    const savedRecycledWorks = localStorage.getItem("recycledWorks");
    return savedRecycledWorks ? JSON.parse(savedRecycledWorks) : [];
  });

  const [taskCompletionStatus, setTaskCompletionStatus] = useState(() => {
    // Khởi tạo trạng thái của các task từ localStorage hoặc giá trị mặc định
    const savedTaskCompletionStatus = localStorage.getItem("taskCompletionStatus");
    return savedTaskCompletionStatus ? JSON.parse(savedTaskCompletionStatus) : {};
  });

  // Cập nhật localStorage mỗi khi works, completedWorks, recycledWorks, hoặc taskCompletionStatus thay đổi
  useEffect(() => {
    localStorage.setItem("works", JSON.stringify(works));
    localStorage.setItem("worksdone", JSON.stringify(completedWorks));
    localStorage.setItem("recycledWorks", JSON.stringify(recycledWorks));
    localStorage.setItem("taskCompletionStatus", JSON.stringify(taskCompletionStatus));
  }, [works, completedWorks, recycledWorks, taskCompletionStatus]);

  const [tasks, setTasks] = useState({
    doNow: [],
    schedule: [],
    delegate: [],
    drop: [],
  });

  const deleteWorkFromDashboard = (workId) => {
    const deletedWork = Object.values(works).flat().find((work) => work.id === workId);

    if (deletedWork) {
      setRecycledWorks((prevRecycledWorks) => [
        ...prevRecycledWorks,
        {
          ...deletedWork,
          subWorks: deletedWork.subWorks.map((task, index) => ({
            ...task,
            isCompleted: !!taskCompletionStatus[`${workId}-${index}`],
          })),
        },
      ]);
    }

    setWorks((prevWorks) => {
      const updatedWorks = { ...prevWorks };
      Object.keys(updatedWorks).forEach((key) => {
        updatedWorks[key] = updatedWorks[key].filter((work) => work.id !== workId);
      });
      return updatedWorks;
    });

    setTaskCompletionStatus((prevStatus) => {
      const updatedStatus = { ...prevStatus };

      const relatedTasks = Object.keys(updatedStatus).filter((key) =>
        key.startsWith(`${workId}-`)
      );

      relatedTasks.forEach((key) => {
        delete updatedStatus[key];
      });

      return updatedStatus;
    });
  };

  return (
    <BrowserRouter>
      <ToastContainer
        position="top-center"
        autoClose={800}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />
      <Routes>
        <Route path="/" element={<Toggler toast={toast} />} />
        <Route path="/ForgotPass" element={<ForgotPass toast={toast} />} />
        <Route path="/ResetPass/:id/:token" element={<ResetPass toast={toast} />} />
        <Route path="/Home" element={<Home tasks={tasks} />}>
          <Route
            path="/Home"
            element={
              <Dashboard
                works={works}
                setWorks={setWorks}
                completedWorks={completedWorks}
                setCompletedWorks={setCompletedWorks}
                recycledWorks={recycledWorks}
                setRecycledWorks={setRecycledWorks}
                taskCompletionStatus={taskCompletionStatus}
                setTaskCompletionStatus={setTaskCompletionStatus}
                deleteWork={deleteWorkFromDashboard}
              />
            }
          />
          <Route
            path="/Home/projects"
            element={
              <Project
                works={works}
                setWorks={setWorks}
                completedWorks={completedWorks}
                setCompletedWorks={setCompletedWorks}
                recycledWorks={recycledWorks}
                setRecycledWorks={setRecycledWorks}
                taskCompletionStatus={taskCompletionStatus}
                setTaskCompletionStatus={setTaskCompletionStatus}
              />
            }
          />
          <Route
            path="/Home/focus"
            element={<Focus toast={toast} tasks={tasks} />}
          />
          <Route
            path="/Home/timetable"
            element={<TimeTable toast={toast} tasks={tasks} />}
          />
          <Route
            path="/Home/calendar"
            element={<Calendar toast={toast} tasks={tasks} />}
          />
          <Route
            path="/Home/report"
            element={<Report toast={toast} tasks={tasks} />}
          />
        </Route>
      </Routes>
    </BrowserRouter>
  );
}

export default App;
