import React, { useState, useEffect, useMemo } from "react";
import { AiFillDelete } from "react-icons/ai";
import { BiSearchAlt2 } from "react-icons/bi";
import axios from "axios";
import Aos from "aos";
import "aos/dist/aos.css";
import "./styles/project.css";

const Project = ({ toast, todo, setTodo, completedTasks, setCompletedTasks }) => {
  const [newItem, setNewItem] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [viewMode, setViewMode] = useState("ongoing"); // Toggle between "ongoing" and "completed"

  axios.defaults.withCredentials = true;

  const filteredItems = useMemo(() => {
    return (viewMode === "ongoing" ? todo : completedTasks).filter((item) =>
      item.title.toLowerCase().includes(searchQuery.toLowerCase())
    );
  }, [todo, completedTasks, searchQuery, viewMode]);

  useEffect(() => {
    Aos.init({ duration: 1000 });
    axios
      .get(`${process.env.REACT_APP_API_URL}/todo/getTodo`)
      .then((res) => {
        setTodo(res.data.filter(item => !item.status));
        setCompletedTasks(res.data.filter(item => item.status));
      })
      .catch((err) => console.log(err));
  }, [setTodo, setCompletedTasks]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (newItem === "") {
      toast.error("Please type something");
      return;
    }
    const newTodoItem = {
      todoId: crypto.randomUUID(),
      title: newItem,
      status: false,
    };
    setTodo((current) => [...current, newTodoItem]);
    await axios
      .post(`${process.env.REACT_APP_API_URL}/todo/postTodo`, newTodoItem)
      .then((res) => {
        console.log(res);
        toast.success("Added Successfully");
      })
      .catch((err) => console.log(err));
    setNewItem("");
  };

  function tickTodo(todoId, status) {
    if (status) {
      const item = todo.find(t => t.todoId === todoId);
      setCompletedTasks([...completedTasks, {...item, status}]);
      setTodo(todo => todo.filter(t => t.todoId !== todoId));
    } else {
      const item = completedTasks.find(t => t.todoId === todoId);
      setTodo([...todo, {...item, status}]);
      setCompletedTasks(completedTasks => completedTasks.filter(t => t.todoId !== todoId));
    }
  }

  function deleteTodo(todoId) {
    axios.delete(`${process.env.REACT_APP_API_URL}/todo/deleteTodo/${todoId}`);
    if (viewMode === "ongoing") {
      setTodo((current) => current.filter((item) => item.todoId !== todoId));
    } else {
      setCompletedTasks((current) => current.filter((item) => item.todoId !== todoId));
    }
  }

  return (
    <div className="main-form" data-aos="zoom-in">
      <div className="search-bar">
        <h1>Project</h1>
        <input
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          type="search"
          placeholder="Search"
        />
        <button onClick={() => setViewMode("ongoing")} disabled={viewMode === "ongoing"}>Ongoing</button>
        <button onClick={() => setViewMode("completed")} disabled={viewMode === "completed"}>Completed</button>
      </div>
      <form onSubmit={handleSubmit} className="form-row">
        <input
          value={newItem}
          onChange={(e) => setNewItem(e.target.value)}
          type="text"
          className="todo-ip"
          placeholder="New Remainder"
        />
        <button id="add-todo">ADD </button>
      </form>
      <div className="item-list" data-aos="zoom-out">
        <ul>
          {filteredItems.length === 0 && <h3 id="no-todo">No Tasks Found</h3>}
          {filteredItems.map((item) => {
            return (
              <li key={item.todoId}>
                <label htmlFor="" className="item-name">
                  <input
                    type="checkbox"
                    checked={item.status}
                    onChange={(e) => tickTodo(item.todoId, e.target.checked)}
                  />
                  {item.title}
                </label>
                <button id="del-bt" onClick={() => deleteTodo(item.todoId)}>
                  <AiFillDelete size={20} color="#FF6969" />
                </button>
              </li>
            );
          })}
        </ul>
      </div>
    </div>
  );
};

export default Project;
