const Project = ({ works, setWorks, completedWorks, setCompletedWorks }) => {
  const [viewMode, setViewMode] = useState("ongoing");
  const [searchQuery, setSearchQuery] = useState("");
  const [expandedWorkIds, setExpandedWorkIds] = useState([]);
  const [taskCompletionStatus, setTaskCompletionStatus] = useState({});
  const [recycledWorks, setRecycledWorks] = useState([]);

  // Toggle expand/collapse for a work item
  const toggleExpand = (workId) => {
    setExpandedWorkIds((prevExpandedWorkIds) =>
      prevExpandedWorkIds.includes(workId)
        ? prevExpandedWorkIds.filter((id) => id !== workId)
        : [...prevExpandedWorkIds, workId]
    );
  };

  // Handle restoring a work item from recycle bin
  const handleRestoreWork = (workId) => {
    const restoredWork = recycledWorks.find((work) => work.id === workId);

    if (restoredWork) {
      setWorks((prevWorks) => {
        const updatedWorks = { ...prevWorks };
        const type = restoredWork.type || "doNow";
        if (!updatedWorks[type].some((w) => w.id === workId)) {
          updatedWorks[type] = [...updatedWorks[type], restoredWork];
        }
        return updatedWorks;
      });

      setRecycledWorks((prevRecycledWorks) =>
        prevRecycledWorks.filter((work) => work.id !== workId)
      );
    }
  };

  // Handle deleting a task within a work in recycle bin
  const handleDeleteTaskInRecycle = (workId, taskIndex) => {
    setRecycledWorks((prevRecycledWorks) =>
      prevRecycledWorks.map((work) => {
        if (work.id === workId) {
          const updatedWork = {
            ...work,
            subWorks: work.subWorks.filter((_, index) => index !== taskIndex),
          };
          return updatedWork;
        }
        return work;
      })
    );
  };

  return (
    <div className="main-form" data-aos="zoom-in">
      <div className="search-bar">
        <h1>Projects</h1>
        <input
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          type="search"
          placeholder={`Search ${
            viewMode === "ongoing"
              ? "ongoing"
              : viewMode === "completed"
              ? "completed"
              : "recycled"
          } works`}
        />
        <button id="search-bt">
          <BiSearchAlt2 size={22} />
        </button>
      </div>
      <div className="view-toggle">
        <button
          className={viewMode === "ongoing" ? "active" : ""}
          onClick={() => setViewMode("ongoing")}
        >
          On Going Works
        </button>
        <button
          className={viewMode === "completed" ? "active" : ""}
          onClick={() => setViewMode("completed")}
        >
          Completed Works
        </button>
        <button
          className={viewMode === "recycle" ? "active" : ""}
          onClick={() => setViewMode("recycle")}
        >
          Recycle Bin
        </button>
      </div>
      <div className="item-list">
        {viewMode === "recycle" ? (
          <ul>
            {recycledWorks.length === 0 ? (
              <h3 id="no-todo">No recycled works</h3>
            ) : (
              recycledWorks.flatMap((work) => {
                const workElements = [
                  <li key={work.id} className="work-item">
                    <div className="work-header">
                      <span className="item-name">{work.name}</span>
                      <div className="work-actions">
                        <button
                          id="restore-bt"
                          onClick={() => handleRestoreWork(work.id)}
                        >
                          <AiOutlineUndo size={20} color="#007bff" />
                        </button>
                        <button
                          id="del-bt"
                          onClick={() =>
                            setRecycledWorks((prev) =>
                              prev.filter((w) => w.id !== work.id)
                            )
                          }
                        >
                          <AiFillDelete size={20} color="#FF6969" />
                        </button>
                        <button
                          className="expand-bt"
                          onClick={() => toggleExpand(work.id)}
                        >
                          {expandedWorkIds.includes(work.id) ? (
                            <FaChevronUp />
                          ) : (
                            <FaChevronDown />
                          )}
                        </button>
                      </div>
                    </div>
                  </li>,
                ];

                if (
                  expandedWorkIds.includes(work.id) &&
                  work.subWorks &&
                  work.subWorks.length > 0
                ) {
                  work.subWorks.forEach((task, index) => {
                    workElements.push(
                      <li key={`${work.id}-${index}`} className="task-item">
                        <div className="task-details">
                          <span>{task.name}</span>
                          <button
                            className="task-delete-icon"
                            onClick={() =>
                              handleDeleteTaskInRecycle(work.id, index)
                            }
                          >
                            <AiFillDelete
                              size={14}
                              className="task-delete-icon-style"
                            />
                          </button>
                        </div>
                      </li>
                    );
                  });
                }

                return workElements;
              })
            )}
          </ul>
        ) : (
          // Other views (ongoing/completed)
          <div>/* Existing logic for ongoing/completed */</div>
        )}
      </div>
    </div>
  );
};

export default Project;
