import React, { useState, useEffect, useMemo } from "react";
import { AiFillDelete } from "react-icons/ai";
import { BiSearchAlt2 } from "react-icons/bi";
import { FaChevronDown, FaChevronUp } from "react-icons/fa";
import "aos/dist/aos.css";
import "./styles/project.css";

const Project = ({ works, setWorks, completedWorks, setCompletedWorks }) => {
  const [viewMode, setViewMode] = useState("ongoing");
  const [searchQuery, setSearchQuery] = useState("");
  const [expandedWorkIds, setExpandedWorkIds] = useState([]);
  const [taskCompletionStatus, setTaskCompletionStatus] = useState({});

  // Filter works based on view mode (ongoing/completed) and search query
  const filteredWorks = useMemo(() => {
    const allOngoingWorks = Object.values(works).flat();

    if (viewMode === "ongoing") {
      return allOngoingWorks.filter(
        (work) =>
          !completedWorks.some((completedWork) => completedWork.id === work.id)
      );
    }

    if (viewMode === "completed") {
      return completedWorks.filter(
        (completedWork, index, self) =>
          self.findIndex((work) => work.id === completedWork.id) === index
      );
    }

    return [];
  }, [viewMode, works, completedWorks]);

  // Toggle expand/collapse for a work item
  const toggleExpand = (workId) => {
    setExpandedWorkIds((prevExpandedWorkIds) =>
      prevExpandedWorkIds.includes(workId)
        ? prevExpandedWorkIds.filter((id) => id !== workId)
        : [...prevExpandedWorkIds, workId]
    );
  };

  // Handle marking work as completed or ongoing
  const handleToggleComplete = (work) => {
    if (viewMode === "ongoing") {
      setCompletedWorks((prev) => {
        const updatedCompleted = [...prev, work];
        return updatedCompleted;
      });

      setWorks((prevWorks) => {
        const updatedWorks = { ...prevWorks };
        Object.keys(updatedWorks).forEach((key) => {
          updatedWorks[key] = updatedWorks[key].filter((w) => w.id !== work.id);
        });
        return updatedWorks;
      });

      // Mark all tasks as completed
      setTaskCompletionStatus((prevStatus) => {
        const updatedStatus = { ...prevStatus };
        work.subWorks.forEach((_, index) => {
          updatedStatus[`${work.id}-${index}`] = true;
        });
        return updatedStatus;
      });
    } else {
      setCompletedWorks((prev) => {
        const updatedCompleted = prev.filter((w) => w.id !== work.id);
        return updatedCompleted;
      });

      setWorks((prevWorks) => {
        const updatedWorks = { ...prevWorks };
        const type = work.type || "doNow";
        updatedWorks[type] = [...updatedWorks[type], work];
        return updatedWorks;
      });

      // Mark all tasks as incomplete
      setTaskCompletionStatus((prevStatus) => {
        const updatedStatus = { ...prevStatus };
        work.subWorks.forEach((_, index) => {
          updatedStatus[`${work.id}-${index}`] = false;
        });
        return updatedStatus;
      });
    }
  };

  // Toggle task completion status
  const toggleTaskCompletion = (workId, taskIndex) => {
    setTaskCompletionStatus((prevStatus) => {
      const updatedStatus = { ...prevStatus };
      const taskKey = `${workId}-${taskIndex}`;
      updatedStatus[taskKey] = !updatedStatus[taskKey];

      const work = filteredWorks.find((w) => w.id === workId);
      if (work) {
        const totalTasks = work.subWorks.length;
        const completedTasks = work.subWorks.filter((_, index) => updatedStatus[`${workId}-${index}`]).length;

        // Nếu tất cả các task được hoàn thành, đánh dấu work là hoàn thành
        if (completedTasks === totalTasks) {
          handleToggleComplete(work);
        } else if (completedTasks < totalTasks && viewMode === "completed") {
          // Nếu bỏ tick một task của work đã hoàn thành, chuyển work về ongoing
          handleToggleComplete(work);
        }
      }

      return updatedStatus;
    });
  };

  // Calculate the progress of each work based on task completion status
  const calculateProgress = (work) => {
    if (!work.subWorks || work.subWorks.length === 0) return 0;

    const totalTasks = work.subWorks.length;
    const completedTasks = work.subWorks.filter((_, index) => {
      const taskKey = `${work.id}-${index}`;
      return taskCompletionStatus[taskKey];
    }).length;

    return Math.round((completedTasks / totalTasks) * 100);
  };

  return (
    <div className="main-form" data-aos="zoom-in">
      <div className="search-bar">
        <h1>Projects</h1>
        <input
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          type="search"
          placeholder={`Search ${
            viewMode === "ongoing" ? "ongoing" : "completed"
          } works`}
        />
        <button id="search-bt">
          <BiSearchAlt2 size={22} />
        </button>
      </div>
      <div className="view-toggle">
        <button
          className={viewMode === "ongoing" ? "active" : ""}
          onClick={() => setViewMode("ongoing")}
        >
          On Going Works
        </button>
        <button
          className={viewMode === "completed" ? "active" : ""}
          onClick={() => setViewMode("completed")}
        >
          Completed Works
        </button>
      </div>
      <div className="item-list">
        {filteredWorks.length === 0 ? (
          <h3 id="no-todo">
            {viewMode === "ongoing"
              ? "No ongoing works"
              : "No completed works"}
          </h3>
        ) : (
          <ul>
            {filteredWorks.flatMap((work) => {
              const progress = calculateProgress(work);
              const workElements = [
                <li key={work.id} className="work-item">
                  <div className="work-header">
                    <label htmlFor="" className="item-name">
                      <input
                        type="checkbox"
                        checked={viewMode === "completed"}
                        onChange={() => handleToggleComplete(work)}
                      />
                      {work.name}
                    </label>
                    <div className="work-actions">
                      {viewMode === "ongoing" && (
                        <button id="del-bt">
                          <AiFillDelete size={20} color="#FF6969" />
                        </button>
                      )}
                      <button
                        className="expand-bt"
                        onClick={() => toggleExpand(work.id)}
                      >
                        {expandedWorkIds.includes(work.id) ? (
                          <FaChevronUp />
                        ) : (
                          <FaChevronDown />
                        )}
                      </button>
                    </div>
                  </div>
                  {/* Thanh tiến trình ở dòng riêng */}
                  <div className="progress-container progress-container-full">
                    <div className="progress-bar">
                      <div
                        className="progress"
                        style={{
                          width: `${progress}%`,
                        }}
                      ></div>
                    </div>
                    <span className="progress-percentage">{progress}%</span>
                  </div>
                </li>,
              ];

              if (
                expandedWorkIds.includes(work.id) &&
                work.subWorks &&
                work.subWorks.length > 0
              ) {
                work.subWorks.forEach((task, index) => {
                  const taskKey = `${work.id}-${index}`;
                  const isTaskCompleted = taskCompletionStatus[taskKey];

                  workElements.push(
                    <li key={taskKey} className="task-item">
                      <div className="task-details">
                        <input
                          type="checkbox"
                          checked={!!isTaskCompleted}
                          onChange={() => toggleTaskCompletion(work.id, index)}
                        />
                        <span
                          style={{
                            textDecoration: isTaskCompleted
                              ? "line-through"
                              : "none",
                          }}
                        >
                          {task.name} - Description: {task.description}, Time:{" "}
                          {task.startTime} - {task.endTime}, Repeat:{" "}
                          {task.repeatOption === "Tùy chỉnh"
                            ? `Lặp lại ${task.customRepeat.repeatEvery} ${
                                task.customRepeat.repeatUnit
                              }${
                                task.customRepeat.repeatUnit === "ngày"
                                  ? ` một lần từ ${new Date(
                                      task.customRepeat.startDate
                                    ).toLocaleDateString("vi-VN")}`
                                  : ` một lần vào các ngày ${task.customRepeat.repeatDays.join(
                                      ", "
                                    )}`
                              }`
                            : task.repeatOption}
                        </span>
                      </div>
                    </li>
                  );
                });
              }

              return workElements;
            })}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Project;
