import React, { useState } from "react";
import DarkMode from "./DarkMode/Darkmode";
import Notification from "./Notification/Notification";
import { IoMdNotifications } from "react-icons/io";


const Secondary = ({ upcomingTasks = [], onOpenNotifi, onCloseNotifi }) => {
  const [dialog, setDialog] = useState({ isLoading: false });

  function openNotifi() {
    onOpenNotifi();
    setDialog({ isLoading: true });  // Thiết lập isLoading thành true để hiển thị Notification
  }

  function closeNotifi() {
    onCloseNotifi();
    setDialog({ isLoading: false });  // Thiết lập isLoading thành false để ẩn Notification
  }

  return (
    <div className="secondary-actions">
      <DarkMode />
      <button
        className={`${upcomingTasks.length ? "bell" : ""}`}
        onClick={openNotifi}  // Sử dụng onClick này để kích hoạt hiển thị Notification
      >
        <span id="noti-count">{upcomingTasks.length}</span>
        <IoMdNotifications size={25} color="#3081D0" />
      </button>
      {dialog.isLoading && (
        <Notification
          closeNotifi={closeNotifi}
          upcomingTasks={upcomingTasks}
        />
      )}
    </div>
  );
};

export default Secondary;
