import Toggler from "./components/Toggler";
import Calendar from "./components/Calendar";
import Home from "./components/Home";
import "react-toastify/dist/ReactToastify.css";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import Project from "./components/Project";
import Focus from "./components/Focus";
import Dashboard from "./components/Dashboard";
import Report from "./components/Report";
import ForgotPass from "./components/ForgotPass";
import TimeTable from "./components/TimeTable";
import ResetPass from "./components/ResetPass";
import { useState, useEffect } from "react";

function App() {
  // Quản lý `works`, `completedWorks` và `taskCompletionStatus` từ cấp App
  const [works, setWorks] = useState(() => {
    const savedWorks = localStorage.getItem("works");
    return savedWorks ? JSON.parse(savedWorks) : {
      doNow: [],
      schedule: [],
      delegate: [],
      drop: [],
    };
  });

  const [completedWorks, setCompletedWorks] = useState(() => {
    const savedCompletedWorks = localStorage.getItem("worksdone");
    return savedCompletedWorks ? JSON.parse(savedCompletedWorks) : [];
  });

  const [taskCompletionStatus, setTaskCompletionStatus] = useState(() => {
    const savedTaskStatus = localStorage.getItem("taskCompletionStatus");
    return savedTaskStatus ? JSON.parse(savedTaskStatus) : {};
  });

  // Cập nhật `localStorage` mỗi khi `works`, `completedWorks` hoặc `taskCompletionStatus` thay đổi
  useEffect(() => {
    localStorage.setItem("works", JSON.stringify(works));
    localStorage.setItem("worksdone", JSON.stringify(completedWorks));
    localStorage.setItem("taskCompletionStatus", JSON.stringify(taskCompletionStatus));
  }, [works, completedWorks, taskCompletionStatus]);

  return (
    <BrowserRouter>
      <ToastContainer
        position="top-center"
        autoClose={800}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />
      <Routes>
        <Route path="/" element={<Toggler toast={toast} />} />
        <Route path="/ForgotPass" element={<ForgotPass toast={toast} />} />
        <Route path="/ResetPass/:id/:token" element={<ResetPass toast={toast} />} />
        <Route path="/Home" element={<Home tasks={tasks} />}>
          <Route
            path="/Home"
            element={
              <Dashboard
                works={works}
                setWorks={setWorks}
                completedWorks={completedWorks}
                setCompletedWorks={setCompletedWorks}
                taskCompletionStatus={taskCompletionStatus}
                setTaskCompletionStatus={setTaskCompletionStatus}
              />
            }
          />
          <Route
            path="/Home/projects"
            element={
              <Project
                works={works}
                setWorks={setWorks}
                completedWorks={completedWorks}
                setCompletedWorks={setCompletedWorks}
                taskCompletionStatus={taskCompletionStatus}
                setTaskCompletionStatus={setTaskCompletionStatus}
              />
            }
          />
        </Route>
      </Routes>
    </BrowserRouter>
  );
}


export default App;
