import React, { useEffect, useState } from "react";
import "./styles/focus.css";

const Focus = ({ works = {}, toast }) => {
  const [mode, setMode] = useState("auto");
  const [selectedWork, setSelectedWork] = useState(null);
  const [selectedTask, setSelectedTask] = useState(null);
  const [currentTask, setCurrentTask] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isFocusing, setIsFocusing] = useState(false);

  const [pomodoroTime, setPomodoroTime] = useState(25 * 60); // 25 minutes in seconds
  const [pomodoroState, setPomodoroState] = useState("Work"); // Work, Short Break, Long Break
  const [pomodoroCycles, setPomodoroCycles] = useState(0); // Count work sessions
  const [isRunning, setIsRunning] = useState(false); // Timer state

  useEffect(() => {
    if (mode === "auto") {
      handleAutoSelectTask();
    }
  }, [mode, works]);

  useEffect(() => {
    let timer;
    if (isRunning && pomodoroTime > 0) {
      timer = setInterval(() => {
        setPomodoroTime((prev) => prev - 1);
      }, 1000);
    } else if (pomodoroTime === 0) {
      handlePomodoroEnd();
    }
    return () => clearInterval(timer);
  }, [isRunning, pomodoroTime]);

  const handlePomodoroEnd = () => {
    if (pomodoroState === "Work") {
      setPomodoroCycles((prev) => prev + 1);
      if ((pomodoroCycles + 1) % 4 === 0) {
        setPomodoroState("Long Break");
        setPomodoroTime(15 * 60); // 15 minutes
      } else {
        setPomodoroState("Short Break");
        setPomodoroTime(5 * 60); // 5 minutes
      }
    } else {
      setPomodoroState("Work");
      setPomodoroTime(25 * 60); // 25 minutes
    }
    toast.info(`${pomodoroState} session ended. Starting next session!`);
  };

  const handlePomodoroSkip = () => {
    setIsRunning(false);
    handlePomodoroEnd();
  };

  const handlePomodoroPause = () => {
    setIsRunning((prev) => !prev);
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${String(mins).padStart(2, "0")}:${String(secs).padStart(2, "0")}`;
  };

  const handleAutoSelectTask = () => {
    if (!works) {
      setCurrentTask(null);
      return;
    }

    const now = new Date();
    const currentTime = now.getHours() * 60 + now.getMinutes();

    for (const type in works) {
      for (const work of works[type] || []) {
        if (work.subWorks && work.subWorks.length > 0) {
          for (const task of work.subWorks) {
            const [startHour, startMinute] = task.startTime.split(":").map(Number);
            const [endHour, endMinute] = task.endTime.split(":").map(Number);
            const taskStartTime = startHour * 60 + startMinute;
            const taskEndTime = endHour * 60 + endMinute;

            if (currentTime >= taskStartTime && currentTime <= taskEndTime) {
              setCurrentTask({
                ...task,
                workName: work.name,
                workStartDate: work.startDate,
                workEndDate: work.endDate,
              });
              return;
            }
          }
        }
      }
    }
    setCurrentTask(null);
  };

  const openManualSelectModal = () => {
    setIsModalOpen(true);
  };

  const handleManualSelectWork = (work) => {
    setSelectedWork(work);
    setSelectedTask(null);
  };

  const handleManualSelectTask = (task) => {
    setSelectedTask(task);
  };

  const handleManualSelectionDone = () => {
    if (!selectedTask) {
      toast.error("Vui lòng chọn một Task trước khi nhấn Done!");
      return;
    }
    setCurrentTask({
      ...selectedTask,
      workName: selectedWork.name,
      workStartDate: selectedWork.startDate,
      workEndDate: selectedWork.endDate,
    });
    setIsModalOpen(false);
    setSelectedWork(null);
    setSelectedTask(null);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedWork(null);
    setSelectedTask(null);
  };

  const handleFocusStart = () => {
    const task = mode === "free" ? null : mode === "auto" ? currentTask : selectedTask;

    if (!task && mode !== "free") {
      alert("No task selected for focus.");
      return;
    }

    setIsFocusing(true);
    toast.success("Focus session started!");
  };

  const handleFocusStop = () => {
    setIsFocusing(false);
    toast.info("Focus session stopped.");
  };

  return (
    <div className="main-form" data-aos="zoom-in">
      <header className="search-bar">
        <h1>Focus</h1>
      </header>

      <div className="focus-modes">
        <button
          className={`focus-mode ${mode === "auto" ? "active" : ""}`}
          onClick={() => setMode("auto")}
        >
          Tự động
        </button>
        <button
          className={`focus-mode ${mode === "manual" ? "active" : ""}`}
          onClick={openManualSelectModal}
        >
          Tự chọn
        </button>
        <button
          className={`focus-mode ${mode === "free" ? "active" : ""}`}
          onClick={() => setMode("free")}
        >
          Tự do
        </button>

        {/* Pomodoro Timer */}
        <div className="pomodoro-timer">
          <h3>{pomodoroState} - {formatTime(pomodoroTime)}</h3>
          <div className="pomodoro-controls">
            <button className="focus-button" onClick={handlePomodoroPause}>
              {isRunning ? "Pause" : "Start"}
            </button>
            <button className="focus-button" onClick={handlePomodoroSkip}>
              Skip
            </button>
          </div>
        </div>
      </div>

      <div className="focus-content">
        {mode === "auto" && currentTask && (
          <div className="task-details">
            <h2>Task hiện tại</h2>
            <p><strong>Work:</strong> {currentTask.workName}</p>
            <p><strong>Task:</strong> {currentTask.name}</p>
            <p><strong>Thời gian:</strong> {currentTask.startTime} - {currentTask.endTime}</p>
            <p><strong>Ngày Work:</strong> {currentTask.workStartDate} - {currentTask.workEndDate}</p>
          </div>
        )}

        {mode === "free" && <p>Focus không giới hạn thời gian.</p>}

        {isFocusing ? (
          <button className="focus-button" onClick={handleFocusStop}>
            Dừng
          </button>
        ) : (
          <button className="focus-button" onClick={handleFocusStart}>
            Bắt đầu
          </button>
        )}
      </div>

      {isModalOpen && (
        <div className="modal">
          <div className="modal-task-content">
            <div className="modal-left">
              <h2>Danh sách Work</h2>
              {Object.values(works).flat().map((work) => (
                <div
                  key={work.id}
                  className={`work-item ${selectedWork?.id === work.id ? "active" : ""}`}
                  onClick={() => handleManualSelectWork(work)}
                >
                  <h4>{work.name}</h4>
                  <p>Ngày: {work.startDate} - {work.endDate}</p>
                </div>
              ))}
            </div>
            <div className="divider"></div>
            <div className="modal-right">
              <button className="modal-close" onClick={closeModal}>
                &times;
              </button>
              <h2>Danh sách Task</h2>
              {selectedWork && selectedWork.subWorks ? (
                <>
                  {selectedWork.subWorks.map((task, index) => (
                    <div
                      key={index}
                      className={`task-item ${selectedTask?.name === task.name ? "active" : ""}`}
                      onClick={() => handleManualSelectTask(task)}
                    >
                      <h4>{task.name}</h4>
                      <p>Thời gian: {task.startTime} - {task.endTime}</p>
                    </div>
                  ))}
                  <div className="modal-buttons-right">
                    <button className="focus-button" onClick={handleManualSelectionDone}>
                      Done
                    </button>
                  </div>
                </>
              ) : (
                <p>Chọn một Work để xem Task.</p>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Focus;
