import Toggler from "./components/Toggler";
import Calendar from "./components/Calendar";
import Home from "./components/Home";
import "react-toastify/dist/ReactToastify.css";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import Project  from "./components/Project";
import Focus from "./components/Focus"
import Dashboard from "./components/Dashboard";
import Report from "./components/Report";
import ForgotPass from "./components/ForgotPass";
import TimeTable from "./components/TimeTable";
import ResetPass from "./components/ResetPass";
import { useState } from "react";

function App() {
  // const [notes, setNotes] = useState([]);
  const [tasks, setTasks] = useState({
    doNow: [],
    schedule: [],
    delegate: [],
    drop: [],
  });
  // const [todo, setTodo] = useState([]);
  // const [focus, setFocus] = useState([]);
  const [completedTasks, setCompletedTasks] = useState([]);

  return (
    <BrowserRouter>
      <ToastContainer
        position="top-center"
        autoClose={800}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />
          <Routes>
            <Route path="/" element={<Toggler toast={toast} />} />
            <Route path="/ForgotPass" element={<ForgotPass toast={toast} />} />
            <Route path="/ResetPass/:id/:token" element={<ResetPass toast={toast} />} />
            <Route path="/Home" element={<Home tasks={tasks} />}>
            <Route
          index
          element={
            <Dashboard
              tasks={tasks}
              setTasks={setTasks}
              completedTasks={completedTasks}
              setCompletedTasks={setCompletedTasks}
            />
          }
        />
        <Route
            path="/Home/projects"
            element={
              <Project
                tasks={tasks}
                completedTasks={completedTasks}
                setTasks={setTasks}
                setCompletedTasks={setCompletedTasks}
              />
            }
          />
        <Route
          path="/Home/focus"
          element={<Focus toast={toast} todo={todo} setTodo={setTodo} />}
        />
        <Route
          path="/Home/timetable"
          element={<TimeTable toast={toast} todo={todo} setTodo={setTodo} />}
        />
        <Route
          path="/Home/calendar"
          element={<Calendar toast={toast} todo={todo} setTodo={setTodo} />}
        />
        <Route
          path="/Home/report"
          element={<Report toast={toast} todo={todo} setTodo={setTodo} />}
        />
      </Route>
    </Routes>

    </BrowserRouter>
  );
}

export default App;
