import React, { useEffect, useState } from "react";
import DarkMode from "./DarkMode/Darkmode";
import Notification from "./Notification/Notification";
import { IoMdNotifications } from "react-icons/io";
import Aos from 'aos';
import 'aos/dist/aos.css';

const Secondary = ({ triggerAnimation },{ upcomingTasks = [], onOpenNotifi, onCloseNotifi }) => {
  const [dialog, setDialog] = useState({ isLoading: false });

  useEffect(() => {
    Aos.init({ duration: 1000 });
  }, []);

  function openNotifi() {
    onOpenNotifi();  // Giả sử hàm này được truyền từ component cha
    setDialog({ isLoading: true });
  }

  function closeNotifi() {
    onCloseNotifi(); // Giả sử hàm này được truyền từ component cha
    setDialog({ isLoading: false });
  }

  return (
    <div className={`secondary-actions ${triggerAnimation ? 'zoom-in' : ''}`} data-aos="zoom-in">
      <DarkMode />
      <button
        className={`${upcomingTasks.length ? " bell" : ""}`}
        onClick={openNotifi}
      >
        <span id="noti-count">{upcomingTasks.length}</span>
        <span>
          <IoMdNotifications size={25} color="#3081D0" />
        </span>
      </button>
      {dialog.isLoading && (
        <Notification
          closeNotifi={closeNotifi}
          upcomingTasks={upcomingTasks}
        />
      )}
    </div>
  );
};

export default Secondary;
