import React, { useEffect, useState } from "react";
import "./styles/focus.css";

const Focus = ({ works = {}, toast }) => {
  const [mode, setMode] = useState("auto");
  const [selectedWork, setSelectedWork] = useState(null);
  const [selectedTask, setSelectedTask] = useState(null);
  const [currentTask, setCurrentTask] = useState(null); // Task được hiển thị
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [tempWork, setTempWork] = useState(null); // Temporary work for modal
  const [tempTask, setTempTask] = useState(null); // Temporary task for modal
  const [isRunning, setIsRunning] = useState(false);
  const [timeLeft, setTimeLeft] = useState(1500); // 25 minutes in seconds
  const [cycle, setCycle] = useState(0);
  const [isFocusPhase, setIsFocusPhase] = useState(true);

  useEffect(() => {
    if (mode === "auto") {
      // Không reset currentTask khi chuyển qua chế độ "tự động"
    }
  }, [mode]);

  useEffect(() => {
    let timer;
    if (isRunning && timeLeft > 0) {
      timer = setInterval(() => {
        setTimeLeft((prev) => prev - 1);
      }, 1000);
    } else if (timeLeft === 0) {
      handlePomodoroComplete();
    }
    return () => clearInterval(timer);
  }, [isRunning, timeLeft]);

  const handlePomodoroComplete = () => {
    setIsRunning(false);
    if (isFocusPhase) {
      setIsFocusPhase(false);
      setCycle((prev) => prev + 1);
      if ((cycle + 1) % 4 === 0) {
        setTimeLeft(900);
        toast.info("Long Break! Take a 15-minute rest.");
      } else {
        setTimeLeft(300);
        toast.info("Short Break! Take a 5-minute rest.");
      }
    } else {
      setIsFocusPhase(true);
      setTimeLeft(1500);
      toast.success("Back to Focus! 25 minutes.");
    }
  };

  const handleStartPause = () => {
    setIsRunning((prev) => !prev);
  };

  const handleSkip = () => {
    setTimeLeft(0);
  };

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${minutes.toString().padStart(2, "0")}:${secs.toString().padStart(2, "0")}`;
  };

  const openManualSelectModal = () => {
    setTempWork(selectedWork); // Lưu trạng thái Work tạm thời
    setTempTask(selectedTask); // Lưu trạng thái Task tạm thời
    setIsModalOpen(true);
  };

  const handleManualSelectWork = (work) => {
    setTempWork((prev) => (prev?.id === work.id ? null : work)); // Toggle selection
    setTempTask(null); // Reset Task nếu Work thay đổi
  };

  const handleManualSelectTask = (task) => {
    setTempTask((prev) => (prev?.name === task.name ? null : task)); // Toggle selection
  };

  const handleManualSelectionDone = () => {
    setSelectedWork(tempWork);
    setSelectedTask(tempTask);

    setCurrentTask(
      tempTask
        ? {
            ...tempTask,
            workName: tempWork.name,
            workStartDate: tempWork.startDate,
            workEndDate: tempWork.endDate,
          }
        : null
    );
    setIsModalOpen(false);
  };

  const closeModal = () => {
    setTempWork(selectedWork); // Khôi phục Work đang chọn
    setTempTask(selectedTask); // Khôi phục Task đang chọn
    setIsModalOpen(false);
  };

  return (
    <div className="main-form" data-aos="zoom-in">
      <header className="search-bar">
        <h1>Focus</h1>
      </header>

      <div className="focus-modes">
        <button
          className={`focus-mode ${mode === "auto" ? "active" : ""}`}
          onClick={() => setMode("auto")}
        >
          Tự động
        </button>
        <button
          className={`focus-mode ${mode === "manual" ? "active" : ""}`}
          onClick={() => {
            setMode("manual");
            openManualSelectModal();
          }}
        >
          Tự chọn
        </button>
        <button
          className={`focus-mode ${mode === "free" ? "active" : ""}`}
          onClick={() => setMode("free")}
        >
          Tự do
        </button>
      </div>

      <div className="focus-content">
        {currentTask ? (
          <div className="task-details">
            <h2>Task hiện tại</h2>
            <p><strong>Work:</strong> {currentTask.workName}</p>
            <p><strong>Task:</strong> {currentTask.name}</p>
            <p><strong>Thời gian:</strong> {currentTask.startTime} - {currentTask.endTime}</p>
            <p><strong>Ngày Work:</strong> {currentTask.workStartDate} - {currentTask.workEndDate}</p>
          </div>
        ) : (
          <p>No available</p>
        )}

        <div className="pomodoro-timer">
          <h2>Pomodoro</h2>
          <p>{formatTime(timeLeft)}</p>
          <div className="timer-controls">
            <button onClick={handleStartPause}>
              {isRunning ? "Tạm dừng" : "Bắt đầu"}
            </button>
            <button onClick={handleSkip}>Bỏ qua</button>
          </div>
        </div>
      </div>

      {isModalOpen && (
        <div className="modal">
          <div className="modal-task-content">
            <div className="modal-left">
              <h2>Danh sách Work</h2>
              {Object.values(works).flat().map((work) => (
                <div
                  key={work.id}
                  className={`work-item ${tempWork?.id === work.id ? "active" : ""}`}
                  onClick={() => handleManualSelectWork(work)}
                >
                  <h4>{work.name}</h4>
                  <p>Ngày: {work.startDate} - {work.endDate}</p>
                </div>
              ))}
            </div>
            <div className="divider"></div>
            <div className="modal-right">
              <div className="modal-buttons">
                <button className="focus-button" onClick={handleManualSelectionDone}>
                  Done
                </button>
                <button className="modal-close" onClick={closeModal}>
                  &times;
                </button>
              </div>
              <h2>Danh sách Task</h2>
              {tempWork && tempWork.subWorks ? (
                <>
                  {tempWork.subWorks.map((task, index) => (
                    <div
                      key={index}
                      className={`task-item ${tempTask?.name === task.name ? "active" : ""}`}
                      onClick={() => handleManualSelectTask(task)}
                    >
                      <h4>{task.name}</h4>
                      <p>Thời gian: {task.startTime} - {task.endTime}</p>
                    </div>
                  ))}
                </>
              ) : (
                <p>Chọn một Work để xem Task.</p>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Focus;
