import React, { useState, useEffect, useMemo } from "react";
import { AiFillDelete } from "react-icons/ai";
import { BiSearchAlt2 } from "react-icons/bi";
import { FaChevronDown, FaChevronUp } from "react-icons/fa";
import "aos/dist/aos.css";
import "./styles/project.css";

const Project = () => {
  const [works, setWorks] = useState({ doNow: [], schedule: [], delegate: [], drop: [] });
  const [completedWorks, setCompletedWorks] = useState([]);
  const [viewMode, setViewMode] = useState("ongoing");
  const [searchQuery, setSearchQuery] = useState("");
  const [expandedWorkIds, setExpandedWorkIds] = useState([]); // State to track expanded works

  // Load data from localStorage when component mounts
  useEffect(() => {
    const storedWorks = JSON.parse(localStorage.getItem("works")) || { doNow: [], schedule: [], delegate: [], drop: [] };
    const storedCompletedWorks = JSON.parse(localStorage.getItem("worksdone")) || [];
    setWorks(storedWorks);
    setCompletedWorks(storedCompletedWorks);
  }, []);

  // Filter works based on view mode (ongoing/completed) and search query
  const filteredWorks = useMemo(() => {
    const allOngoingWorks = Object.values(works).flat(); // Merge all works from different categories

    if (viewMode === "ongoing") {
      // Show only works that are not completed
      return allOngoingWorks.filter(
        (work) => !completedWorks.some((completedWork) => completedWork.id === work.id)
      );
    }

    if (viewMode === "completed") {
      // Show only completed works (unique)
      return completedWorks.filter(
        (completedWork, index, self) =>
          self.findIndex((work) => work.id === completedWork.id) === index // Remove duplicates
      );
    }

    return [];
  }, [viewMode, works, completedWorks]);

  // Toggle expand/collapse for a work item
  const toggleExpand = (workId) => {
    setExpandedWorkIds((prevExpandedWorkIds) =>
      prevExpandedWorkIds.includes(workId)
        ? prevExpandedWorkIds.filter((id) => id !== workId)
        : [...prevExpandedWorkIds, workId]
    );
  };

  return (
    <div className="main-form" data-aos="zoom-in">
      <div className="search-bar">
        <h1>Projects</h1>
        <input
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          type="search"
          placeholder={`Search ${viewMode === "ongoing" ? "ongoing" : "completed"} works`}
        />
        <button id="search-bt">
          <BiSearchAlt2 size={22} />
        </button>
      </div>
      <div className="view-toggle">
        <button
          className={viewMode === "ongoing" ? "active" : ""}
          onClick={() => setViewMode("ongoing")}
        >
          On Going Works
        </button>
        <button
          className={viewMode === "completed" ? "active" : ""}
          onClick={() => setViewMode("completed")}
        >
          Completed Works
        </button>
      </div>
      <div className="item-list">
        {filteredWorks.length === 0 ? (
          <h3 id="no-todo">
            {viewMode === "ongoing" ? "No ongoing works" : "No completed works"}
          </h3>
        ) : (
          <ul>
            {filteredWorks.map((work) => (
              <li key={work.id} className="work-item">
                <div className="work-header">
                  <label htmlFor="" className="item-name">
                    <input type="checkbox" checked={viewMode === "completed"} readOnly />
                    {work.name} {/* Display work name */}
                  </label>
                  <div className="work-actions">
                    {viewMode === "ongoing" && (
                      <button id="del-bt">
                        <AiFillDelete size={20} color="#FF6969" />
                      </button>
                    )}
                    <button className="expand-bt" onClick={() => toggleExpand(work.id)}>
                      {expandedWorkIds.includes(work.id) ? <FaChevronUp /> : <FaChevronDown />}
                    </button>
                  </div>
                </div>
                {expandedWorkIds.includes(work.id) && work.subWorks && work.subWorks.length > 0 && (
                  <ul className="task-list">
                    {work.subWorks.map((task, index) => (
                      <li key={index} className="task-item">
                        {task.name}
                      </li>
                    ))}
                  </ul>
                )}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Project;
