import React, { useState, useEffect, useMemo } from "react";
import { AiFillDelete } from "react-icons/ai";
import { BiSearchAlt2 } from "react-icons/bi";
import { AiOutlineDown, AiOutlineUp } from "react-icons/ai";
import "aos/dist/aos.css";
import "./styles/project.css";

const Project = () => {
  const [works, setWorks] = useState({ doNow: [], schedule: [], delegate: [], drop: [] });
  const [completedWorks, setCompletedWorks] = useState([]);
  const [viewMode, setViewMode] = useState("ongoing");
  const [searchQuery, setSearchQuery] = useState("");
  const [expandedWorkIds, setExpandedWorkIds] = useState([]);

  useEffect(() => {
    const storedWorks = JSON.parse(localStorage.getItem("works")) || {
      doNow: [],
      schedule: [],
      delegate: [],
      drop: [],
    };
    const storedCompletedWorks = JSON.parse(localStorage.getItem("worksdone")) || [];
    setWorks(storedWorks);
    setCompletedWorks(storedCompletedWorks);
  }, []);

  const filteredWorks = useMemo(() => {
    const allOngoingWorks = Object.values(works).flat();

    if (viewMode === "ongoing") {
      return allOngoingWorks.filter(
        (work) => !completedWorks.some((completedWork) => completedWork.id === work.id)
      );
    }

    if (viewMode === "completed") {
      return completedWorks.filter(
        (completedWork, index, self) =>
          self.findIndex((work) => work.id === completedWork.id) === index
      );
    }

    return [];
  }, [viewMode, works, completedWorks]);

  const toggleExpand = (workId) => {
    setExpandedWorkIds((prev) =>
      prev.includes(workId) ? prev.filter((id) => id !== workId) : [...prev, workId]
    );
  };

  return (
    <div className="main-form" data-aos="zoom-in">
      <div className="search-bar">
        <h1>Projects</h1>
        <input
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          type="search"
          placeholder={`Search ${viewMode === "ongoing" ? "ongoing" : "completed"} works`}
        />
        <button id="search-bt">
          <BiSearchAlt2 size={22} />
        </button>
      </div>
      <div className="view-toggle">
        <button
          className={viewMode === "ongoing" ? "active" : ""}
          onClick={() => setViewMode("ongoing")}
        >
          On Going Works
        </button>
        <button
          className={viewMode === "completed" ? "active" : ""}
          onClick={() => setViewMode("completed")}
        >
          Completed Works
        </button>
      </div>
      <div className="item-list">
        {filteredWorks.length === 0 ? (
          <h3 id="no-todo">
            {viewMode === "ongoing" ? "No ongoing works" : "No completed works"}
          </h3>
        ) : (
          <ul>
            {filteredWorks.map((work) => (
              <li key={work.id} className="work-item">
                <div className="work-header">
                  <div className="work-info">
                    <button
                      className="expand-btn"
                      onClick={() => toggleExpand(work.id)}
                      aria-label={expandedWorkIds.includes(work.id) ? "Collapse" : "Expand"}
                    >
                      {expandedWorkIds.includes(work.id) ? (
                        <AiOutlineUp size={18} />
                      ) : (
                        <AiOutlineDown size={18} />
                      )}
                    </button>
                    <span className="work-name">{work.name}</span>
                  </div>
                  {viewMode === "ongoing" && (
                    <button id="del-bt">
                      <AiFillDelete size={20} color="#FF6969" />
                    </button>
                  )}
                </div>
                {expandedWorkIds.includes(work.id) && work.subWorks && (
                  <ul className="task-list">
                    {work.subWorks.map((task, index) => (
                      <li key={index} className="task-item">
                        {task.name}
                      </li>
                    ))}
                  </ul>
                )}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Project;
