import React, { useState, useEffect, useMemo } from "react";
import "aos/dist/aos.css";
import "./styles/timetable.css";

const calculateTasksForWeek = (works) => {
  if (!works || Object.keys(works).length === 0) {
    return Array.from({ length: 7 }, () => []); // Trả về tuần rỗng nếu không có work
  }

  const today = new Date();
  const startOfWeek = new Date(today.setDate(today.getDate() - today.getDay() + 1)); // Thứ 2
  const endOfWeek = new Date(startOfWeek);
  endOfWeek.setDate(startOfWeek.getDate() + 6); // Chủ nhật

  const weekTasks = Array.from({ length: 7 }, () => []);

  Object.values(works)
    .flat()
    .forEach((work) => {
      const workStartDate = new Date(work.startDate);
      const workEndDate = new Date(work.endDate);

      if (!work.subWorks) return;

      work.subWorks.forEach((task) => {
        const taskStartDate =
          task.repeatOption === "Tùy chỉnh" && task.customRepeat.startDate
            ? new Date(task.customRepeat.startDate)
            : workStartDate;

        let currentDate = new Date(taskStartDate);

        while (currentDate <= workEndDate) {
          // Chỉ xét các ngày trong tuần hiện tại
          if (currentDate >= startOfWeek && currentDate <= endOfWeek) {
            const dayIndex = (currentDate.getDay() + 6) % 7; // Thứ 2 là 0
            weekTasks[dayIndex].push({
              name: task.name,
              description: task.description,
              startTime: task.startTime,
              endTime: task.endTime,
            });
          }

          // Tính ngày lặp tiếp theo
          if (task.repeatOption === "Hàng ngày") {
            currentDate.setDate(currentDate.getDate() + 1);
          } else if (task.repeatOption === "Tùy chỉnh") {
            const { repeatEvery, repeatUnit, repeatDays } = task.customRepeat;
            if (repeatUnit === "ngày") {
              currentDate.setDate(currentDate.getDate() + repeatEvery);
            } else if (repeatUnit === "tuần") {
              const nextDay = currentDate.getDay() + repeatEvery * 7;
              const nextDate = new Date(currentDate);
              nextDate.setDate(nextDate.getDate() + nextDay);
              const dayOfWeek = nextDate.getDay();
              if (repeatDays.includes(["CN", "T2", "T3", "T4", "T5", "T6", "T7"][dayOfWeek])) {
                currentDate.setDate(nextDate.getDate());
              }
            }
          } else {
            break; // Không lặp lại
          }
        }
      });
    });

  return weekTasks;
};



const TimeTable = ({ works }) => {
  console.log("Works received in TimeTable:", works);

  const [showTaskList, setShowTaskList] = useState(false);
  const [weekTasks, setWeekTasks] = useState(Array.from({ length: 7 }, () => []));


  const days = useMemo(
    () => ["Thứ 2", "Thứ 3", "Thứ 4", "Thứ 5", "Thứ 6", "Thứ 7", "Chủ Nhật"],
    []
  );

  useEffect(() => {
    const tasks = calculateTasksForWeek(works);
    setWeekTasks(tasks);
  }, [works]);

  useEffect(() => {
    console.log("Week tasks:", weekTasks);
  }, [weekTasks]);
  

  const toggleTaskList = () => {
    setShowTaskList((prev) => !prev);
  };

  return (
    <div className="main-form" data-aos="zoom-in">
      <header className="search-bar">
        <h1>TimeTable</h1>
        <button className="view-tasklist-btn" onClick={toggleTaskList}>
          View Task List
        </button>
      </header>

      {showTaskList && (
        <div className="tasklist-overlay">
          <div className="tasklist-container">
            <button
              className="close-tasklist-btn"
              onClick={() => setShowTaskList(false)}
            >
              Close
            </button>
            <h2>Tasks for the Week</h2>
            {weekTasks.map((tasks, dayIndex) => (
  <div key={dayIndex} className="tasklist-day">
    <h3>
      {days[dayIndex]}{" "}
      {new Date(
        new Date().setDate(
          new Date().getDate() - new Date().getDay() + 1 + dayIndex
        )
      ).toLocaleDateString("vi-VN")}
    </h3>
    {tasks.length > 0 ? (
      <ul>
        {tasks.map((task, index) => (
          <li key={index}>
            <strong>{task.name}</strong>: {task.startTime} - {task.endTime}
            <p>{task.description}</p>
          </li>
        ))}
      </ul>
    ) : (
      <p>No tasks for this day.</p>
    )}
  </div>
))}


          </div>
        </div>
      )}

      <div className="timetable-container">
        <div className="header-row">
          <div className="header-cell time-column">Thời gian</div>
          {days.map((day, index) => (
            <div key={index} className="header-cell">
              {day}
            </div>
          ))}
        </div>

        <div className="body-container">
          {[...Array(8)].map((_, dayIndex) => (
            <div key={dayIndex} className={`column ${dayIndex === 0 ? "time-column" : ""}`}>
              {dayIndex === 0 && <div className="time-slot">Time Slots</div>}
              {dayIndex > 0 &&
                weekTasks[dayIndex - 1].map((task, index) => (
                  <div key={index} className="time-slot">
                    <div className="task-name">{task.name}</div>
                    <div>
                      {task.startTime} - {task.endTime}
                    </div>
                  </div>
                ))}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TimeTable;
