import React, { useState, useEffect } from "react";
import Aos from "aos";
import "./styles/dashboard.css";
import { AiFillDelete, AiFillEdit, AiOutlinePlus } from "react-icons/ai";

const Dashboard = ({
  works,
  setWorks,
  completedWorks,
  setCompletedWorks,
  recycledWorks,
  setRecycledWorks,
  taskCompletionStatus,
  setTaskCompletionStatus,
}) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isTaskModalOpen, setIsTaskModalOpen] = useState(false);

  const [formData, setFormData] = useState({
    id: null,
    name: "",
    type: "",
    startDate: "",
    endDate: "",
    describe: "",
    tag: "",
  });

  const [taskFormData, setTaskFormData] = useState({
    workId: null,
    taskName: "",
    taskDescription: "",
    startTime: "",
    endTime: "",
    repeatOption: "Không lặp lại",
    customRepeat: {
      repeatEvery: 1,
      repeatUnit: "ngày",
      repeatDays: [],
    },
  });

  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    Aos.init({ duration: 1000 });
  }, []);

  useEffect(() => {
    if (isModalOpen || isTaskModalOpen) {
      document.body.classList.add("disable-hover");
    } else {
      document.body.classList.remove("disable-hover");
    }
  }, [isModalOpen, isTaskModalOpen]);

  const handleAddNewWork = () => {
    setIsModalOpen(true);
    setEditMode(false);
    setFormData({
      id: null,
      name: "",
      type: "",
      startDate: "",
      endDate: "",
      describe: "",
      tag: "",
    });
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleTaskFormChange = (e) => {
    const { name, value } = e.target;

    setTaskFormData((prev) => {
      const updated = { ...prev, [name]: value };

      if (name === "customRepeat.repeatUnit" && value === "ngày") {
        updated.customRepeat.repeatDays = [];
      }

      return updated;
    });
  };

  const handleAddTask = (workId) => {
    const work = Object.values(works).flat().find((work) => work.id === workId);
    setIsTaskModalOpen(true);
    setTaskFormData({ ...taskFormData, workId, workName: work ? work.name : "" });
  };

  const handleDone = () => {
    const { name, type, startDate, endDate } = formData;

    if (!name || !type || !startDate || !endDate || !formData.tag) {
      alert("Please fill in all fields.");
      return;
    }

    setWorks((prevWorks) => {
      const updatedWorks = { ...prevWorks };

      if (editMode) {
        Object.keys(updatedWorks).forEach((type) => {
          updatedWorks[type] = updatedWorks[type].filter(
            (work) => work.id !== formData.id
          );
        });
      }

      updatedWorks[type] = [
        ...updatedWorks[type],
        {
          ...formData,
          id: formData.id || Date.now(),
          subWorks: formData.subWorks || [],
        },
      ];

      return updatedWorks;
    });

    setIsModalOpen(false);
  };

  const handleDoneTask = () => {
    const { startTime, endTime, taskName, taskDescription } = taskFormData;

    if (!taskName || !startTime || !endTime) {
      alert("Please fill in all fields.");
      return;
    }

    let [startHour, startMinute] = startTime.split(":").map(Number);
    let [endHour, endMinute] = endTime.split(":").map(Number);

    if (endHour === 0 && endMinute === 0) {
      endHour = 24;
    }

    const startTotalMinutes = startHour * 60 + startMinute;
    const endTotalMinutes = endHour * 60 + endMinute;

    if (endTotalMinutes < startTotalMinutes) {
      alert("End time must be equal to or after start time.");
      return;
    }

    const updatedCustomRepeat = { ...taskFormData.customRepeat };
    if (updatedCustomRepeat.repeatUnit === "ngày") {
      delete updatedCustomRepeat.repeatDays;
    }

    setWorks((prevWorks) => {
      const updatedWorks = { ...prevWorks };
      const workType = Object.keys(updatedWorks).find((type) =>
        updatedWorks[type].some((work) => work.id === taskFormData.workId)
      );

      if (!workType) {
        alert("Work not found. Please try again.");
        return prevWorks;
      }

      const workIndex = updatedWorks[workType].findIndex(
        (work) => work.id === taskFormData.workId
      );

      if (workIndex === -1) {
        alert("Work not found. Please try again.");
        return prevWorks;
      }

      const work = updatedWorks[workType][workIndex];
      if (!work.subWorks) {
        work.subWorks = [];
      }

      const isDuplicateTask = work.subWorks.some(
        (subWork) => subWork.name === taskFormData.taskName
      );

      if (!isDuplicateTask) {
        work.subWorks.push({
          name: taskFormData.taskName,
          description: taskFormData.taskDescription,
          startTime: taskFormData.startTime,
          endTime: taskFormData.endTime,
          repeatOption: taskFormData.repeatOption,
          customRepeat: taskFormData.customRepeat,
        });
      }

      return updatedWorks;
    });

    setTaskFormData({
      workId: null,
      taskName: "",
      taskDescription: "",
      startTime: "",
      endTime: "",
      repeatOption: "Không lặp lại",
      customRepeat: {
        repeatEvery: 1,
        repeatUnit: "ngày",
        repeatDays: [],
      },
    });

    setIsTaskModalOpen(false);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
    setIsTaskModalOpen(false);
  };

  const handleDeleteTask = (type, id) => {
    setWorks((prevWorks) => ({
      ...prevWorks,
      [type]: prevWorks[type].filter((work) => work.id !== id),
    }));
    setRecycledWorks((prevRecycledWorks) => [
      ...prevRecycledWorks,
      works[type].find((work) => work.id === id),
    ]);
  };

  const handleEditTask = (work) => {
    setEditMode(true);
    setFormData(work);
    setIsModalOpen(true);
  };

  const formatDate = (dateString) => {
    if (!dateString) return "";
    const [year, month, day] = dateString.split("-");
    return `${day}/${month}/${year}`;
  };

  return (
    <div className="home-body-container" data-aos="zoom-in">
      <header id="dash-header" className="search-bar">
        <h1>Dashboard</h1>
      </header>
      <button className="add-work-btn" onClick={handleAddNewWork}>
        Add new work
      </button>

      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h2>{editMode ? "Edit Work" : "Add New Work"}</h2>
            <input
              type="text"
              placeholder="Enter work name"
              name="name"
              value={formData.name}
              onChange={handleFormChange}
            />
            <div className="form-row">
              <select
                name="type"
                value={formData.type}
                onChange={handleFormChange}
              >
                <option value="">Select type</option>
                <option value="doNow">Do now</option>
                <option value="schedule">Schedule</option>
                <option value="delegate">Delegate</option>
                <option value="drop">Drop</option>
              </select>
              <select
                name="tag"
                value={formData.tag}
                onChange={handleFormChange}
              >
                <option value="">Select tag</option>
                <option value="Học tập">Học tập</option>
                <option value="Làm việc">Làm việc</option>
                <option value="Nghỉ ngơi">Nghỉ ngơi</option>
                <option value="Giải trí">Giải trí</option>
              </select>
            </div>
            <div className="form-row">
              <input
                type="date"
                name="startDate"
                value={formData.startDate}
                onChange={handleFormChange}
              />
              <input
                type="date"
                name="endDate"
                value={formData.endDate}
                onChange={handleFormChange}
              />
            </div>
            <textarea
              placeholder="Describe work"
              name="describe"
              value={formData.describe}
              onChange={handleFormChange}
              rows="4"
            ></textarea>
            <div className="modal-buttons">
              <button onClick={handleDone}>Done</button>
              <button onClick={handleCancel}>Cancel</button>
            </div>
          </div>
        </div>
      )}

      <main className="body-content">
        {["doNow", "schedule", "delegate", "drop"].map((type) => (
          <div key={type} className="work-box">
            <h2>{type.charAt(0).toUpperCase() + type.slice(1)}</h2>
            <div className="work-list">
              {works[type].map((work) => (
                <div key={work.id} className="work">
                  <div className="work-info">
                    <input
                      type="checkbox"
                      onChange={() =>
                        setTaskCompletionStatus((prev) => ({
                          ...prev,
                          [work.id]: !prev[work.id],
                        }))
                      }
                    />
                    <span>{work.name}</span>
                    <p>{`${formatDate(work.startDate)} - ${formatDate(
                      work.endDate
                    )}`}</p>
                  </div>
                  <div className="work-buttons">
                    <AiOutlinePlus
                      className="add-task-icon"
                      onClick={() => handleAddTask(work.id)}
                    />
                    <AiFillEdit
                      className="edit-icon"
                      onClick={() => handleEditTask(work)}
                    />
                    <AiFillDelete
                      className="delete-icon"
                      onClick={() => handleDeleteTask(type, work.id)}
                    />
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </main>
    </div>
  );
};

export default Dashboard;
