import React, { useEffect, useState, useMemo } from "react";
import "aos/dist/aos.css";
import "./styles/timetable.css";

const calculateTasksForWeek = (works) => {
  const currentDate = new Date();
  const weekStart = new Date(currentDate);
  weekStart.setDate(currentDate.getDate() - currentDate.getDay() + 1); // Thứ 2
  const weekEnd = new Date(weekStart);
  weekEnd.setDate(weekStart.getDate() + 6); // Chủ Nhật


  const tasksForWeek = [];

  works.forEach((work) => {
    const workStartDate = new Date(work.startDate);
    const workEndDate = new Date(work.endDate);

    work.subWorks.forEach((task) => {
      const taskStartDate = task.customRepeat?.startDate
        ? new Date(task.customRepeat.startDate)
        : workStartDate;

      let currentDate = new Date(taskStartDate);
      while (currentDate <= workEndDate) {
        if (
          currentDate >= weekStart &&
          currentDate <= weekEnd &&
          task.repeatOption === "Không lặp lại"
        ) {
          if (currentDate.toDateString() === taskStartDate.toDateString()) {
            tasksForWeek.push({
              day: currentDate.getDay(),
              ...task,
            });
          }
        } else if (task.repeatOption === "Hàng ngày") {
          if (currentDate >= weekStart && currentDate <= weekEnd) {
            tasksForWeek.push({
              day: currentDate.getDay(),
              ...task,
            });
          }
        } else if (task.repeatOption === "Tùy chỉnh") {
          const repeatUnit = task.customRepeat?.repeatUnit || "ngày";
          const repeatEvery = task.customRepeat?.repeatEvery || 1;

          if (repeatUnit === "ngày") {
            if (currentDate >= weekStart && currentDate <= weekEnd) {
              tasksForWeek.push({
                day: currentDate.getDay(),
                ...task,
              });
            }
            currentDate.setDate(currentDate.getDate() + repeatEvery);
          } else if (repeatUnit === "tuần") {
            const repeatDays = task.customRepeat?.repeatDays || [];
            const currentDayOfWeek = currentDate.getDay();

            if (
              repeatDays.includes(
                ["CN", "T2", "T3", "T4", "T5", "T6", "T7"][currentDayOfWeek]
              ) &&
              currentDate >= weekStart &&
              currentDate <= weekEnd
            ) {
              tasksForWeek.push({
                day: currentDayOfWeek,
                ...task,
              });
            }
            currentDate.setDate(currentDate.getDate() + 7 * repeatEvery);
          }
        }
        currentDate.setDate(currentDate.getDate() + 1);
      }
    });
  });

  return tasksForWeek.sort((a, b) => new Date(a.startTime) - new Date(b.startTime));
};

const TimeTable = ({ works }) => {
  const [showTaskList, setShowTaskList] = useState(false);
  const [tasksForWeek, setTasksForWeek] = useState([]);

  const days = useMemo(
    () => ["Thứ 2", "Thứ 3", "Thứ 4", "Thứ 5", "Thứ 6", "Thứ 7", "Chủ Nhật"],
    []
  );
  const hours = useMemo(() => Array.from({ length: 24 }, (_, i) => `${i}:00 - ${i + 1}:00`), []);


  useEffect(() => {
    const filteredTasks = calculateTasksForWeek(works);
    setTasksForWeek(filteredTasks);
  }, [works]);

  const toggleTaskList = () => {
    setShowTaskList((prev) => !prev);
  };

  return (
    <div className="main-form" data-aos="zoom-in">
      <header className="search-bar">
        <h1>TimeTable</h1>
        <button className="view-tasklist-btn" onClick={toggleTaskList}>
          View Task List
        </button>
      </header>

      {showTaskList && (
        <div className="tasklist-overlay">
          <div className="tasklist-container">
            <button className="close-btn" onClick={toggleTaskList}>
              Close
            </button>
            <h2>Weekly Tasks</h2>
            <div className="tasklist">
              {days.map((day, index) => (
                <div key={index} className="day-tasks">
                  <h3>
                    {day} (
                    {new Date().toLocaleDateString("vi-VN", {
                      weekday: "long",
                      day: "numeric",
                      month: "numeric",
                      year: "numeric",
                    })}
                    )
                  </h3>
                  {tasksForWeek
                    .filter((task) => task.day === index + 1)
                    .map((task, idx) => (
                      <div key={idx} className="task-item">
                        <p>{task.name}</p>
                        <p>{`${task.startTime} - ${task.endTime}`}</p>
                      </div>
                    ))}
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      <div className="timetable-container">
        <div className="header-row">
          <div className="header-cell time-column">Thời gian</div>
          {days.map((day, index) => (
            <div key={index} className="header-cell">
              {day}
            </div>
          ))}
        </div>
        <div className="body-container">
        {[...Array(8)].map((_, dayIndex) => (
            <div key={dayIndex} className={`column ${dayIndex === 0 ? "time-column" : ""}`}>
              {hours.map((hour, hourIndex) => (
                <div key={hourIndex} className="time-slot">
                  {dayIndex === 0 ? hour : null}
                  <div className="atomic-slots">
                    {[...Array(4)].map((_, atomicIndex) => (
                      <div key={atomicIndex} className="atomic-slot"></div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TimeTable;
