import React, { useEffect, useState } from "react";
import { AiFillDelete } from "react-icons/ai";
import { BiSearchAlt2 } from "react-icons/bi";
import Aos from "aos";
import "aos/dist/aos.css";
import "./styles/focus.css";

const Focus = () => {
  const [newItem, setNewItem] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [todo, setTodo] = useState([
    { todoId: 1, title: "Sample Task 1", status: false },
    { todoId: 2, title: "Sample Task 2", status: true },
  ]);

  useEffect(() => {
    Aos.init({ duration: 1000 });
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newItem === "") {
      alert("Please type something");
      return;
    }
    const newTodoItem = {
      todoId: Date.now(),
      title: newItem,
      status: false,
    };
    setTodo((current) => [...current, newTodoItem]);
    setNewItem("");
  };

  const tickTodo = (todoId, status) => {
    setTodo((current) =>
      current.map((todo) =>
        todo.todoId === todoId ? { ...todo, status } : todo
      )
    );
  };

  const deleteTodo = (todoId) => {
    setTodo((current) => current.filter((todo) => todo.todoId !== todoId));
  };

  const filteredItems = todo.filter((eachItem) =>
    eachItem.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="main-form" data-aos="zoom-in">
    
      
    </div>
  );
};

export default Focus;
