import React, { useEffect, useState, useCallback, useRef } from "react";
import "./styles/focus.css";

const Focus = ({ works = {}, toast }) => {
  const [mode, setMode] = useState("auto");
  const [selectedWork, setSelectedWork] = useState(null);
  const [selectedTask, setSelectedTask] = useState(null);
  const [currentTaskManual, setCurrentTaskManual] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [tempWork, setTempWork] = useState(null);
  const [tempTask, setTempTask] = useState(null);
  const [timeLeft, setTimeLeft] = useState(1500); // Initial Pomodoro time
  const [isRunning, setIsRunning] = useState(false);
  const [cycle, setCycle] = useState(0);
  const [isFocusPhase, setIsFocusPhase] = useState(true);
  const [tasksForToday, setTasksForToday] = useState([]);
  const [currentTime, setCurrentTime] = useState(new Date());
  const timelineRef = useRef(null);
  const [selectedTaskName, setSelectedTaskName] = useState(null);
  const [noteContent, setNoteContent] = useState("");
  const [isFreeMode, setIsFreeMode] = useState(false);
  const [freeModeTime, setFreeModeTime] = useState(0);
  const [currentMode, setCurrentMode] = useState("pomodoro");
  const [countdownTimeTask, setCountdownTimeTask] = useState(0);
  const [isTaskSelected, setIsTaskSelected] = useState(false);
  const [todayDataTimeCount, setTodayDataTimeCount] = useState([]);
  const [isTodayDataVisible, setIsTodayDataVisible] = useState(false);
  const [lastTaskStartTime, setLastTaskStartTime] = useState(null);
  const intervalRef = useRef(null);

  // Toggle between Free mode and Pomodoro/structured mode
  const toggleFreeMode = useCallback(() => {
    setIsFreeMode(!isFreeMode);
    if (!isFreeMode) {
      // Entering free mode
      setFreeModeTime(0);
      setIsRunning(false);
    } else {
      // Exiting free mode, return to Pomodoro
      setTimeLeft(isFocusPhase ? 1500 : timeLeft);
    }
  }, [isFreeMode, isFocusPhase, timeLeft]);

  // Handle the completion of a Pomodoro cycle
  const handlePomodoroComplete = useCallback(() => {
    setIsRunning(false);
    setIsFocusPhase((prev) => !prev);
    if (isFocusPhase) {
      setCycle((prev) => prev + 1);
      setTimeLeft((cycle + 1) % 4 === 0 ? 900 : 300);
      toast.info((cycle + 1) % 4 === 0 ? "Long Break! Take a 15-minute rest." : "Short Break! Take a 5-minute rest.");
    } else {
      setTimeLeft(1500);
      toast.success("Back to Focus! 25 minutes.");
    }
  }, [cycle, isFocusPhase, toast]);

  // Start or pause the current selected task
  const handleStartPause = () => {
    if (isRunning) {
      clearInterval(intervalRef.current);
      if (selectedTaskName && lastTaskStartTime) {
        const elapsedTime = Math.floor((Date.now() - lastTaskStartTime) / 1000);
        updateTaskTimeCount(selectedTaskName, elapsedTime);
      }
      setIsRunning(false);
      setLastTaskStartTime(null);
    } else {
      setLastTaskStartTime(Date.now());
      intervalRef.current = setInterval(() => {
        updateTaskTimeCount(selectedTaskName, 1);
      }, 1000);
      setIsRunning(true);
    }
  };

  // Update the time count for a specific task
  const updateTaskTimeCount = (taskName, seconds) => {
    setTodayDataTimeCount((prev) => {
      const updated = [...prev];
      const taskIndex = updated.findIndex((task) => task.name === taskName);
      if (taskIndex >= 0) {
        updated[taskIndex].realTimeTaskCount += seconds;
      }
      return updated;
    });
  };

  // React to changes in timeLeft, particularly to handle zero
  useEffect(() => {
    if (timeLeft === 0) {
      handlePomodoroComplete();
    }
  }, [timeLeft, handlePomodoroComplete]);

  // Effect to handle real-time updates
  useEffect(() => {
    if (isRunning && selectedTaskName) {
      intervalRef.current = setInterval(() => {
        setTodayDataTimeCount((prev) => {
          const updated = [...prev];
          const taskIndex = updated.findIndex((task) => task.name === selectedTaskName);
          if (taskIndex >= 0) {
            updated[taskIndex].realTimeTaskCount += 1;
          }
          return updated;
        });
      }, 1000);
    } else {
      clearInterval(intervalRef.current);
    }
    return () => clearInterval(intervalRef.current);
  }, [isRunning, selectedTaskName]);

  // Calculate tasks for today from works
  useEffect(() => {
    const calculateTasksForToday = () => {
      const today = new Date();
      const dailyTasks = Object.values(works).flat().reduce((acc, work) => {
        work.subWorks.forEach((task) => {
          if (new Date(work.startDate) <= today && today <= new Date(work.endDate)) {
            const taskTimeSlot = calculateTimeSlot(task.startTime, task.endTime);
            acc.push({
              name: task.name,
              date: today.toDateString(),
              realTimeTaskCount: 0,
              timeSlot: taskTimeSlot,
              status: "pending-today"
            });
          }
        });
        return acc;
      }, []);
      setTasksForToday(dailyTasks);
    };

    if (mode === "auto") {
      calculateTasksForToday();
    }
  }, [mode, works]);

  // Utility functions
  const calculateTimeSlot = (startTime, endTime) => {
    const [startHour, startMinute] = startTime.split(":").map(Number);
    const [endHour, endMinute] = endTime.split(":").map(Number);
    const start = new Date().setHours(startHour, startMinute, 0, 0);
    const end = new Date().setHours(endHour, endMinute, 0, 0);
    const duration = (end - start) / 1000;
    return formatTime(duration);
  };

  const formatTime = (seconds) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;
    return `${hours.toString().padStart(2, "0")}:${minutes.toString().padStart(2, "0")}:${secs.toString().padStart(2, "0")}`;
  };

  return (
    <div className="main-form">
      <header className="search-bar">
        <h1>Focus</h1>
      </header>

      <div className="container-row">
      <div className="today-data-container">
        <button
          className="today-data-toggle"
          onClick={() => setIsTodayDataVisible((prev) => !prev)}
        >
          TODAY-DataTimeCount ‚ñº
        </button>
        {isTodayDataVisible && (
          <div className="today-data-content">
            <p>Today: {new Date().toDateString()}</p>
            <ul>
              {todayDataTimeCount.map((task, index) => (
                <li key={index}>
                  {task.name}{" "}
                  <strong>
                    {formatTime(task.realTimeTaskCount)} / {task.timeSlot}
                  </strong>{" "}
                  <span>{task.status}</span>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>


  <div className="countdown-task">
    {isTaskSelected ? (
      <p>{formatTime(countdownTimeTask)}</p>
    ) : (
      <p>Select task!</p>
    )}
  </div>

  <div className="focus-modes">
    <button
      className={`focus-mode ${mode === "auto" ? "active" : ""}`}
      onClick={() => setMode("auto")}
    >
      T·ª± ƒë·ªông
    </button>
    <button
      className={`focus-mode ${mode === "manual" ? "active" : ""}`}
      onClick={() => {
        setMode("manual");
        openManualSelectModal();
      }}
    >
      T·ª± ch·ªçn
    </button>
    <button
      className={`focus-mode ${mode === "free" ? "active" : ""}`}
      onClick={() => setMode("free")}
    >
      T·ª± do
    </button>
  </div>
</div>


      <div className="focus-content">
        <div className="timeline-container" ref={timelineRef}>
          <div className="timeline">
            <div
              className="current-time-line"
              style={{
                "--time-position": `${currentTime.getHours() * 60 + currentTime.getMinutes()}px`,
                top: `${currentTime.getHours() * 60 + currentTime.getMinutes() + 6.5}px`,
              }}
            ></div>
            {Array.from({ length: 25 }).map((_, hour) => (
              <div key={hour} className="hour-block" style={{ top: `${hour * 60}px` }}>
                <div className="hour-marker">
                  {hour < 10 ? `0${hour}:00` : `${hour}:00`}
                </div>
                <div className="hour-divider"></div>
              </div>
            ))}
            {tasksForToday.map((task) => renderTaskOnTimeline(task))}
          </div>
        </div>
                    {mode === "auto" && (
          <div className="task-list">
          <h2>Today Task</h2>
          <p>
            T·ªïng s·ªë task: <strong>{tasksForToday.length}</strong>
          </p>
          {tasksForToday.length > 0 ? (
            <table className="task-table">
              <thead>
                <tr>
                  <th>Task</th>
                  <th>Work</th>
                  <th>Time</th>
                  <th>TimeSlot</th>
                </tr>
              </thead>
              <tbody>
                {tasksForToday.map((task, index) => {
                  const timeSlot = calculateTimeSlot(task.startTime, task.endTime); // T√≠nh TimeSlot
                  return (
                    <tr
                      key={index}
                      className={`task-list-item ${
                        selectedTaskName === task.name ? "selected" : ""
                      }`}
                      onClick={() => handleSelectTaskBlock(task.name)}
                    >
                      <td>{task.name}</td>
                      <td>{task.workName || "N/A"}</td>
                      <td>
                        {task.startTime.toLocaleTimeString([], {
                          hour: "2-digit",
                          minute: "2-digit",
                          hour12: false,
                        })}{" "}
                        -{" "}
                        {task.endTime.toLocaleTimeString([], {
                          hour: "2-digit",
                          minute: "2-digit",
                          hour12: false,
                        })}
                      </td>
                      <td>{timeSlot}</td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          ) : (
            <p>Empty Task Today</p>
          )}
        </div>
        
        )}
        <div className="note-container">
          <div className="note-header">
            <button
              className="note-button"
              onClick={() => alert("Daily form is not implemented yet.")}
            >
              Daily
            </button>
            <button className="note-button" onClick={() => setNoteContent("")}>
              Clear
            </button>
          </div>
          <div className="note-body">
            <label htmlFor="note-input" className="note-label">Note</label>
            <textarea
              id="note-input"
              className="note-input"
              value={noteContent}
              onChange={(e) => setNoteContent(e.target.value)}
              placeholder="Write your notes here..."
            ></textarea>
          </div>
        </div>
      </div>


      {mode === "manual" && currentTaskManual && (
        <div className="task-select">
          <h2>Task hi·ªán t·∫°i</h2>
          <p>
            <strong>Work:</strong> {currentTaskManual.workName}
          </p>
          <p>
            <strong>Task:</strong> {currentTaskManual.name}
          </p>
          <p>
            <strong>Th·ªùi gian:</strong>{" "}
            {currentTaskManual.startTime.toLocaleTimeString([], {
              hour: "2-digit",
              minute: "2-digit",
              hour12: false,
            })}{" "}
            -{" "}
            {currentTaskManual.endTime.toLocaleTimeString([], {
              hour: "2-digit",
              minute: "2-digit",
              hour12: false,
            })}
          </p>

          <p>
            <strong>Ng√†y Work:</strong> {currentTaskManual.workStartDate} -{" "}
            {currentTaskManual.workEndDate}
          </p>
          <p>
            <strong>Timeslot:</strong>{" "}
            {calculateTimeSlot(
              new Date(currentTaskManual.startTime),
              new Date(currentTaskManual.endTime)
            )}
          </p>
        </div>
      )}


<div className="pomodoro-timer" style={{ backgroundColor: getPomodoroBackground() }}>
  <div className="pomodoro-header">
    <span className="swap-icon" onClick={toggleFreeMode}>
      üîÑ
    </span>
    <div className="pomodoro-modes">
  {!isFreeMode ? (
    <>
      <span
        className={`pomodoro-mode ${currentMode === "pomodoro" ? "active" : ""}`}
        onClick={() => {
          setCurrentMode("pomodoro");
          handleModeChange("pomodoro");
        }}
      >
        Pomodoro
      </span>
      <span
        className={`pomodoro-mode ${currentMode === "short-break" ? "active" : ""}`}
        onClick={() => {
          setCurrentMode("short-break");
          handleModeChange("short-break");
        }}
      >
        Short-break
      </span>
      <span
        className={`pomodoro-mode ${currentMode === "long-break" ? "active" : ""}`}
        onClick={() => {
          setCurrentMode("long-break");
          handleModeChange("long-break");
        }}
      >
        Long-break
      </span>
    </>
  ) : (
    <span
  className={`pomodoro-mode ${isFreeMode ? "active" : ""}`}
  onClick={() => {
    if (!isFreeMode) {
      toggleFreeMode();
    } else {
      setFreeModeTime(0);
    }
  }}
>
  Stopwatch
</span>

  )}
</div>



  </div>
  <p>{formatTime(isFreeMode ? freeModeTime : timeLeft)}</p> {/* Hi·ªÉn th·ªã th·ªùi gian */}
  <div className="timer-controls">
  <button
  onClick={isTaskSelected ? handleStartPause : () => toast.error("Please choose a task")}
  disabled={!isTaskSelected}
>
  {isRunning ? "T·∫°m d·ª´ng" : "B·∫Øt ƒë·∫ßu"}
</button>

    {isFreeMode && (
      <button onClick={() => setFreeModeTime(0)}>K·∫øt th√∫c</button>
    )}
    {!isFreeMode && <button onClick={handleSkip}>B·ªè qua</button>}
  </div>
</div>

      {isModalOpen && (
        <div className="modal">
          <div className="modal-task-content">
            <div className="modal-left">
              <h2>Danh s√°ch Work</h2>
              {Object.values(works).flat().map((work) => (
                <div
                  key={work.id}
                  className={`work-item ${
                    tempWork?.id === work.id ? "active" : ""
                  }`}
                  onClick={() => handleManualSelectWork(work)}
                >
                  <h4>{work.name}</h4>
                  <p>
                    Ng√†y: {work.startDate} - {work.endDate}
                  </p>
                </div>
              ))}
            </div>
            <div className="divider"></div>
            <div className="modal-right">
              <div className="modal-buttons">
                <button className="focus-button" onClick={handleManualSelectionDone}>
                  Done
                </button>
                <button className="modal-close" onClick={closeModal}>
                  &times;
                </button>
              </div>
              <h2>Danh s√°ch Task</h2>
              {tempWork && tempWork.subWorks ? (
                <>
                  {tempWork.subWorks.map((task, index) => (
                    <div
                      key={index}
                      className={`task-item ${
                        tempTask?.name === task.name ? "active" : ""
                      }`}
                      onClick={() => handleManualSelectTask(task)}
                    >
                      <h4>{task.name}</h4>
                      <p>
                        Th·ªùi gian: {task.startTime} - {task.endTime}
                      </p>
                    </div>
                  ))}
                </>
              ) : (
                <p>Ch·ªçn m·ªôt Work ƒë·ªÉ xem Task.</p>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Focus;
