import React, { useEffect } from "react";
import "aos/dist/aos.css";
import "./styles/timetable.css";

const updateHeadersWithDate = (days) => {
  const currentDate = new Date();
  const todayIndex = (currentDate.getDay() + 6) % 7; // Điều chỉnh chủ nhật thành 6, thứ 2 thành 0
  const getDateForDay = (dayIndex) => {
    const date = new Date(currentDate);
    date.setDate(currentDate.getDate() - todayIndex + dayIndex);
    return `${date.getDate()}/${date.getMonth() + 1}`;
  };

  const headerCells = document.querySelectorAll(".header-cell");
  headerCells.forEach((cell, index) => {
    if (index > 0) {
      const dateText = getDateForDay(index - 1);
      cell.innerHTML = `${days[index - 1]}<br><span class="date-text">${dateText}</span>`;
      cell.classList.toggle("today-highlight", index - 1 === todayIndex);
    }
  });
};

const TimeTable = () => {
  const days = ["Thứ 2", "Thứ 3", "Thứ 4", "Thứ 5", "Thứ 6", "Thứ 7", "Chủ Nhật"];
  const hours = Array.from({ length: 24 }, (_, i) => `${i}:00 - ${i + 1}:00`);

  useEffect(() => {
    updateHeadersWithDate(days);
    const intervalId = setInterval(() => updateHeadersWithDate(days), 60000); // Kiểm tra thay đổi ngày mỗi phút
    return () => clearInterval(intervalId); // Dọn dẹp khi component unmount
  }, [days]); // Thêm `days` vào dependency array

  return (
    <div className="main-form" data-aos="zoom-in">
      <header className="search-bar">
        <h1>TimeTable</h1>
      </header>

      <div className="timetable-container">
        <div className="header-row">
          <div className="header-cell time-column">Thời gian</div>
          {days.map((day, index) => (
            <div key={index} className="header-cell">
              {day}
            </div>
          ))}
        </div>

        <div className="body-container">
          {[...Array(8)].map((_, dayIndex) => (
            <div key={dayIndex} className={`column ${dayIndex === 0 ? "time-column" : ""}`}>
              {hours.map((hour, hourIndex) => (
                <div key={hourIndex} className="time-slot">
                  {dayIndex === 0 ? hour : null}
                  <div className="atomic-slots">
                    {[...Array(4)].map((_, atomicIndex) => (
                      <div key={atomicIndex} className="atomic-slot"></div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TimeTable;
