import React, { useState, useEffect } from "react";
import Aos from "aos";
import "./styles/dashboard.css";
import { AiFillDelete, AiFillEdit, AiOutlinePlus } from "react-icons/ai";

const Dashboard = ({ works, setWorks, completedWorks, setCompletedWorks }) => {
  // Sử dụng props để quản lý works và completedWorks
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isTaskModalOpen, setIsTaskModalOpen] = useState(false);
  const [formData, setFormData] = useState({
    id: null,
    name: "",
    type: "",
    startDate: "",
    endDate: "",
    describe: "",
    tag: "",
  });

  const [taskFormData, setTaskFormData] = useState({
    workId: null,
    taskName: "",
    taskDescription: "",
    startTime: "",
    endTime: "",
    repeatOption: "Không lặp lại",
    customRepeat: {
      repeatEvery: 1,
      repeatUnit: "ngày",
      repeatDays: [],
    },
  });

  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    Aos.init({ duration: 1000 });
  }, []);

  useEffect(() => {
    localStorage.setItem("works", JSON.stringify(works));
  }, [works]);

  const handleAddNewWork = () => {
    setIsModalOpen(true);
    setEditMode(false);
    setFormData({
      id: null,
      name: "",
      type: "",
      startDate: "",
      endDate: "",
      describe: "",
      tag: "",
    });
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleTaskFormChange = (e) => {
    const { name, value } = e.target;
    setTaskFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleAddTask = (workId) => {
    const work = Object.values(works).flat().find((task) => task.id === workId);
    setIsTaskModalOpen(true);
    setTaskFormData({ ...taskFormData, workId, workName: work ? work.name : "" });
  };

  const handleDone = () => {
    const { name, type, startDate, endDate } = formData;

    if (!name || !type || !startDate || !endDate || !formData.tag) {
      alert("Please fill in all fields.");
      return;
    }

    setWorks((prevWorks) => {
      const updatedWorks = { ...prevWorks };

      if (editMode) {
        Object.keys(updatedWorks).forEach((type) => {
          updatedWorks[type] = updatedWorks[type].filter(
            (work) => work.id !== formData.id
          );
        });
      }

      updatedWorks[type] = [
        ...updatedWorks[type],
        {
          ...formData,
          id: formData.id || Date.now(),
          subTasks: formData.subTasks || [], // Khởi tạo subTasks
        },
      ];

      return updatedWorks;
    });

    setIsModalOpen(false);
  };

  const handleDoneTask = () => {
    if (!taskFormData.taskName || !taskFormData.startTime || !taskFormData.endTime) {
      alert("Please fill in all fields.");
      return;
    }

    setWorks((prevWorks) => {
      const updatedWorks = { ...prevWorks };
      const taskType = Object.keys(updatedWorks).find((type) =>
        updatedWorks[type].some((task) => task.id === taskFormData.workId)
      );

      if (!taskType) {
        alert("Work not found. Please try again.");
        return prevWorks;
      }

      const workIndex = updatedWorks[taskType].findIndex(
        (work) => work.id === taskFormData.workId
      );

      if (workIndex === -1) {
        alert("Work not found. Please try again.");
        return prevWorks;
      }

      // Đảm bảo subTasks tồn tại và chỉ thêm task mới một lần
      const work = updatedWorks[taskType][workIndex];
      if (!work.subTasks) {
        work.subTasks = [];
      }

      const isDuplicateTask = work.subTasks.some(
        (subTask) => subTask.name === taskFormData.taskName
      );

      if (!isDuplicateTask) {
        work.subTasks.push({
          name: taskFormData.taskName,
          description: taskFormData.taskDescription,
          startTime: taskFormData.startTime,
          endTime: taskFormData.endTime,
          repeatOption: taskFormData.repeatOption,
          customRepeat: taskFormData.customRepeat,
        });
      }

      return updatedWorks;
    });

    setTaskFormData({
      workId: null,
      taskName: "",
      taskDescription: "",
      startTime: "",
      endTime: "",
      repeatOption: "Không lặp lại",
      customRepeat: {
        repeatEvery: 1,
        repeatUnit: "ngày",
        repeatDays: [],
      },
    });

    setIsTaskModalOpen(false);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
    setIsTaskModalOpen(false);
  };

  const handleDeleteTask = (type, id) => {
    setWorks((prevWorks) => ({
      ...prevWorks,
      [type]: prevWorks[type].filter((work) => work.id !== id),
    }));
  };

  const handleEditTask = (work) => {
    setEditMode(true);
    setFormData(work);
    setIsModalOpen(true);
  };

  const handleTaskCheckbox = (type, workId) => {
    setWorks((prevWorks) => {
      const updatedWorks = { ...prevWorks };

      // Tìm công việc cần hoàn thành và di chuyển vào completedWorks
      const completedWork = updatedWorks[type].find((work) => work.id === workId);
      if (completedWork) {
        setCompletedWorks((prevCompletedWorks) => [...prevCompletedWorks, completedWork]);
        updatedWorks[type] = updatedWorks[type].filter((work) => work.id !== workId);
      }

      return updatedWorks;
    });
  };

  const handleDragStart = (e, task, type) => {
    e.dataTransfer.setData("task", JSON.stringify({ ...task, type }));
  };

  const handleDrop = (e, newType) => {
    const data = JSON.parse(e.dataTransfer.getData("task"));

    if (data.type === newType) return;

    setWorks((prevWorks) => {
      const updatedSourceTasks = prevWorks[data.type].filter(
        (task) => task.id !== data.id
      );
      const updatedTargetTasks = [
        ...prevWorks[newType],
        { ...data, type: newType },
      ];
      return {
        ...prevWorks,
        [data.type]: updatedSourceTasks,
        [newType]: updatedTargetTasks,
      };
    });
  };

  const allowDrop = (e) => e.preventDefault();

  const formatDate = (dateString) => {
    if (!dateString) return "";
    const [year, month, day] = dateString.split("-");
    return `${day}/${month}/${year}`;
  };

  return (
    <div className="home-body-conatiner" data-aos="zoom-in">
      {/* Nội dung còn lại giữ nguyên */}
    </div>
  );
};

export default Dashboard;
