import React, { useState, useEffect } from "react";
import Aos from "aos";
import "./styles/dashboard.css";
import { AiFillDelete, AiFillEdit, AiOutlinePlus } from "react-icons/ai";

const Dashboard = () => {
  const [works, setWorks] = useState(() => {
    const savedWorks = localStorage.getItem("works");
    return savedWorks ? JSON.parse(savedWorks) : { doNow: [], schedule: [], delegate: [], drop: [] };
  });

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isTaskModalOpen, setIsTaskModalOpen] = useState(false);
  const [completedWorks, setCompletedWorks] = useState([]);

  const [formData, setFormData] = useState({
    id: null,
    name: "",
    type: "",
    startDate: "",
    endDate: "",
    describe: "",
    tag: "",
  });

  const [taskFormData, setTaskFormData] = useState({
    workId: null,
    taskName: "",
    taskDescription: "",
    startTime: "",
    endTime: "",
    repeatOption: "Không lặp lại",
    customRepeat: {
      repeatEvery: 1,
      repeatUnit: "ngày",
      repeatDays: [],
    },
  });

  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    Aos.init({ duration: 1000 });
  }, []);

  useEffect(() => {
    if (isModalOpen || isTaskModalOpen) {
      document.body.classList.add("disable-hover");
    } else {
      document.body.classList.remove("disable-hover");
    }
  }, [isModalOpen, isTaskModalOpen]);
  

  useEffect(() => {
    localStorage.setItem("works", JSON.stringify(works)); // Lưu works
    localStorage.setItem("completedWorks", JSON.stringify(completedWorks)); // Lưu completedWorks
  }, [works, completedWorks]); // Chỉ ghi khi `works` hoặc `completedWorks` thay đổi
  

  const handleAddNewWork = () => {
    setIsModalOpen(true);
    setEditMode(false);
    setFormData({
      id: null,
      name: "",
      type: "",
      startDate: "",
      endDate: "",
      describe: "",
      tag: "",
    });
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleTaskFormChange = (e) => {
    const { name, value } = e.target;
  
    setTaskFormData((prev) => {
      const updated = { ...prev, [name]: value };
  
      // Nếu chọn "ngày", reset các ngày trong tuần
      if (name === "customRepeat.repeatUnit" && value === "ngày") {
        updated.customRepeat.repeatDays = [];
      }
  
      return updated;
    });
  };
  

  const handleAddTask = (workId) => {
    const work = Object.values(works).flat().find((work) => work.id === workId);
    setIsTaskModalOpen(true);
    setTaskFormData({ ...taskFormData, workId, workName: work ? work.name : "" });
  };

  const handleDone = () => {
    const { name, type, startDate, endDate } = formData;

    if (!name || !type || !startDate || !endDate || !formData.tag) {
      alert("Please fill in all fields.");
      return;
    }

    setWorks((prevWorks) => {
      const updatedWorks = { ...prevWorks };

      if (editMode) {
        Object.keys(updatedWorks).forEach((type) => {
          updatedWorks[type] = updatedWorks[type].filter(
            (work) => work.id !== formData.id
          );
        });
      }

      updatedWorks[type] = [
        ...updatedWorks[type],
        {
          ...formData,
          id: formData.id || Date.now(),
          subWorks: formData.subWorks || [], // Khởi tạo subWorks
        },
      ];

      return updatedWorks;
    });

    setIsModalOpen(false);
  };

  const handleDoneTask = () => {
    if (!taskFormData.taskName || !taskFormData.startTime || !taskFormData.endTime) {
      alert("Please fill in all fields.");
      return;
    }
  
    const updatedCustomRepeat = { ...taskFormData.customRepeat };
    if (updatedCustomRepeat.repeatUnit === "ngày") {
      delete updatedCustomRepeat.repeatDays; // Xóa repeatDays nếu là ngày
    }
  
    const newTask = {
      ...taskFormData,
      customRepeat: updatedCustomRepeat,
    };

    setWorks((prevWorks) => {
      const updatedWorks = { ...prevWorks };
      const workType = Object.keys(updatedWorks).find((type) =>
        updatedWorks[type].some((work) => work.id === taskFormData.workId)
      );

      if (!workType) {
        alert("Work not found. Please try again.");
        return prevWorks; // Không thực hiện cập nhật nếu không tìm thấy work
      }

      const workIndex = updatedWorks[workType].findIndex(
        (work) => work.id === taskFormData.workId
      );

      if (workIndex === -1) {
        alert("Work not found. Please try again.");
        return prevWorks; // Không thực hiện cập nhật nếu không tìm thấy work
      }

      // Đảm bảo subWorks tồn tại và chỉ thêm task mới một lần
      const work = updatedWorks[workType][workIndex];
      if (!work.subWorks) {
        work.subWorks = [];
      }

      // Thêm task mới chỉ khi nó chưa tồn tại
      const isDuplicateTask = work.subWorks.some(
        (subWork) => subWork.name === taskFormData.taskName
      );

      if (!isDuplicateTask) {
        work.subWorks.push({
          name: taskFormData.taskName,
          description: taskFormData.taskDescription,
          startTime: taskFormData.startTime,
          endTime: taskFormData.endTime,
          repeatOption: taskFormData.repeatOption,
          customRepeat: taskFormData.customRepeat,
        });
      }

      return updatedWorks;
    });

    // Reset lại form sau khi thêm
    setTaskFormData({
      workId: null,
      taskName: "",
      taskDescription: "",
      startTime: "",
      endTime: "",
      repeatOption: "Không lặp lại",
      customRepeat: {
        repeatEvery: 1,
        repeatUnit: "ngày",
        repeatDays: [],
      },
    });

    setIsTaskModalOpen(false);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
    setIsTaskModalOpen(false);
  };

  const handleDeleteTask = (type, id) => {
    setWorks((prevWorks) => ({
      ...prevWorks,
      [type]: prevWorks[type].filter((work) => work.id !== id),
    }));
  };

  const handleEditTask = (work) => {
    setEditMode(true);
    setFormData(work);
    setIsModalOpen(true);
  };

  const handleWorkCheckbox = (type, workId, subWorkIndex) => {
    setWorks((prevWorks) => {
      const updatedWorks = { ...prevWorks };

      if (subWorkIndex === -1) {
        const completedWork = updatedWorks[type].find((work) => work.id === workId);
        if (completedWork) {
          setCompletedWorks((prevCompleted) => {
            const isAlreadyCompleted = prevCompleted.some((work) => work.id === workId);
            return isAlreadyCompleted ? prevCompleted : [...prevCompleted, completedWork];
          });
        }
        updatedWorks[type] = updatedWorks[type].filter((work) => work.id !== workId);
      }

      return updatedWorks;
    });
};

  const handleDragStart = (e, work, type) => {
    e.dataTransfer.setData("work", JSON.stringify({ ...work, type }));
  };

  const handleDrop = (e, newType) => {
    const data = JSON.parse(e.dataTransfer.getData("work"));

    if (data.type === newType) return;

    setWorks((prevWorks) => {
      const updatedSourceWorks = prevWorks[data.type].filter(
        (work) => work.id !== data.id
      );
      const updatedTargetWorks = [
        ...prevWorks[newType],
        { ...data, type: newType },
      ];
      return {
        ...prevWorks,
        [data.type]: updatedSourceWorks,
        [newType]: updatedTargetWorks,
      };
    });
  };

  const allowDrop = (e) => e.preventDefault();

  const formatDate = (dateString) => {
    if (!dateString) return "";
    const [year, month, day] = dateString.split("-");
    return `${day}/${month}/${year}`;
  };

  return (
    <div className="home-body-conatiner" data-aos="zoom-in">
      <header id="dash-header" className="search-bar">
        <h1>Dashboard</h1>
      </header>
      <button className="add-work-btn" onClick={handleAddNewWork}>
        Add new work
      </button>

      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h2>{editMode ? "Edit Work" : "Add New Work"}</h2>
            <input
              type="text"
              placeholder="Enter work name"
              name="name"
              value={formData.name}
              onChange={handleFormChange}
            />
            <div className="form-row">
              <select
                name="type"
                value={formData.type}
                onChange={handleFormChange}
              >
                <option value="">Select type</option>
                <option value="doNow">Do now</option>
                <option value="schedule">Schedule</option>
                <option value="delegate">Delegate</option>
                <option value="drop">Drop</option>
              </select>
              <select
                name="tag"
                value={formData.tag}
                onChange={(e) => {
                  handleFormChange(e);
                  if (e.target.value === "Other") {
                    setFormData((prev) => ({ ...prev, customTag: "" }));
                  } else {
                    setFormData((prev) => ({ ...prev, customTag: null }));
                  }
                }}
              >
                <option value="">Select tag</option>
                <option value="Học tập">Học tập</option>
                <option value="Làm việc">Làm việc</option>
                <option value="Nghỉ ngơi">Nghỉ ngơi</option>
                <option value="Giải trí">Giải trí</option>
                <option value="Other">Other</option>
              </select>
              {formData.tag === "Other" && (
                <input
                  type="text"
                  placeholder="Enter custom tag"
                  name="customTag"
                  value={formData.customTag || ""}
                  onChange={handleFormChange}
                />
              )}
            </div>
            <div className="form-row">
              <input
                type="date"
                name="startDate"
                value={formData.startDate}
                onChange={handleFormChange}
              />
              <input
                type="date"
                name="endDate"
                value={formData.endDate}
                onChange={handleFormChange}
              />
            </div>
            <textarea
              placeholder="Describe work (use # for hashtags)"
              name="describe"
              value={formData.describe}
              onChange={handleFormChange}
              rows="4"
            ></textarea>
            <div className="modal-buttons">
              <button onClick={handleDone}>Done</button>
              <button onClick={handleCancel}>Cancel</button>
            </div>
          </div>
        </div>
      )}

      {isTaskModalOpen && (
        <div className="modal modal-task">
          <div className="modal-content modal-task-content">
            <div className="modal-left">
              <h2>Add Task for {taskFormData.workName}</h2>
              <input
                type="text"
                placeholder="Enter task name"
                name="taskName"
                value={taskFormData.taskName}
                onChange={handleTaskFormChange}
              />
              <textarea
                placeholder="Task description"
                name="taskDescription"
                value={taskFormData.taskDescription}
                onChange={handleTaskFormChange}
                rows="4"
              ></textarea>
              <div className="form-row-inline">
                <div>
                  <label>Start Time</label>
                  <input
                    type="time"
                    name="startTime"
                    value={taskFormData.startTime}
                    onChange={handleTaskFormChange}
                  />
                </div>
                <div>
                  <label>End Time</label>
                  <input
                    type="time"
                    name="endTime"
                    value={taskFormData.endTime}
                    onChange={handleTaskFormChange}
                  />
                </div>
              </div>
              <select
                name="repeatOption"
                value={taskFormData.repeatOption}
                onChange={(e) => {
                  handleTaskFormChange(e);
                  if (e.target.value === "Tùy chỉnh") {
                    setTaskFormData((prev) => ({
                      ...prev,
                      customRepeat: {
                        ...prev.customRepeat,
                        isCustomizing: true,
                      },
                    }));
                  }
                }}
              >
                <option value="Không lặp lại">Không lặp lại</option>
                <option value="Hàng ngày">Hàng ngày</option>
                <option value="Tùy chỉnh">Tùy chỉnh</option>
              </select>
              {taskFormData.repeatOption === "Tùy chỉnh" && taskFormData.customRepeat.isCustomizing && (
  <div className="custom-repeat-form">
    <div className="form-row-inline">
      <label>
        Lặp lại mỗi:
        <input
          type="number"
          min="1"
          name="repeatEvery"
          value={taskFormData.customRepeat.repeatEvery}
          onChange={(e) =>
            setTaskFormData((prev) => ({
              ...prev,
              customRepeat: {
                ...prev.customRepeat,
                repeatEvery: Math.max(1, Number(e.target.value)),
              },
            }))
          }
        />
      </label>
      <select
        name="repeatUnit"
        value={taskFormData.customRepeat.repeatUnit}
        onChange={(e) =>
          setTaskFormData((prev) => {
            const isDay = e.target.value === "ngày";
            return {
              ...prev,
              customRepeat: {
                ...prev.customRepeat,
                repeatUnit: e.target.value,
                repeatDays: isDay ? [] : prev.customRepeat.repeatDays, // Xóa repeatDays nếu là ngày
              },
            };
          })
        }
      >
        <option value="ngày">Ngày</option>
        <option value="tuần">Tuần</option>
        <option value="tháng">Tháng</option>
        <option value="năm">Năm</option>
      </select>
    </div>

    {/* Nếu là ngày thì thêm trường ngày bắt đầu */}
    {taskFormData.customRepeat.repeatUnit === "ngày" ? (
      <div className="form-row">
        <label>
          Ngày bắt đầu:
          <input
            type="date"
            name="startDate"
            value={taskFormData.customRepeat.startDate || ""}
            onChange={(e) =>
              setTaskFormData((prev) => ({
                ...prev,
                customRepeat: {
                  ...prev.customRepeat,
                  startDate: e.target.value,
                },
              }))
            }
          />
        </label>
      </div>
    ) : (
      // Nếu không phải ngày, hiển thị danh sách thứ
      <div className="form-row">
        <label>Lặp lại vào:</label>
        {["T2", "T3", "T4", "T5", "T6", "T7", "CN"].map((day) => (
          <label key={day} className="repeat-day-option">
            <input
              type="checkbox"
              disabled={taskFormData.customRepeat.repeatUnit === "ngày"}
              checked={taskFormData.customRepeat.repeatDays.includes(day)}
              onChange={(e) => {
                setTaskFormData((prev) => {
                  const updatedDays = e.target.checked
                    ? [...prev.customRepeat.repeatDays, day]
                    : prev.customRepeat.repeatDays.filter((d) => d !== day);
                  return {
                    ...prev,
                    customRepeat: {
                      ...prev.customRepeat,
                      repeatDays: updatedDays,
                    },
                  };
                });
              }}
            />
            {day}
          </label>
        ))}
      </div>
    )}

    <button
      className="custom-repeat-done"
      onClick={() =>
        setTaskFormData((prev) => ({
          ...prev,
          customRepeat: {
            ...prev.customRepeat,
            isCustomizing: false,
          },
        }))
      }
    >
      Done
    </button>
  </div>
)}

{taskFormData.repeatOption === "Tùy chỉnh" && !taskFormData.customRepeat.isCustomizing && (
  <p>
    Lặp lại {taskFormData.customRepeat.repeatEvery}{" "}
    {taskFormData.customRepeat.repeatUnit}
    {taskFormData.customRepeat.repeatUnit === "ngày"
      ? ` một lần từ ${new Date(taskFormData.customRepeat.startDate).toLocaleDateString("vi-VN")}`
      : ` một lần vào thứ ${taskFormData.customRepeat.repeatDays.join(", ")}`}
  </p>
)}


              <div className="modal-buttons">
                <button onClick={handleDoneTask}>Add Task</button>
                <button onClick={() => setIsTaskModalOpen(false)}>Cancel</button>
              </div>
            </div>
            <div className="modal-right">
  <h2>Existing Tasks</h2>
  {(() => {
    const workType = Object.keys(works).find((type) =>
      works[type].some((work) => work.id === taskFormData.workId)
    );
    if (!workType) return <p>No tasks available.</p>;

    const work = works[workType].find((work) => work.id === taskFormData.workId);

    return work && work.subWorks && work.subWorks.length > 0 ? (
      <ul>
        {work.subWorks.map((subWork, index) => (
          <li key={index}>
            <h4>{subWork.name}</h4>
            <p>{subWork.description}</p>
            <p>
              {subWork.startTime} - {subWork.endTime}
            </p>
          </li>
        ))}
      </ul>
    ) : (
      <p>No tasks available.</p>
    );
  })()}
</div>

          </div>
        </div>
      )}

      <main className="body-content">
        {["doNow", "schedule", "delegate", "drop"].map((type) => (
          <div
            className="task-box"
            key={type}
            onDrop={(e) => handleDrop(e, type)}
            onDragOver={allowDrop}
          >
            <h2>{type.charAt(0).toUpperCase() + type.slice(1)}</h2>
            <div className="task-list">
              {works[type].map((work) => (
                <div
                  key={work.id}
                  className="task"
                  draggable
                  onDragStart={(e) => handleDragStart(e, work, type)}
                >
                  <div>
                    <input
                      type="checkbox"
                      onChange={() => handleWorkCheckbox(type, work.id, -1)}
                    />
                    <span>{work.name}</span>
                    <p>
                      {formatDate(work.startDate)} - {formatDate(work.endDate)}
                    </p>
                    <p>
                      {work.describe
                        ? work.describe.split(" ").map((word, i) =>
                            word.startsWith("#") ? (
                              <span key={i} style={{ color: "blue" }}>
                                {word}{" "}
                              </span>
                            ) : (
                              `${word} `
                            )
                          )
                        : "No description"}
                    </p>
                    <p>Tasks: {work.subWorks ? work.subWorks.length : 0}</p> {/* Hiển thị số task */}
                  </div>
                  <div className="task-buttons">
                    <AiOutlinePlus
                      className="add-task-icon"
                      onClick={() => handleAddTask(work.id)}
                    />
                    <AiFillEdit
                      className="edit-icon"
                      onClick={() => handleEditTask(work)}
                    />
                    <AiFillDelete
                      className="delete-icon"
                      onClick={() => handleDeleteTask(type, work.id)}
                    />
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </main>
    </div>
  );
};

export default Dashboard;
