import React, { useState, useEffect, useMemo } from "react";
import { AiFillDelete } from "react-icons/ai";
import { BiSearchAlt2 } from "react-icons/bi";
import { FaChevronDown, FaChevronUp } from "react-icons/fa";
import "aos/dist/aos.css";
import "./styles/project.css";

const Project = () => {
  const [works, setWorks] = useState({
    doNow: [],
    schedule: [],
    delegate: [],
    drop: [],
  });
  const [completedWorks, setCompletedWorks] = useState([]);
  const [viewMode, setViewMode] = useState("ongoing");
  const [searchQuery, setSearchQuery] = useState("");
  const [expandedWorkIds, setExpandedWorkIds] = useState([]); // State to track expanded works

  // Load data from localStorage when component mounts
  useEffect(() => {
    const storedWorks =
      JSON.parse(localStorage.getItem("works")) || {
        doNow: [],
        schedule: [],
        delegate: [],
        drop: [],
      };
    const storedCompletedWorks =
      JSON.parse(localStorage.getItem("worksdone")) || [];
    setWorks(storedWorks);
    setCompletedWorks(storedCompletedWorks);
  }, []);

  // Save completedWorks to localStorage
  useEffect(() => {
    localStorage.setItem("worksdone", JSON.stringify(completedWorks));
  }, [completedWorks]);

  // Filter works based on view mode (ongoing/completed) and search query
  const filteredWorks = useMemo(() => {
    const allOngoingWorks = Object.values(works).flat(); // Merge all works from different categories

    if (viewMode === "ongoing") {
      // Show only works that are not completed
      return allOngoingWorks.filter(
        (work) =>
          !completedWorks.some((completedWork) => completedWork.id === work.id)
      );
    }

    // useEffect to load data from localStorage when switching viewMode
useEffect(() => {
  if (viewMode === "completed") {
    const storedCompletedWorks = JSON.parse(localStorage.getItem("worksdone")) || [];
    setCompletedWorks(storedCompletedWorks);
  } else {
    const storedWorks = JSON.parse(localStorage.getItem("works")) || {
      doNow: [],
      schedule: [],
      delegate: [],
      drop: [],
    };
    setWorks(storedWorks);
  }
}, [viewMode]);


    return [];
  }, [viewMode, works, completedWorks]);

  // Toggle expand/collapse for a work item
  const toggleExpand = (workId) => {
    setExpandedWorkIds((prevExpandedWorkIds) =>
      prevExpandedWorkIds.includes(workId)
        ? prevExpandedWorkIds.filter((id) => id !== workId)
        : [...prevExpandedWorkIds, workId]
    );
  };

  // Handle marking work as completed or ongoing
  const handleToggleComplete = (work) => {
    if (viewMode === "ongoing") {
      // Nếu đang ở ongoing, đánh dấu là completed
      setCompletedWorks((prev) => {
        const updatedCompleted = [...prev, work];
  
        // Lưu completedWorks vào localStorage
        localStorage.setItem("worksdone", JSON.stringify(updatedCompleted));
        return updatedCompleted;
      });
  
      setWorks((prevWorks) => {
        const updatedWorks = { ...prevWorks };
        Object.keys(updatedWorks).forEach((key) => {
          updatedWorks[key] = updatedWorks[key].filter((w) => w.id !== work.id);
        });
  
        // Cập nhật works vào localStorage
        localStorage.setItem("works", JSON.stringify(updatedWorks));
        return updatedWorks;
      });
    } else {
      // Nếu đang ở completed, chuyển về ongoing
      setCompletedWorks((prev) => {
        const updatedCompleted = prev.filter((w) => w.id !== work.id);
  
        // Lưu completedWorks vào localStorage
        localStorage.setItem("worksdone", JSON.stringify(updatedCompleted));
        return updatedCompleted;
      });
  
      setWorks((prevWorks) => {
        const updatedWorks = { ...prevWorks };
        const type = work.type || "doNow"; // Xác định loại công việc
        updatedWorks[type] = [...updatedWorks[type], work];
  
        // Cập nhật works vào localStorage
        localStorage.setItem("works", JSON.stringify(updatedWorks));
        return updatedWorks;
      });
    }
  };
  
  // Đọc lại dữ liệu khi component mount
  useEffect(() => {
    const savedWorks = JSON.parse(localStorage.getItem("works")) || { doNow: [], schedule: [], delegate: [], drop: [] };
    const savedCompletedWorks = JSON.parse(localStorage.getItem("worksdone")) || [];
    setWorks(savedWorks);
    setCompletedWorks(savedCompletedWorks);
  }, []);
  
  

  return (
    <div className="main-form" data-aos="zoom-in">
      <div className="search-bar">
        <h1>Projects</h1>
        <input
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          type="search"
          placeholder={`Search ${
            viewMode === "ongoing" ? "ongoing" : "completed"
          } works`}
        />
        <button id="search-bt">
          <BiSearchAlt2 size={22} />
        </button>
      </div>
      <div className="view-toggle">
        <button
          className={viewMode === "ongoing" ? "active" : ""}
          onClick={() => setViewMode("ongoing")}
        >
          On Going Works
        </button>
        <button
          className={viewMode === "completed" ? "active" : ""}
          onClick={() => setViewMode("completed")}
        >
          Completed Works
        </button>
      </div>
      <div className="item-list">
        {filteredWorks.length === 0 ? (
          <h3 id="no-todo">
            {viewMode === "ongoing"
              ? "No ongoing works"
              : "No completed works"}
          </h3>
        ) : (
          <ul>
            {filteredWorks.flatMap((work) => {
              const workElements = [
                <li key={work.id} className="work-item">
                  <div className="work-header">
                    <label htmlFor="" className="item-name">
                      <input
                        type="checkbox"
                        checked={viewMode === "completed"}
                        onChange={() => handleToggleComplete(work)}
                      />
                      {work.name}
                    </label>
                    <div className="work-actions">
                      {viewMode === "ongoing" && (
                        <button id="del-bt">
                          <AiFillDelete size={20} color="#FF6969" />
                        </button>
                      )}
                      <button
                        className="expand-bt"
                        onClick={() => toggleExpand(work.id)}
                      >
                        {expandedWorkIds.includes(work.id) ? (
                          <FaChevronUp />
                        ) : (
                          <FaChevronDown />
                        )}
                      </button>
                    </div>
                  </div>
                </li>,
              ];

              // If expanded, add task items between works
              if (
                expandedWorkIds.includes(work.id) &&
                work.subWorks &&
                work.subWorks.length > 0
              ) {
                work.subWorks.forEach((task, index) => {
                  workElements.push(
                    <li key={`${work.id}-task-${index}`} className="task-item">
                      <div className="task-details">
                        <span>
                          {task.name} - Description: {task.description}, Time:{" "}
                          {task.startTime} - {task.endTime}, Repeat:{" "}
                          {task.repeatOption}
                        </span>
                      </div>
                    </li>
                  );
                });
              }

              return workElements;
            })}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Project;
