import React, { useEffect, useState } from "react";
import { IoCalendarNumber, IoNotificationsOutline, IoMoonOutline, IoSunnyOutline } from "react-icons/io5";
import { BiLogOut, BiSolidDashboard } from "react-icons/bi";
import { MdOutlineWatchLater } from "react-icons/md";
import { AiOutlineSchedule, AiOutlineBarChart } from "react-icons/ai";
import { useNavigate, useLocation } from "react-router-dom";
import icon from "../utils/icon.PNG";
import Aos from "aos";
import "aos/dist/aos.css";
import { toast } from "react-toastify";

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    Aos.init({ duration: 800 });
  }, []);

  function logOut() {
    toast.success("Logged out successfully (mock)");
    navigate("/");
  }

  function toggleDarkMode() {
    setDarkMode(!darkMode);
  }

  const navItems = [
    { path: "", label: "User Avatar", icon: <img src="path_to_user_avatar.jpg" alt="User Avatar" style={{ borderRadius: '50%', width: '20px', height: '20px' }} />, onClick: () => console.log("User profile clicked") },
    { path: "/Home/notifications", label: "Notifications", icon: <IoNotificationsOutline size={20} color="white" />, onClick: () => console.log("Notifications clicked") },
    { path: "", label: "Toggle Dark Mode", icon: darkMode ? <IoSunnyOutline size={20} color="white" /> : <IoMoonOutline size={20} color="white" />, onClick: toggleDarkMode },
    { path: "/Home", label: "Dashboard", icon: <BiSolidDashboard size={22} color="white" /> },
    { path: "/Home/projects", label: "Project", icon: <MdOutlineWatchLater size={20} color="white" /> },
    { path: "/Home/focus", label: "Focus", icon: <AiOutlineSchedule size={20} color="white" /> },
    { path: "/Home/timetable", label: "TimeTable", icon: <IoCalendarNumber size={20} color="white" /> },
    { path: "/Home/calendar", label: "Calendar", icon: <IoCalendarNumber size={20} color="white" /> },
    { path: "/Home/report", label: "Report", icon: <AiOutlineBarChart size={20} color="white" /> }
  ];

  return (
    <nav className="nav-left" data-aos="fade-right">
      <button className="awaretime-theme">
        <img src={icon} alt="" />
      </button>
      {navItems.map((item, index) => (
        <button
          key={index}
          onClick={() => {
            item.onClick ? item.onClick() : navigate(item.path);
          }}
          className={`nav-icon skull ${location.pathname === item.path ? "active" : ""}`}
        >
          {item.icon}
        </button>
      ))}
      <button className="nav-icon skull" onClick={logOut}>
        <BiLogOut size={22} color="white" />
      </button>
    </nav>
  );
};

export default Navbar;
