import React, { useState, useEffect } from "react";
import Aos from "aos";
import "./styles/dashboard.css"; // Add a CSS file for custom styling
import { AiFillDelete } from "react-icons/ai";

const Dashboard = () => {
  const [workName, setWorkName] = useState("");
  const [workType, setWorkType] = useState("");
  const [tasks, setTasks] = useState({
    doNow: [],
    schedule: [],
    delegate: [],
    drop: [],
  });

  useEffect(() => {
    Aos.init({ duration: 1000 });
  }, []);

  const handleAddTask = () => {
    if (!workName || !workType) {
      alert("Please enter a name and select a type.");
      return;
    }

    setTasks((prevTasks) => ({
      ...prevTasks,
      [workType]: [
        ...prevTasks[workType],
        { id: Date.now(), name: workName, type: workType },
      ],
    }));

    setWorkName("");
    setWorkType("");
  };

  const handleDeleteTask = (type, id) => {
    setTasks((prevTasks) => ({
      ...prevTasks,
      [type]: prevTasks[type].filter((task) => task.id !== id),
    }));
  };

  const handleDragStart = (e, task, type) => {
    e.dataTransfer.setData("task", JSON.stringify({ ...task, type }));
  };

  const handleDrop = (e, newType) => {
    const data = JSON.parse(e.dataTransfer.getData("task"));
    setTasks((prevTasks) => {
      // Remove from old type
      const updatedSourceTasks = prevTasks[data.type].filter(
        (task) => task.id !== data.id
      );
      // Add to new type
      const updatedTargetTasks = [
        ...prevTasks[newType],
        { ...data, type: newType },
      ];
      return {
        ...prevTasks,
        [data.type]: updatedSourceTasks,
        [newType]: updatedTargetTasks,
      };
    });
  };

  const allowDrop = (e) => e.preventDefault();

  return (
    <div className="home-body-conatiner" data-aos="zoom-in">
      <header id="dash-header" className="search-bar">
        <h1>Dashboard</h1>
      </header>
      <div className="task-input">
        <input
          type="text"
          placeholder="Enter work name"
          value={workName}
          onChange={(e) => setWorkName(e.target.value)}
        />
        <select
          value={workType}
          onChange={(e) => setWorkType(e.target.value)}
        >
          <option value="">Select type</option>
          <option value="doNow">Do now</option>
          <option value="schedule">Schedule</option>
          <option value="delegate">Delegate</option>
          <option value="drop">Drop</option>
        </select>
        <button onClick={handleAddTask}>Add</button>
      </div>
      <main className="body-content">
        <div
          className="task-box"
          onDrop={(e) => handleDrop(e, "doNow")}
          onDragOver={allowDrop}
        >
          <h2>Do now</h2>
          {tasks.doNow.map((task) => (
            <div
              key={task.id}
              className="task"
              draggable
              onDragStart={(e) => handleDragStart(e, task, "doNow")}
            >
              <span>{task.name}</span>
              <AiFillDelete
                className="delete-icon"
                onClick={() => handleDeleteTask("doNow", task.id)}
              />
            </div>
          ))}
        </div>
        <div
          className="task-box"
          onDrop={(e) => handleDrop(e, "schedule")}
          onDragOver={allowDrop}
        >
          <h2>Schedule</h2>
          {tasks.schedule.map((task) => (
            <div
              key={task.id}
              className="task"
              draggable
              onDragStart={(e) => handleDragStart(e, task, "schedule")}
            >
              <span>{task.name}</span>
              <AiFillDelete
                className="delete-icon"
                onClick={() => handleDeleteTask("schedule", task.id)}
              />
            </div>
          ))}
        </div>
        <div
          className="task-box"
          onDrop={(e) => handleDrop(e, "delegate")}
          onDragOver={allowDrop}
        >
          <h2>Delegate</h2>
          {tasks.delegate.map((task) => (
            <div
              key={task.id}
              className="task"
              draggable
              onDragStart={(e) => handleDragStart(e, task, "delegate")}
            >
              <span>{task.name}</span>
              <AiFillDelete
                className="delete-icon"
                onClick={() => handleDeleteTask("delegate", task.id)}
              />
            </div>
          ))}
        </div>
        <div
          className="task-box"
          onDrop={(e) => handleDrop(e, "drop")}
          onDragOver={allowDrop}
        >
          <h2>Drop</h2>
          {tasks.drop.map((task) => (
            <div
              key={task.id}
              className="task"
              draggable
              onDragStart={(e) => handleDragStart(e, task, "drop")}
            >
              <span>{task.name}</span>
              <AiFillDelete
                className="delete-icon"
                onClick={() => handleDeleteTask("drop", task.id)}
              />
            </div>
          ))}
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
