import Toggler from "./components/Toggler";
import Calendar from "./components/Calendar";
import Home from "./components/Home";
import "react-toastify/dist/ReactToastify.css";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import Project  from "./components/Project";
import Dashboard from "./components/Dashboard";
import Report from "./components/Report";
import ForgotPass from "./components/ForgotPass";
import ResetPass from "./components/ResetPass";
import { useState } from "react";

function App() {
  const [notes, setNotes] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [todo, setTodo] = useState([]);

  return (
    <BrowserRouter>
      <ToastContainer
        position="top-center"
        autoClose={800}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />
          <Routes>
            <Route path="/" element={<Toggler toast={toast} />} />
            <Route path="/ForgotPass" element={<ForgotPass toast={toast} />} />
            <Route path="/ResetPass/:id/:token" element={<ResetPass toast={toast} />} />
            <Route path="/Home" element={<Home tasks={tasks} />}>
            <Route
          index
          element={
            <Dashboard
              notes={notes}
              setNotes={setNotes}
              tasks={tasks}
              setTasks={setTasks}
              todo={todo}
              setTodo={setTodo}
            />
          }
        />
        <Route
          path="/Home/projects"
          element={<Project toast={toast} todo={todo} setTodo={setTodo} />}
        />
        <Route
          path="/Home/focus"
          element={<Focus toast={toast} todo={todo} setTodo={setTodo} />}
        />
        <Route
          path="/Home/timetable"
          element={<div>TimeTable Page (To Be Implemented)</div>}
        />
        <Route
          path="/Home/calendar"
          element={<div>Calendar Page (To Be Implemented)</div>}
        />
        <Route
          path="/Home/report"
          element={<div>Report Page (To Be Implemented)</div>}
        />
      </Route>
    </Routes>

    </BrowserRouter>
  );
}

export default App;
