import React, { useEffect, useState } from "react";
import { IoMdNotifications, IoMdSunny, IoIosMoon } from "react-icons/io";
import { BiLogOut, BiDashboard } from "react-icons/bi";
import { MdOutlineWatchLater } from "react-icons/md";
import { AiOutlineSchedule, AiOutlineBarChart } from "react-icons/ai";
import { useNavigate, useLocation } from "react-router-dom";
import icon from "../utils/icon.PNG";
import Aos from "aos";
import "aos/dist/aos.css";
import { toast } from "react-toastify";
import axios from "axios";
import Notification from "./Notification/Notification";

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [user, setUser] = useState({});
  const [upcomingTasks, setUpcomingTasks] = useState([]);
  const [dialog, setDialog] = useState({ isLoading: false });

  useEffect(() => {
    Aos.init({ duration: 800 });
    axios.get(`${process.env.REACT_APP_API_URL}/getUser`)
      .then((res) => setUser(res.data))
      .catch((err) => console.error(err));

    axios.get(`${process.env.REACT_APP_API_URL}/task/getTask`)
      .then((res) => setUpcomingTasks(res.data.filter(task => !task.done)))
      .catch((err) => console.error(err));
  }, []);

  const openNotifi = () => setDialog({ isLoading: true });
  const closeNotifi = () => setDialog({ isLoading: false });

  const setDarkMode = (dark) => {
    document.body.setAttribute("data-theme", dark ? "dark" : "light");
  };

  function logOut() {
    toast.success("Logged out successfully (mock)");
    navigate("/");
  }

  const navItems = [
    { path: "/Home", label: "Dashboard", icon: <BiDashboard size={22} color="white" /> },
    { path: "/Home/projects", label: "Project", icon: <MdOutlineWatchLater size={20} color="white" /> },
    { path: "/Home/focus", label: "Focus", icon: <AiOutlineSchedule size={20} color="white" /> },
    { path: "/Home/timetable", label: "TimeTable", icon: <IoMdNotifications size={20} color="white" /> },
    { path: "/Home/calendar", label: "Calendar", icon: <AiOutlineBarChart size={20} color="white" /> },
    { path: "/Home/report", label: "Report", icon: <BiLogOut size={20} color="white" /> },
  ];

  return (
    <nav className="nav-left" data-aos="fade-right">
      <button className="awaretime-theme">
        <img src={icon} alt="" />
      </button>
      {navItems.map((item) => (
        <button
          key={item.path}
          onClick={() => navigate(item.path)}
          className={`nav-icon skull ${location.pathname === item.path ? "active" : ""}`}
        >
          {item.icon}
        </button>
      ))}
      <button className="nav-icon skull" onClick={() => setDarkMode(true)}>
        <IoMdSunny color="#d8860b" size={16} />
        <IoIosMoon color="black" size={16} />
      </button>
      <button className="nav-icon skull" onClick={openNotifi}>
        <IoMdNotifications size={25} color="#3081D0" />
      </button>
      <button className="nav-icon skull" onClick={logOut}>
        <BiLogOut size={22} color="white" />
      </button>
      {dialog.isLoading && <Notification closeNotifi={closeNotifi} upcomingTasks={upcomingTasks} />}
    </nav>
  );
};

export default Navbar;
