import React, { useEffect, useState, useCallback } from "react";
import "./styles/focus.css";

const Focus = ({ works = {}, toast }) => {
  const [mode, setMode] = useState("auto");
  const [tasksForToday, setTasksForToday] = useState([]);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [visibleHours, setVisibleHours] = useState([]);

  // Pomodoro logic
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 60000); // Update current time every minute
    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    calculateVisibleHours();
  }, [currentTime]);

  const calculateVisibleHours = () => {
    const now = new Date(currentTime);
    const hours = [];
    for (let i = -2; i <= 2; i++) {
      const temp = new Date(now);
      temp.setHours(now.getHours() + i, 0, 0, 0);
      hours.push(temp);
    }
    setVisibleHours(hours);
  };

  // Calculate tasks for today
  const calculateTasksForToday = useCallback(() => {
    const today = new Date();
    const tasks = [];

    Object.values(works).flat().forEach((work) => {
      const workStartDate = new Date(work.startDate);
      const workEndDate = new Date(work.endDate);

      work.subWorks.forEach((task) => {
        const { startTime, endTime, repeatOption, customRepeat = {} } = task;
        const [startHour, startMinute] = startTime.split(":").map(Number);
        const [endHour, endMinute] = endTime.split(":").map(Number);

        // Create valid date range
        let validDates = [];
        if (repeatOption === "Không lặp lại") {
          validDates = [workStartDate];
        } else if (repeatOption === "Hàng ngày") {
          let currentDate = new Date(workStartDate);
          while (currentDate <= workEndDate) {
            validDates.push(new Date(currentDate));
            currentDate.setDate(currentDate.getDate() + 1);
          }
        } else if (repeatOption === "Tùy chỉnh") {
          const { repeatEvery = 1, repeatUnit = "ngày", startDate, repeatDays = [] } = customRepeat;
          const taskStartDate = startDate ? new Date(startDate) : workStartDate;
          let currentDate = new Date(taskStartDate);

          if (repeatUnit === "ngày") {
            while (currentDate <= workEndDate) {
              validDates.push(new Date(currentDate));
              currentDate.setDate(currentDate.getDate() + repeatEvery);
            }
          } else if (repeatUnit === "tuần") {
            const dayMapping = ["CN", "T2", "T3", "T4", "T5", "T6", "T7"];
            while (currentDate <= workEndDate) {
              const dayOfWeek = dayMapping[currentDate.getDay()];
              if (repeatDays.includes(dayOfWeek)) {
                validDates.push(new Date(currentDate));
              }
              currentDate.setDate(currentDate.getDate() + 1);
            }
          }
        }

        validDates.forEach((date) => {
          if (date.toDateString() === today.toDateString()) {
            tasks.push({
              ...task,
              workName: work.name,
              startTime: new Date(today.setHours(startHour, startMinute)),
              endTime: new Date(today.setHours(endHour, endMinute)),
            });
          }
        });
      });
    });

    const sortedTasks = tasks.sort((a, b) => a.startTime - b.startTime);
    setTasksForToday(sortedTasks);
  }, [works]);

  useEffect(() => {
    if (mode === "auto") {
      calculateTasksForToday();
    }
  }, [mode, calculateTasksForToday]);

  // Render task column on the time axis
  const renderTaskColumn = (hour) => {
    const tasksAtThisHour = tasksForToday.filter(
      (task) =>
        task.startTime.getHours() <= hour.getHours() &&
        task.endTime.getHours() >= hour.getHours()
    );

    if (tasksAtThisHour.length === 0) return null;

    const firstTask = tasksAtThisHour[0];

    return (
      <div
        className="task-column"
        style={{
          top: `${(hour.getHours() - visibleHours[0].getHours()) * 20}px`,
        }}
        key={firstTask.name}
      >
        <div className="task-label">
          {firstTask.name} ({firstTask.workName})
        </div>
      </div>
    );
  };

  return (
    <div className="main-form" data-aos="zoom-in">
      <header className="search-bar">
        <h1>Focus</h1>
      </header>

      <div className="focus-modes">
        <button
          className={`focus-mode ${mode === "auto" ? "active" : ""}`}
          onClick={() => setMode("auto")}
        >
          Tự động
        </button>
        <button
          className={`focus-mode ${mode === "manual" ? "active" : ""}`}
          onClick={() => setMode("manual")}
        >
          Tự chọn
        </button>
        <button
          className={`focus-mode ${mode === "free" ? "active" : ""}`}
          onClick={() => setMode("free")}
        >
          Tự do
        </button>
      </div>

      <div className="focus-content">
        {mode === "auto" && (
          <div className="time-axis-container">
            <div className="time-axis">
              {visibleHours.map((hour, index) => (
                <div className="time-axis-hour" key={index}>
                  <span className="time-axis-label">
                    {hour.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })}
                  </span>
                  {renderTaskColumn(hour)}
                </div>
              ))}
              <div className="time-axis-current"></div>
            </div>
            <div className="task-list">
              <h2>Task cho hôm nay</h2>
              <p>Tổng số task: <strong>{tasksForToday.length}</strong></p>
              <table className="task-table">
                <thead>
                  <tr>
                    <th>STT</th>
                    <th>Tên Task</th>
                    <th>Work</th>
                    <th>Thời gian</th>
                  </tr>
                </thead>
                <tbody>
                  {tasksForToday.map((task, index) => (
                    <tr key={index}>
                      <td>{index + 1}</td>
                      <td>{task.name}</td>
                      <td>{task.workName}</td>
                      <td>
                        {task.startTime.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })} -{" "}
                        {task.endTime.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Focus;
