import React, { useState, useEffect } from "react";
import Aos from "aos";
import "./styles/dashboard.css";
import { AiFillDelete, AiFillEdit, AiOutlinePlus } from "react-icons/ai";

const Dashboard = () => {
  const [tasks, setTasks] = useState(() => {
    const savedTasks = localStorage.getItem("tasks");
    return savedTasks ? JSON.parse(savedTasks) : { doNow: [], schedule: [], delegate: [], drop: [] };
  });

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isTaskModalOpen, setIsTaskModalOpen] = useState(false);
  const [completedTasks, setCompletedTasks] = useState([]);

  const [formData, setFormData] = useState({
    id: null,
    name: "",
    type: "",
    startDate: "",
    endDate: "",
    describe: "",
    tag: "",
  });

  const [taskFormData, setTaskFormData] = useState({
    workId: null,
    taskName: "",
    taskDescription: "",
    startTime: "",
    endTime: "",
    repeatOption: "Không lặp lại",
    customRepeat: {
      repeatEvery: 1,
      repeatUnit: "ngày",
      repeatDays: [],
    },
  });

  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    Aos.init({ duration: 1000 });
  }, []);

  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  const handleAddNewWork = () => {
    setIsModalOpen(true);
    setEditMode(false);
    setFormData({
      id: null,
      name: "",
      type: "",
      startDate: "",
      endDate: "",
      describe: "",
      tag: "",
    });
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleTaskFormChange = (e) => {
    const { name, value } = e.target;
    setTaskFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleAddTask = (workId) => {
    const work = Object.values(tasks).flat().find((task) => task.id === workId);
    setIsTaskModalOpen(true);
    setTaskFormData({ ...taskFormData, workId, workName: work ? work.name : "" });
  };
  

  const handleDone = () => {
    const { name, type, startDate, endDate } = formData;
  
    if (!name || !type || !startDate || !endDate || !formData.tag) {
      alert("Please fill in all fields.");
      return;
    }
  
    setTasks((prevTasks) => {
      const updatedTasks = { ...prevTasks };
  
      if (editMode) {
        Object.keys(updatedTasks).forEach((type) => {
          updatedTasks[type] = updatedTasks[type].filter(
            (task) => task.id !== formData.id
          );
        });
      }
  
      updatedTasks[type] = [
        ...updatedTasks[type],
        {
          ...formData,
          id: formData.id || Date.now(),
          subTasks: formData.subTasks || [], // Khởi tạo subTasks
        },
      ];
  
      return updatedTasks;
    });
  
    setIsModalOpen(false);
  };
  

  const handleDoneTask = () => {
    if (!taskFormData.taskName || !taskFormData.startTime || !taskFormData.endTime) {
      alert("Please fill in all fields.");
      return;
    }
  
    setTasks((prevTasks) => {
      const updatedTasks = { ...prevTasks };
      const taskType = Object.keys(updatedTasks).find((type) =>
        updatedTasks[type].some((task) => task.id === taskFormData.workId)
      );
  
      if (!taskType) {
        alert("Work not found. Please try again.");
        return prevTasks; // Không thực hiện cập nhật nếu không tìm thấy work
      }
  
      const workIndex = updatedTasks[taskType].findIndex(
        (task) => task.id === taskFormData.workId
      );
  
      if (workIndex === -1) {
        alert("Work not found. Please try again.");
        return prevTasks; // Không thực hiện cập nhật nếu không tìm thấy work
      }
  
      // Đảm bảo subTasks tồn tại và chỉ thêm task mới một lần
      const work = updatedTasks[taskType][workIndex];
      if (!work.subTasks) {
        work.subTasks = [];
      }
  
      // Thêm task mới chỉ khi nó chưa tồn tại
      const isDuplicateTask = work.subTasks.some(
        (subTask) => subTask.name === taskFormData.taskName
      );
      
      if (!isDuplicateTask) {
        work.subTasks.push({
          name: taskFormData.taskName,
          description: taskFormData.taskDescription,
          startTime: taskFormData.startTime,
          endTime: taskFormData.endTime,
          repeatOption: taskFormData.repeatOption,
          customRepeat: taskFormData.customRepeat,
        });
      }
  
      return updatedTasks;
    });
  
    // Reset lại form sau khi thêm
    setTaskFormData({
      workId: null,
      taskName: "",
      taskDescription: "",
      startTime: "",
      endTime: "",
      repeatOption: "Không lặp lại",
      customRepeat: {
        repeatEvery: 1,
        repeatUnit: "ngày",
        repeatDays: [],
      },
    });
  
    setIsTaskModalOpen(false); 
  };
  
  
  

  const handleCancel = () => {
    setIsModalOpen(false);
    setIsTaskModalOpen(false);
  };

  const handleDeleteTask = (type, id) => {
    setTasks((prevTasks) => ({
      ...prevTasks,
      [type]: prevTasks[type].filter((task) => task.id !== id),
    }));
  };

  const handleEditTask = (task) => {
    setEditMode(true);
    setFormData(task);
    setIsModalOpen(true);
  };

  const handleTaskCheckbox = (type, workId, subTaskIndex) => {
    setTasks((prevTasks) => {
      const updatedTasks = { ...prevTasks };
  
      if (subTaskIndex === -1) {
        // Xóa công việc hoàn toàn khỏi danh sách và thêm vào mục hoàn thành
        const completedTask = updatedTasks[type].find((task) => task.id === workId);
        if (completedTask) {
          setCompletedTasks((prevCompleted) => [...prevCompleted, completedTask]);
        }
        updatedTasks[type] = updatedTasks[type].filter((task) => task.id !== workId);
      } else {
        // Xóa sub-task
        updatedTasks[type] = updatedTasks[type].map((task) => {
          if (task.id === workId) {
            return {
              ...task,
              subTasks: task.subTasks.filter((_, idx) => idx !== subTaskIndex),
            };
          }
          return task;
        });
      }
  
      return updatedTasks;
    });
  };
  
  

  const handleDragStart = (e, task, type) => {
    e.dataTransfer.setData("task", JSON.stringify({ ...task, type }));
  };

  const handleDrop = (e, newType) => {
    const data = JSON.parse(e.dataTransfer.getData("task"));

    if (data.type === newType) return;

    setTasks((prevTasks) => {
      const updatedSourceTasks = prevTasks[data.type].filter(
        (task) => task.id !== data.id
      );
      const updatedTargetTasks = [
        ...prevTasks[newType],
        { ...data, type: newType },
      ];
      return {
        ...prevTasks,
        [data.type]: updatedSourceTasks,
        [newType]: updatedTargetTasks,
      };
    });
  };

  const allowDrop = (e) => e.preventDefault();

  const formatDate = (dateString) => {
    if (!dateString) return "";
    const [year, month, day] = dateString.split("-");
    return `${day}/${month}/${year}`;
  };

  return (
    <div className="home-body-conatiner" data-aos="zoom-in">
      <header id="dash-header" className="search-bar">
        <h1>Dashboard</h1>
      </header>
      <button className="add-work-btn" onClick={handleAddNewWork}>
        Add new work
      </button>

      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h2>{editMode ? "Edit Work" : "Add New Work"}</h2>
            <input
              type="text"
              placeholder="Enter work name"
              name="name"
              value={formData.name}
              onChange={handleFormChange}
            />
            <div className="form-row">
              <select
                name="type"
                value={formData.type}
                onChange={handleFormChange}
              >
                <option value="">Select type</option>
                <option value="doNow">Do now</option>
                <option value="schedule">Schedule</option>
                <option value="delegate">Delegate</option>
                <option value="drop">Drop</option>
              </select>
              <select
                name="tag"
                value={formData.tag}
                onChange={(e) => {
                  handleFormChange(e);
                  if (e.target.value === "Other") {
                    setFormData((prev) => ({ ...prev, customTag: "" }));
                  } else {
                    setFormData((prev) => ({ ...prev, customTag: null }));
                  }
                }}
              >
                <option value="">Select tag</option>
                <option value="Học tập">Học tập</option>
                <option value="Làm việc">Làm việc</option>
                <option value="Nghỉ ngơi">Nghỉ ngơi</option>
                <option value="Giải trí">Giải trí</option>
                <option value="Other">Other</option>
              </select>
              {formData.tag === "Other" && (
                <input
                  type="text"
                  placeholder="Enter custom tag"
                  name="customTag"
                  value={formData.customTag || ""}
                  onChange={handleFormChange}
                />
              )}
            </div>
            <div className="form-row">
              <input
                type="date"
                name="startDate"
                value={formData.startDate}
                onChange={handleFormChange}
              />
              <input
                type="date"
                name="endDate"
                value={formData.endDate}
                onChange={handleFormChange}
              />
            </div>
            <textarea
              placeholder="Describe work (use # for hashtags)"
              name="describe"
              value={formData.describe}
              onChange={handleFormChange}
              rows="4"
            ></textarea>
            <div className="modal-buttons">
              <button onClick={handleDone}>Done</button>
              <button onClick={handleCancel}>Cancel</button>
            </div>
          </div>
        </div>
      )}

{isTaskModalOpen && (
  <div className="modal modal-task">
    <div className="modal-content modal-task-content">
      <div className="modal-left">
        <h2>Add Task for {taskFormData.workName}</h2>
        <input
          type="text"
          placeholder="Enter task name"
          name="taskName"
          value={taskFormData.taskName}
          onChange={handleTaskFormChange}
        />
        <textarea
          placeholder="Task description"
          name="taskDescription"
          value={taskFormData.taskDescription}
          onChange={handleTaskFormChange}
          rows="4"
        ></textarea>
        <div className="form-row">
          <input
            type="time"
            name="startTime"
            value={taskFormData.startTime}
            onChange={handleTaskFormChange}
          />
          <input
            type="time"
            name="endTime"
            value={taskFormData.endTime}
            onChange={handleTaskFormChange}
          />
        </div>
        <select
          name="repeatOption"
          value={taskFormData.repeatOption}
          onChange={(e) => {
            handleTaskFormChange(e);
            if (e.target.value === "Tùy chỉnh") {
              setTaskFormData((prev) => ({
                ...prev,
                customRepeat: {
                  ...prev.customRepeat,
                  isCustomizing: true,
                },
              }));
            }
          }}
        >
          <option value="Không lặp lại">Không lặp lại</option>
          <option value="Hàng ngày">Hàng ngày</option>
          <option value="Tùy chỉnh">Tùy chỉnh</option>
        </select>
        {taskFormData.repeatOption === "Tùy chỉnh" && taskFormData.customRepeat.isCustomizing && (
          <div className="custom-repeat-form">
            <div className="form-row">
              <label>
                Lặp lại mỗi:
                <input
                  type="number"
                  min="1"
                  name="repeatEvery"
                  value={taskFormData.customRepeat.repeatEvery}
                  onChange={(e) =>
                    setTaskFormData((prev) => ({
                      ...prev,
                      customRepeat: {
                        ...prev.customRepeat,
                        repeatEvery: Math.max(1, Number(e.target.value)),
                      },
                    }))
                  }
                />
              </label>
              <select
                name="repeatUnit"
                value={taskFormData.customRepeat.repeatUnit}
                onChange={(e) =>
                  setTaskFormData((prev) => ({
                    ...prev,
                    customRepeat: {
                      ...prev.customRepeat,
                      repeatUnit: e.target.value,
                    },
                  }))
                }
              >
                <option value="ngày">Ngày</option>
                <option value="tuần">Tuần</option>
                <option value="tháng">Tháng</option>
                <option value="năm">Năm</option>
              </select>
            </div>
            <div className="form-row">
              <label>Lặp lại vào:</label>
              {["T2", "T3", "T4", "T5", "T6", "T7", "CN"].map((day) => (
                <label key={day} className="repeat-day-option">
                  <input
                    type="checkbox"
                    checked={taskFormData.customRepeat.repeatDays.includes(day)}
                    onChange={(e) => {
                      setTaskFormData((prev) => {
                        const updatedDays = e.target.checked
                          ? [...prev.customRepeat.repeatDays, day]
                          : prev.customRepeat.repeatDays.filter((d) => d !== day);
                        return {
                          ...prev,
                          customRepeat: {
                            ...prev.customRepeat,
                            repeatDays: updatedDays,
                          },
                        };
                      });
                    }}
                  />
                  {day}
                </label>
              ))}
            </div>
            <button
              className="custom-repeat-done"
              onClick={() =>
                setTaskFormData((prev) => ({
                  ...prev,
                  customRepeat: {
                    ...prev.customRepeat,
                    isCustomizing: false,
                  },
                }))
              }
            >
              Done
            </button>
          </div>
        )}
        {taskFormData.repeatOption === "Tùy chỉnh" && !taskFormData.customRepeat.isCustomizing && (
          <p>
            Lặp lại {taskFormData.customRepeat.repeatEvery} {taskFormData.customRepeat.repeatUnit}{" "}
            một lần vào thứ {taskFormData.customRepeat.repeatDays.join(", ")}
          </p>
        )}
        <div className="modal-buttons">
          <button onClick={handleDoneTask}>Add Task</button>
          <button onClick={() => setIsTaskModalOpen(false)}>Cancel</button>
        </div>
      </div>
      <div className="divider"></div>
      <div className="modal-right">
        <h2>Existing Tasks</h2>
        {(() => {
          const taskType = Object.keys(tasks).find((type) =>
            tasks[type].some((task) => task.id === taskFormData.workId)
          );
          if (!taskType) return <p>No tasks available.</p>;
          const work = tasks[taskType].find((task) => task.id === taskFormData.workId);
          return work && work.subTasks && work.subTasks.length > 0 ? (
            <ul>
              {work.subTasks.map((subTask, index) => (
                <li key={index}>
                  <h4>{subTask.name}</h4>
                  <p>{subTask.description}</p>
                  <p>
                    {subTask.startTime} - {subTask.endTime}
                  </p>
                </li>
              ))}
            </ul>
          ) : (
            <p>No tasks available.</p>
          );
        })()}
      </div>
    </div>
  </div>
)}



      <main className="body-content">
        {["doNow", "schedule", "delegate", "drop"].map((type) => (
          <div
            className="task-box"
            key={type}
            onDrop={(e) => handleDrop(e, type)}
            onDragOver={allowDrop}
          >
            <h2>{type.charAt(0).toUpperCase() + type.slice(1)}</h2>
            <div className="task-list">
            {tasks[type].map((task) => (
              <div
                key={task.id}
                className="task"
                draggable
                onDragStart={(e) => handleDragStart(e, task, type)}
              >
                <div>
                  <input
                    type="checkbox"
                    onChange={() => handleTaskCheckbox(type, task.id, -1)}
                  />
                  <span>{task.name}</span>
                  <p>
                    {formatDate(task.startDate)} - {formatDate(task.endDate)}
                  </p>
                  <p>
                    {task.describe
                      ? task.describe.split(" ").map((word, i) =>
                          word.startsWith("#") ? (
                            <span key={i} style={{ color: "blue" }}>
                              {word}{" "}
                            </span>
                          ) : (
                            `${word} `
                          )
                        )
                      : "No description"}
                  </p>
                  <p>Tasks: {task.subTasks ? task.subTasks.length : 0}</p> {/* Hiển thị số task */}
                </div>
                <div className="task-buttons">
                  <AiOutlinePlus
                    className="add-task-icon"
                    onClick={() => handleAddTask(task.id)}
                  />
                  <AiFillEdit
                    className="edit-icon"
                    onClick={() => handleEditTask(task)}
                  />
                  <AiFillDelete
                    className="delete-icon"
                    onClick={() => handleDeleteTask(type, task.id)}
                  />
                </div>
              </div>
            ))}

            </div>
          </div>
        ))}
      </main>
    </div>
  );
};

export default Dashboard;
