import React, { useState, useEffect, useRef } from "react";
import "./styles/focus.css";

const Focus = ({ works = {}, toast }) => {
  const [mode, setMode] = useState("auto");
  const [pomodoroTime, setPomodoroTime] = useState(25 * 60); // 25 minutes in seconds
  const [pomodoroState, setPomodoroState] = useState("Work"); // Work, Short Break, Long Break
  const [pomodoroCycles, setPomodoroCycles] = useState(0); // Count work sessions
  const [isRunning, setIsRunning] = useState(false); // Timer state
  const intervalRef = useRef(null);

  // Flip clock refs
  const minuteTensRef = useRef(null);
  const minuteOnesRef = useRef(null);
  const secondTensRef = useRef(null);
  const secondOnesRef = useRef(null);

  const [currentTask, setCurrentTask] = useState(null); // Current task for auto mode
  const [isModalOpen, setIsModalOpen] = useState(false);


  
  useEffect(() => {
    if (mode === "auto") {
      handleAutoSelectTask();
    }
  }, [mode, works]);

  useEffect(() => {
    if (isRunning && pomodoroTime > 0) {
      intervalRef.current = setInterval(() => {
        setPomodoroTime((prev) => prev - 1);
      }, 1000);
    } else if (pomodoroTime === 0) {
      handlePomodoroEnd();
    }
    return () => clearInterval(intervalRef.current);
  }, [isRunning, pomodoroTime]);

  useEffect(() => {
    updateFlipClock();
  }, [pomodoroTime]);

  const handlePomodoroEnd = () => {
    clearInterval(intervalRef.current);
    if (pomodoroState === "Work") {
      setPomodoroCycles((prev) => prev + 1);
      if ((pomodoroCycles + 1) % 4 === 0) {
        setPomodoroState("Long Break");
        setPomodoroTime(15 * 60); // 15 minutes
      } else {
        setPomodoroState("Short Break");
        setPomodoroTime(5 * 60); // 5 minutes
      }
    } else {
      setPomodoroState("Work");
      setPomodoroTime(25 * 60); // 25 minutes
    }
    toast.info(`${pomodoroState} session ended. Starting next session!`);
  };

  const handlePomodoroSkip = () => {
    clearInterval(intervalRef.current);
    handlePomodoroEnd();
  };

  const handlePomodoroPause = () => {
    setIsRunning((prev) => !prev);
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return [String(mins).padStart(2, "0"), String(secs).padStart(2, "0")];
  };

  const updateFlipClock = () => {
    const [minutes, seconds] = formatTime(pomodoroTime);

    updateDigit(minuteTensRef.current, minutes.charAt(0));
    updateDigit(minuteOnesRef.current, minutes.charAt(1));
    updateDigit(secondTensRef.current, seconds.charAt(0));
    updateDigit(secondOnesRef.current, seconds.charAt(1));
  };

  const updateDigit = (digitRef, newValue) => {
    if (!digitRef || !digitRef.children || digitRef.children.length < 3) {
      console.error("Digit ref is not properly attached:", digitRef);
      return;
    }
  
    const [top, bottom, overlayTop, overlayBottom] = digitRef.children;
  
    if (!top || !bottom || !overlayTop || !overlayBottom) {
      console.error("Missing digit elements:", { top, bottom, overlayTop, overlayBottom });
      return;
    }
  
    const currentValue = top.textContent;
  
    if (currentValue === newValue) return;
  
    overlayTop.textContent = newValue;
    overlayBottom.textContent = newValue;
  
    digitRef.classList.add("flip");
  
    digitRef.addEventListener(
      "animationend",
      () => {
        digitRef.classList.remove("flip");
        top.textContent = newValue;
        bottom.textContent = newValue;
      },
      { once: true } // Ensure the listener runs only once
    );
  };
  

  const handleAutoSelectTask = () => {
    if (!works) {
      setCurrentTask(null);
      return;
    }

    const now = new Date();
    const currentTime = now.getHours() * 60 + now.getMinutes();

    for (const type in works) {
      for (const work of works[type] || []) {
        if (work.subWorks && work.subWorks.length > 0) {
          for (const task of work.subWorks) {
            const [startHour, startMinute] = task.startTime.split(":").map(Number);
            const [endHour, endMinute] = task.endTime.split(":").map(Number);
            const taskStartTime = startHour * 60 + startMinute;
            const taskEndTime = endHour * 60 + endMinute;

            if (currentTime >= taskStartTime && currentTime <= taskEndTime) {
              setCurrentTask({
                ...task,
                workName: work.name,
                workStartDate: work.startDate,
                workEndDate: work.endDate,
              });
              return;
            }
          }
        }
      }
    }
    setCurrentTask(null);
  };

  return (
    <div className="main-form">
      <header className="search-bar">
        <h1>Focus</h1>
      </header>

      <div className="focus-modes">
        <button className={`focus-mode ${mode === "auto" ? "active" : ""}`} onClick={() => setMode("auto")}>
          Auto
        </button>
        <button className={`focus-mode ${mode === "manual" ? "active" : ""}`} onClick={() => setIsModalOpen(true)}>
          Manual
        </button>
        <button className={`focus-mode ${mode === "free" ? "active" : ""}`} onClick={() => setMode("free")}>
          Free
        </button>
      </div>

      <div className="pomodoro-timer">
        <h3>{pomodoroState}</h3>
        <div className="countdown">
          <div className="time-section">
            <div ref={minuteTensRef} className="time-segment">
              <div className="segment-display__top">2</div>
              <div className="segment-display__bottom">2</div>
              <div className="segment-overlay">
                <div className="segment-overlay__top"></div>
                <div className="segment-overlay__bottom"></div>
              </div>
            </div>
            <div ref={minuteOnesRef} className="time-segment">
              <div className="segment-display__top">5</div>
              <div className="segment-display__bottom">5</div>
              <div className="segment-overlay">
                <div className="segment-overlay__top"></div>
                <div className="segment-overlay__bottom"></div>
              </div>
            </div>
          </div>
          <div className="time-section">
            <div ref={secondTensRef} className="time-segment">
              <div className="segment-display__top">0</div>
              <div className="segment-display__bottom">0</div>
              <div className="segment-overlay">
                <div className="segment-overlay__top"></div>
                <div className="segment-overlay__bottom"></div>
              </div>
            </div>
            <div ref={secondOnesRef} className="time-segment">
              <div className="segment-display__top">0</div>
              <div className="segment-display__bottom">0</div>
              <div className="segment-overlay">
                <div className="segment-overlay__top"></div>
                <div className="segment-overlay__bottom"></div>
              </div>
            </div>
          </div>
        </div>
        <div className="pomodoro-controls">
          <button onClick={handlePomodoroPause}>{isRunning ? "Pause" : "Start"}</button>
          <button onClick={handlePomodoroSkip}>Skip</button>
        </div>
      </div>
    </div>
  );
};

export default Focus;
