import Toggler from "./components/Toggler";
import Calendar from "./components/Calendar";
import Home from "./components/Home";
import "react-toastify/dist/ReactToastify.css";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import Project from "./components/Project";
import Focus from "./components/Focus";
import Dashboard from "./components/Dashboard";
import Report from "./components/Report";
import ForgotPass from "./components/ForgotPass";
import TimeTable from "./components/TimeTable";
import ResetPass from "./components/ResetPass";
import { useState } from "react";

function App() {
  // Quản lý các work và completedWork ở App để có thể truyền xuống các component khác nhau
  const [works, setWorks] = useState({
    doNow: [],
    schedule: [],
    delegate: [],
    drop: [],
  });
  const [completedWorks, setCompletedWorks] = useState([]);

  return (
    <BrowserRouter>
      <ToastContainer
        position="top-center"
        autoClose={800}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />
      <Routes>
        <Route path="/" element={<Toggler toast={toast} />} />
        <Route path="/ForgotPass" element={<ForgotPass toast={toast} />} />
        <Route path="/ResetPass/:id/:token" element={<ResetPass toast={toast} />} />
        <Route path="/Home" element={<Home tasks={works} />}>
          <Route
            index
            element={
              <Dashboard
                works={works}
                setWorks={setWorks} // Truyền xuống để quản lý và cập nhật trạng thái works
                completedWorks={completedWorks}
                setCompletedWorks={setCompletedWorks} // Truyền xuống để cập nhật trạng thái completedWorks
              />
            }
          />
          <Route
            path="/Home/projects"
            element={
              <Project
                works={works} // Truyền toàn bộ works
                completedWorks={completedWorks} // Truyền danh sách completedWorks
                toast={toast}
              />
            }
          />
          <Route
            path="/Home/focus"
            element={<Focus toast={toast} tasks={works} />}
          />
          <Route
            path="/Home/timetable"
            element={<TimeTable toast={toast} tasks={works} />}
          />
          <Route
            path="/Home/calendar"
            element={<Calendar toast={toast} tasks={works} />}
          />
          <Route
            path="/Home/report"
            element={<Report toast={toast} tasks={works} />}
          />
        </Route>
      </Routes>
    </BrowserRouter>
  );
}

export default App;
