import React, { useEffect, useMemo, useState } from "react";
import "aos/dist/aos.css";
import "./styles/timetable.css";

const updateHeadersWithDate = (days) => {
  const currentDate = new Date();
  const todayIndex = (currentDate.getDay() + 6) % 7; // Điều chỉnh chủ nhật thành 6, thứ 2 thành 0
  const getDateForDay = (dayIndex) => {
    const date = new Date(currentDate);
    date.setDate(currentDate.getDate() - todayIndex + dayIndex);
    return `${date.getDate()}/${date.getMonth() + 1}`;
  };

  const headerCells = document.querySelectorAll(".header-cell");
  headerCells.forEach((cell, index) => {
    if (index > 0) {
      const dateText = getDateForDay(index - 1);
      cell.innerHTML = `${days[index - 1]}<br><span class="date-text">${dateText}</span>`;
      cell.classList.toggle("today-highlight", index - 1 === todayIndex);
    }
  });
};

const TimeTable = ({ works }) => {
  const days = useMemo(
    () => ["Thứ 2", "Thứ 3", "Thứ 4", "Thứ 5", "Thứ 6", "Thứ 7", "Chủ Nhật"],
    []
  );
  const [weeklyTasks, setWeeklyTasks] = useState([]);
  const [isTaskListVisible, setIsTaskListVisible] = useState(false);

  const getStartOfWeek = () => {
    const currentDate = new Date();
    const day = currentDate.getDay() || 7; // Chủ nhật là 7
    currentDate.setHours(0, 0, 0, 0);
    currentDate.setDate(currentDate.getDate() - day + 1); // Đặt về thứ Hai
    return currentDate;
  };

  const calculateTasksForWeek = () => {
    if (!works || typeof works !== "object") {
      console.error("Invalid 'works' data:", works);
      setWeeklyTasks([]);
      return;
    }
  
    const startOfWeek = getStartOfWeek();
    const tasks = [];
  
    Object.values(works).flat().forEach((work) => {
      const workStartDate = new Date(work.startDate);
      const workEndDate = new Date(work.endDate);
  
      work.subWorks.forEach((task) => {
        const { startTime, endTime, repeatOption, customRepeat = {} } = task;
        let currentDate = new Date(startOfWeek);
        const validDates = [];
  
        if (repeatOption === "Không lặp lại") {
          const taskDate = new Date(workStartDate);
          if (taskDate >= startOfWeek && taskDate <= workEndDate) {
            validDates.push(taskDate);
          }
        } else if (repeatOption === "Hàng ngày") {
          while (currentDate <= workEndDate) {
            validDates.push(new Date(currentDate));
            currentDate.setDate(currentDate.getDate() + 1);
          }
        } else if (repeatOption === "Tùy chỉnh") {
          const { repeatEvery = 1, repeatUnit = "ngày", repeatDays = [] } = customRepeat;
          if (repeatUnit === "ngày") {
            while (currentDate <= workEndDate) {
              validDates.push(new Date(currentDate));
              currentDate.setDate(currentDate.getDate() + repeatEvery);
            }
          } else if (repeatUnit === "tuần") {
            const dayMapping = ["CN", "T2", "T3", "T4", "T5", "T6", "T7"];
            while (currentDate <= workEndDate) {
              const dayOfWeek = dayMapping[currentDate.getDay()];
              if (repeatDays.includes(dayOfWeek)) {
                validDates.push(new Date(currentDate));
              }
              currentDate.setDate(currentDate.getDate() + 1);
            }
          }
        }
  
        validDates.forEach((date) => {
          if (date >= startOfWeek && date <= new Date(startOfWeek.getTime() + 6 * 24 * 60 * 60 * 1000)) {
            const [startHour, startMinute] = startTime.split(":").map(Number);
            const [endHour, endMinute] = endTime.split(":").map(Number);
            tasks.push({
              ...task,
              workName: work.name,
              day: date.getDay() || 7,
              startTime: new Date(date.setHours(startHour, startMinute)),
              endTime: new Date(date.setHours(endHour, endMinute)),
            });
          }
        });
      });
    });
  
    const sortedTasks = tasks.sort((a, b) => a.startTime - b.startTime);
    setWeeklyTasks(sortedTasks);
  };
  

  useEffect(() => {
    calculateTasksForWeek();
  }, [works]);

  useEffect(() => {
    updateHeadersWithDate(days);
    const intervalId = setInterval(() => updateHeadersWithDate(days), 60000);
    return () => clearInterval(intervalId);
  }, [days]);

  return (
    <div className="main-form" data-aos="zoom-in">
      <header className="search-bar">
        <h1>TimeTable</h1>
        <button
          className="view-tasklist-button"
          onClick={() => setIsTaskListVisible(true)}
        >
          View Weekly Tasks
        </button>
      </header>

      {isTaskListVisible && (
        <div className="tasklist-modal">
          <div className="tasklist-content">
            <button
              className="close-tasklist"
              onClick={() => setIsTaskListVisible(false)}
            >
              &times;
            </button>
            <h2>Weekly Task List</h2>
            <div className="weekly-tasklist">
              {days.map((day, index) => (
                <div key={index} className="day-column">
                  <h3>{day}</h3>
                  <ul>
                    {weeklyTasks
                      .filter((task) => task.day === index + 1)
                      .map((task, taskIndex) => (
                        <li key={taskIndex}>
                          <strong>{task.name}</strong>: {task.startTime.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })} -{" "}
                          {task.endTime.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                        </li>
                      ))}
                  </ul>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TimeTable;
