import React, { useState, useMemo } from "react";
import { AiFillDelete } from "react-icons/ai";
import { BiSearchAlt2 } from "react-icons/bi";
import Aos from "aos";
import "aos/dist/aos.css";
import "./styles/project.css";

const Project = ({ tasks, completedTasks, toast }) => {
  const [viewMode, setViewMode] = useState("ongoing");
  const [searchQuery, setSearchQuery] = useState("");

  const filteredTasks = useMemo(() => {
    const list = viewMode === "ongoing" 
      ? Object.values(tasks).flat() // Ongoing Works
      : completedTasks; // Completed Works
    return list.filter((work) =>
      work.name.toLowerCase().includes(searchQuery.toLowerCase())
    );
  }, [viewMode, tasks, completedTasks, searchQuery]);
  
  useEffect(() => {
    console.log("Tasks received in Project:", tasks);
    console.log("Completed Tasks received in Project:", completedTasks);
  }, [tasks, completedTasks]);
  
  return (
    <div className="main-form" data-aos="zoom-in">
      <div className="search-bar">
        <h1>Projects</h1>
        <input
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          type="search"
          placeholder={`Search ${viewMode === "ongoing" ? "ongoing" : "completed"} works`}
        />
        <button id="search-bt">
          <BiSearchAlt2 size={22} />
        </button>
      </div>
      <div className="view-toggle">
        <button
          className={viewMode === "ongoing" ? "active" : ""}
          onClick={() => setViewMode("ongoing")}
        >
          On Going Works
        </button>
        <button
          className={viewMode === "completed" ? "active" : ""}
          onClick={() => setViewMode("completed")}
        >
          Completed Works
        </button>
      </div>
      <div className="item-list">
  {filteredTasks.length === 0 ? (
    <h3 id="no-todo">
      {viewMode === "ongoing" ? "No ongoing works" : "No completed works"}
    </h3>
  ) : (
    <ul>
      {filteredTasks.map((work) => (
        <li key={work.id}>
          <label htmlFor="" className="item-name">
            <input
              type="checkbox"
              checked={viewMode === "completed"}
              readOnly
            />
            {work.name} {/* Hiển thị tên Work */}
          </label>
          {viewMode === "ongoing" && (
            <button id="del-bt">
              <AiFillDelete size={20} color="#FF6969" />
            </button>
          )}
        </li>
      ))}
    </ul>
  )}
</div>

    </div>
  );
};

export default Project;
