import React, { useState, useEffect } from "react";
import "./styles/focus.css";

const Focus = ({ works, taskCompletionStatus, setTaskCompletionStatus }) => {
  const [mode, setMode] = useState("auto"); // Chế độ: auto, manual, free
  const [selectedWork, setSelectedWork] = useState(null); // Work được chọn (manual)
  const [selectedTask, setSelectedTask] = useState(null); // Task được chọn
  const [currentTask, setCurrentTask] = useState(null); // Task đang được focus
  const [progress, setProgress] = useState(0); // Tiến trình
  const [timeLeft, setTimeLeft] = useState(0); // Thời gian còn lại

  useEffect(() => {
    if (mode === "auto") {
      handleAutoSelectTask();
    }
  }, [mode, works]);

  const handleAutoSelectTask = () => {
    const now = new Date();
    const currentHour = now.getHours();
    const currentMinute = now.getMinutes();
    const currentTime = currentHour * 60 + currentMinute;

    for (const type in works) {
      for (const work of works[type]) {
        if (work.subWorks && work.subWorks.length > 0) {
          for (const task of work.subWorks) {
            const [startHour, startMinute] = task.startTime.split(":").map(Number);
            const [endHour, endMinute] = task.endTime.split(":").map(Number);
            const taskStartTime = startHour * 60 + startMinute;
            const taskEndTime = endHour * 60 + endMinute;

            if (currentTime >= taskStartTime && currentTime <= taskEndTime) {
              setCurrentTask({
                ...task,
                workName: work.name,
                timeRange: [taskStartTime, taskEndTime],
              });
              return;
            }
          }
        }
      }
    }
    setCurrentTask(null);
  };

  const handleManualSelectWork = (work) => {
    setSelectedWork(work);
    setSelectedTask(null);
  };

  const handleManualSelectTask = (task) => {
    setSelectedTask(task);
  };

  const handleFocusStart = () => {
    if (!currentTask && mode !== "free") {
      alert("No task selected for focus.");
      return;
    }

    const taskDuration =
      mode === "free"
        ? 0
        : currentTask
        ? currentTask.timeRange[1] - currentTask.timeRange[0]
        : selectedTask
        ? selectedTask.timeRange[1] - selectedTask.timeRange[0]
        : 0;

    setTimeLeft(taskDuration * 60);

    const interval = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(interval);
          alert("Focus session complete!");
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    if (currentTask) {
      setTaskCompletionStatus((prev) => ({
        ...prev,
        [`${currentTask.workId}-${currentTask.index}`]: true,
      }));
    }
  };

  return (
    <div className="main-form" data-aos="zoom-in">
      {/* Nửa trên */}
      <div className="focus-header">
        <h1>Focus</h1>
        <div className="focus-modes">
          <button
            className={`focus-mode ${mode === "auto" ? "active" : ""}`}
            onClick={() => setMode("auto")}
          >
            Tự động
          </button>
          <button
            className={`focus-mode ${mode === "manual" ? "active" : ""}`}
            onClick={() => setMode("manual")}
          >
            Tự chọn
          </button>
          <button
            className={`focus-mode ${mode === "free" ? "active" : ""}`}
            onClick={() => setMode("free")}
          >
            Tự do
          </button>
        </div>
      </div>

      {/* Nửa dưới */}
      <div className="focus-content">
        {mode === "auto" && currentTask && (
          <div className="task-details">
            <h2>Task hiện tại</h2>
            <p>
              <strong>Work:</strong> {currentTask.workName}
            </p>
            <p>
              <strong>Task:</strong> {currentTask.name}
            </p>
            <p>
              <strong>Thời gian:</strong> {currentTask.startTime} - {currentTask.endTime}
            </p>
            <div className="progress-bar">
              <div
                className="progress-bar-fill"
                style={{
                  width: `${(100 * (timeLeft / 60)) / (currentTask.timeRange[1] - currentTask.timeRange[0])}%`,
                }}
              ></div>
            </div>
            <p>{Math.floor(timeLeft / 60)}:{String(timeLeft % 60).padStart(2, "0")} còn lại</p>
          </div>
        )}

        {mode === "manual" && (
          <div className="manual-select">
            <h2>Chọn work và task</h2>
            <div className="work-list">
              {Object.values(works).flat().map((work) => (
                <button
                  key={work.id}
                  className={`work-item ${selectedWork?.id === work.id ? "active" : ""}`}
                  onClick={() => handleManualSelectWork(work)}
                >
                  {work.name}
                </button>
              ))}
            </div>
            {selectedWork && (
              <div className="task-list">
                {selectedWork.subWorks && selectedWork.subWorks.length > 0 ? (
                  selectedWork.subWorks.map((task, index) => (
                    <button
                      key={index}
                      className={`task-item ${selectedTask?.name === task.name ? "active" : ""}`}
                      onClick={() => handleManualSelectTask(task)}
                    >
                      {task.name}
                    </button>
                  ))
                ) : (
                  <p>No tasks available. Treating this work as a task.</p>
                )}
              </div>
            )}
          </div>
        )}

        {mode === "free" && <p>Tự do focus không giới hạn thời gian.</p>}

        <button className="focus-button" onClick={handleFocusStart}>
          Focus
        </button>
      </div>
    </div>
  );
};

export default Focus;
