import React, { useState, useEffect, useMemo } from "react";
import { AiFillDelete } from "react-icons/ai";
import { BiSearchAlt2 } from "react-icons/bi";
import "aos/dist/aos.css";
import "./styles/project.css";

const Project = () => {
  const [works, setWorks] = useState({ doNow: [], schedule: [], delegate: [], drop: [] });
  const [completedWorks, setCompletedWorks] = useState([]);
  const [viewMode, setViewMode] = useState("ongoing");
  const [searchQuery, setSearchQuery] = useState("");

  // Lấy dữ liệu từ localStorage khi component được mount
  useEffect(() => {
    const storedWorks = JSON.parse(localStorage.getItem("works")) || { doNow: [], schedule: [], delegate: [], drop: [] };
    const storedCompletedWorks = JSON.parse(localStorage.getItem("completedWorks")) || [];
    setWorks(storedWorks);
    setCompletedWorks(storedCompletedWorks);
  }, []);
  

  // Bộ lọc works theo trạng thái hiển thị (ongoing/completed) và tìm kiếm
  const filteredWorks = useMemo(() => {
    const allOngoingWorks = Object.values(works).flat(); // Gộp tất cả works từ các danh mục
  
    if (viewMode === "ongoing") {
      // Chỉ lấy Work chưa được đánh dấu hoàn thành
      return allOngoingWorks.filter((work) => !completedWorks.some((completedWork) => completedWork.id === work.id));
    }
  
    // Trường hợp "completed": chỉ lấy Work đã hoàn thành
    return completedWorks;
  }, [viewMode, works, completedWorks]);
  

  return (
    <div className="main-form" data-aos="zoom-in">
      <div className="search-bar">
        <h1>Projects</h1>
        <input
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          type="search"
          placeholder={`Search ${viewMode === "ongoing" ? "ongoing" : "completed"} works`}
        />
        <button id="search-bt">
          <BiSearchAlt2 size={22} />
        </button>
      </div>
      <div className="view-toggle">
        <button
          className={viewMode === "ongoing" ? "active" : ""}
          onClick={() => setViewMode("ongoing")}
        >
          On Going Works
        </button>
        <button
          className={viewMode === "completed" ? "active" : ""}
          onClick={() => setViewMode("completed")}
        >
          Completed Works
        </button>
      </div>
      <div className="item-list">
        {filteredWorks.length === 0 ? (
          <h3 id="no-todo">
            {viewMode === "ongoing" ? "No ongoing works" : "No completed works"}
          </h3>
        ) : (
          <ul>
            {filteredWorks.map((work) => (
              <li key={work.id}>
                <label htmlFor="" className="item-name">
                  <input
                    type="checkbox"
                    checked={viewMode === "completed"}
                    readOnly
                  />
                  {work.name} {/* Hiển thị tên Work */}
                </label>
                {viewMode === "ongoing" && (
                  <button id="del-bt">
                    <AiFillDelete size={20} color="#FF6969" />
                  </button>
                )}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Project;
