import React, { useEffect, useMemo, useState } from "react";
import "aos/dist/aos.css";
import "./styles/timetable.css";

const getStartOfWeek = (date) => {
  const day = date.getDay(); // 0 (Chủ nhật) - 6 (Thứ Bảy)
  const diff = date.getDate() - day + (day === 0 ? -6 : 1); // Điều chỉnh về thứ 2
  const startOfWeek = new Date(date.setDate(diff));
  startOfWeek.setHours(0, 0, 0, 0);
  return startOfWeek;
};

const getTasksForWeek = (tasks, startOfWeek) => {
  const tasksByDay = Array(7).fill(null).map(() => []);
  tasks.forEach((task) => {
    const taskDate = new Date(task.startTime);
    const dayIndex = (taskDate - startOfWeek) / (24 * 60 * 60 * 1000);
    if (dayIndex >= 0 && dayIndex < 7) {
      tasksByDay[dayIndex].push(task);
    }
  });
  return tasksByDay;
};

const TimeTable = ({ works }) => {
  const days = useMemo(
    () => ["Thứ 2", "Thứ 3", "Thứ 4", "Thứ 5", "Thứ 6", "Thứ 7", "Chủ Nhật"],
    []
  );
  const hours = useMemo(() => Array.from({ length: 24 }, (_, i) => `${i}:00 - ${i + 1}:00`), []);
  const [tasksByDay, setTasksByDay] = useState([]);

  useEffect(() => {
    const today = new Date();
    const startOfWeek = getStartOfWeek(today);
    const allTasks = Object.values(works)
      .flat()
      .flatMap((work) => work.subWorks.map((task) => ({
        ...task,
        startTime: new Date(`${work.startDate}T${task.startTime}:00`),
        endTime: new Date(`${work.endDate}T${task.endTime}:00`),
      })));
    const tasksGrouped = getTasksForWeek(allTasks, startOfWeek);
    setTasksByDay(tasksGrouped);
  }, [works]);

  const renderTasks = (tasks, dayIndex) => {
    const overlappingTasks = [];
    tasks.sort((a, b) => a.startTime - b.startTime);

    return tasks.map((task, index) => {
      const startMinutes =
        task.startTime.getHours() * 60 + task.startTime.getMinutes();
      const endMinutes = task.endTime.getHours() * 60 + task.endTime.getMinutes();
      const top = startMinutes;
      const height = endMinutes - startMinutes;

      const overlapGroup = overlappingTasks.find(
        (group) =>
          group.some(
            (t) =>
              !(t.endMinutes <= startMinutes || t.startMinutes >= endMinutes)
          )
      );
      if (overlapGroup) {
        overlapGroup.push({ ...task, startMinutes, endMinutes });
      } else {
        overlappingTasks.push([{ ...task, startMinutes, endMinutes }]);
      }

      const columnIndex = overlapGroup
        ? overlapGroup.indexOf(task)
        : overlappingTasks.length - 1;

      const width = `calc(${100 / overlappingTasks[columnIndex].length}% - 5px)`;
      const left = `calc(${(100 / overlappingTasks[columnIndex].length) * columnIndex}%)`;

      return (
        <div
          key={index}
          className="task"
          style={{
            top: `${top}px`,
            height: `${height}px`,
            width,
            left,
          }}
          title={`${task.name} (${task.startTime.toLocaleTimeString()} - ${task.endTime.toLocaleTimeString()})`}
        >
          {task.name}
        </div>
      );
    });
  };

  return (
    <div className="main-form" data-aos="zoom-in">
      <header className="search-bar">
        <h1>TimeTable</h1>
      </header>

      <div className="timetable-container">
        <div className="header-row">
          <div className="header-cell time-column">Thời gian</div>
          {days.map((day, index) => (
            <div key={index} className="header-cell">
              {day}
            </div>
          ))}
        </div>

        <div className="body-container">
          <div className="time-column">
            {hours.map((hour, hourIndex) => (
              <div key={hourIndex} className="time-slot">
                {hour}
              </div>
            ))}
          </div>
          {tasksByDay.map((tasks, dayIndex) => (
            <div key={dayIndex} className="day-column">
              {renderTasks(tasks, dayIndex)}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TimeTable;
